   &lt;span class=&quot;hljs-operator&quot;&gt;|&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;  &lt;span class=&quot;hljs-operator&quot;&gt;|&lt;/span&gt; ZH1E4A &lt;span class=&quot;hljs-operator&quot;&gt;|&lt;/span&gt; PostalCode    &lt;span class=&quot;hljs-operator&quot;&gt;|&lt;/span&gt;
