        pinsRepaint = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
        pinsToRepaint = turn + SCORE;
        pinsRepaint = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        pinsToRepaint=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( pico == LINE ){
            isPlaying = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( turn &amp;gt;= &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt; ){
                isPlaying = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{
            curPin = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
            isRowFull = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
            turn++;
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;combination&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; ( &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;i&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; LINE; i++ ){
          combination[i] = generator.nextInt(&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;) + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
        }
    }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Pin&lt;/span&gt;{
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; color, X, Y, radius;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;Pin&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt;{
        X = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; Y = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; radius = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; color = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;Pin&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;( &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; r,&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; c )&lt;/span&gt;{
