&lt;p&gt;View:  &lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.awt.*;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; javax.swing.*;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Frame&lt;/span&gt;{  
    Model model;
    JButton checkAnswer;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; JPanel button;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; Color COLORS[] = {Color.black, Color.white, Color.red, Color.yellow, Color.green, Color.blue, &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Color&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;254&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;250&lt;/span&gt;)};

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;View&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String name, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; w, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; h, Model m)&lt;/span&gt;{
        model = m;
        setTitle( name );
        setSize( w,h );
        setResizable( &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt; );
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.setLayout(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;BorderLayout&lt;/span&gt;());

        button = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JPanel&lt;/span&gt;();
        button.setSize( &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Dimension&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;400&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;100&lt;/span&gt;));
        button.setVisible(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
        checkAnswer = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JButton&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Check&quot;&lt;/span&gt;);
        checkAnswer.setSize( &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Dimension&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;));
        button.add( checkAnswer );
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.add( button, BorderLayout.SOUTH);
        button.setVisible(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;paint&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;( Graphics g )&lt;/span&gt; {
        g.setColor( &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Color&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;));
        g.fillRect( &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;400&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;590&lt;/span&gt;);

        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; ( &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; model.pins.length; i++ ) {
            paintPins(model.pins[i][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;],g);
            paintPins(model.pins[i][&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],g);
            paintPins(model.pins[i][&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;],g);
            paintPins(model.pins[i][&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;],g);
            paintPins(model.pins[i][&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;],g);
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;update&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;( Graphics g )&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( model.isUpdate ) {
            paint(g);
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            model.isUpdate = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
            paintPins(model.pins[model.repaintPin-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;],g);
            paintPins(model.pins[model.repaintPin-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],g);
            paintPins(model.pins[model.repaintPin-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;],g);
            paintPins(model.pins[model.repaintPin-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;],g);
            paintPins(model.pins[model.repaintPin-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;],g);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;repaintPins&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;( &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; pin )&lt;/span&gt; {
        model.repaintPin = pin;
        model.isUpdate = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        repaint();
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;paintPins&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Pin p, Graphics g )&lt;/span&gt;{
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;X&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; p.getX();
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;Y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; p.getY();
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;color&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; p.getColor();
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;radius&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; p.getRadius();
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;x&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; X-radius;
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; Y-radius;

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (color &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;){
            g.setColor( COLORS[color]);
            g.fillOval( x,y,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius );
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{
            g.setColor( &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Color&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;238&lt;/span&gt;) );
            g.drawOval( x,y,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; );
        }
        g.setColor( Color.black );
        g.drawOval( x,y,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;*radius );
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Controller:&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.awt.*;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.awt.event.*;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Controller&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;implements&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MouseListener&lt;/span&gt;, ActionListener { 
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; Model model;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; View view;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;Controller&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Model m, View v)&lt;/span&gt;{ 
        model = m;
        view = v;

        view.addWindowListener( &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;WindowAdapter&lt;/span&gt;(){
            &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;windowClosing&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(WindowEvent e)&lt;/span&gt;{
            System.exit(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;);
        } });
        view.addMouseListener(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;);
        view.checkAnswer.addActionListener(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;);
        model.combination();
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;actionPerformed&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;( ActionEvent e )&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(e.getSource() == view.checkAnswer){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(model.isRowFull){
                model.check();
            }
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;mousePressed&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(MouseEvent e)&lt;/span&gt; {
        &lt;span class=&quot;hljs-type&quot;&gt;Point&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;mouse&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Point&lt;/span&gt;();

        mouse = e.getPoint();
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (model.isPlaying){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mouse.x &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;350&lt;/span&gt;) {
                &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;button&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; + (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt;)((mouse.y - &lt;span class=&quot;hljs-number&quot;&gt;32&lt;/span&gt;) / &lt;span class=&quot;hljs-number&quot;&gt;50&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ((button &amp;gt;= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &amp;amp;&amp;amp; (button &amp;lt;= &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;)){
                    model.fillHole(button);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(model.pinsRepaint){
                        view.repaintPins( model.pinsToRepaint );
                    }
                }
            }
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;mouseClicked&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(MouseEvent e)&lt;/span&gt; {}
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;mouseReleased&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(MouseEvent e)&lt;/span&gt;{}
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;mouseEntered&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(MouseEvent e)&lt;/span&gt; {}
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;mouseExited&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(MouseEvent e)&lt;/span&gt;  {}
}
&lt;/code&gt;&lt;/pre&gt;
