&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addClassName&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;classNameS, toElement&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; s = toElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\s/&lt;/span&gt;);
  s.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(c); toElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt; = s.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;);
