&lt;p&gt;&lt;strong&gt;A verry good solution with pure javascript is here&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/*create an XMLHttpRequest object*/&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;GethttpRequest&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){  
  &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; httpRequest=&lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;XMLHttpRequest&lt;/span&gt;){
    httpRequest   =&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(httpRequest.&lt;span class=&quot;hljs-property&quot;&gt;overrideMimeType&lt;/span&gt;){
    httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;overrideMimeType&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;text/xml&apos;&lt;/span&gt;);
    }
  }&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;ActiveXObject&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt;{httpRequest   =&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Msxml2.XMLHTTP&quot;&lt;/span&gt;);
  }&lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt;(e){
      &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt;{
        httpRequest   =&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);
      }&lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt;(e){}
    }
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!httpRequest){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;}
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; httpRequest;
}

  &lt;span class=&quot;hljs-comment&quot;&gt;/*Defining a function to make the request every time when it is needed*/&lt;/span&gt;

  &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;MakeRequest&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){

    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; uriPost       =&lt;span class=&quot;hljs-string&quot;&gt;&quot;myURL&quot;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; xhrPost       =&lt;span class=&quot;hljs-title class_&quot;&gt;GethttpRequest&lt;/span&gt;();
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; fdPost        =&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;();
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; date          =&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Date&lt;/span&gt;();

    &lt;span class=&quot;hljs-comment&quot;&gt;/*data to be sent on server*/&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; data          = { 
                        &lt;span class=&quot;hljs-string&quot;&gt;&quot;name&quot;&lt;/span&gt;      :&lt;span class=&quot;hljs-string&quot;&gt;&quot;name&quot;&lt;/span&gt;,
                        &lt;span class=&quot;hljs-string&quot;&gt;&quot;lName&quot;&lt;/span&gt;     :&lt;span class=&quot;hljs-string&quot;&gt;&quot;lName&quot;&lt;/span&gt;,
                        &lt;span class=&quot;hljs-string&quot;&gt;&quot;phone&quot;&lt;/span&gt;     :&lt;span class=&quot;hljs-string&quot;&gt;&quot;phone&quot;&lt;/span&gt;,
                        &lt;span class=&quot;hljs-string&quot;&gt;&quot;key&quot;&lt;/span&gt;       :&lt;span class=&quot;hljs-string&quot;&gt;&quot;key&quot;&lt;/span&gt;,
                        &lt;span class=&quot;hljs-string&quot;&gt;&quot;password&quot;&lt;/span&gt;  :&lt;span class=&quot;hljs-string&quot;&gt;&quot;date&quot;&lt;/span&gt;
                      };

    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;data =&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(data);             
    fdPost.&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;data&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;data);
    xhrPost.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;POST&quot;&lt;/span&gt; ,uriPost, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;timeout&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;9000&lt;/span&gt;;&lt;span class=&quot;hljs-comment&quot;&gt;/*the time you need to quit the request if it is not completed*/&lt;/span&gt;
    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onloadstart&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
      &lt;span class=&quot;hljs-comment&quot;&gt;/*do something*/&lt;/span&gt;
    };
    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt;      = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
      &lt;span class=&quot;hljs-comment&quot;&gt;/*do something*/&lt;/span&gt;
    };
    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onloadend&lt;/span&gt;   = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
      &lt;span class=&quot;hljs-comment&quot;&gt;/*do something*/&lt;/span&gt;
    }
    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onprogress&lt;/span&gt;  =&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
      &lt;span class=&quot;hljs-comment&quot;&gt;/*do something*/&lt;/span&gt;
    }

    xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; =&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){

      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;){

      }&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;){

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;){

          &lt;span class=&quot;hljs-comment&quot;&gt;/*request succesfull*/&lt;/span&gt;

        }&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; !==&lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;){

          &lt;span class=&quot;hljs-comment&quot;&gt;/*request failled*/&lt;/span&gt;

        }

      }


   }
  xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;ontimeout&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;){
    &lt;span class=&quot;hljs-comment&quot;&gt;/*you can stop the request*/&lt;/span&gt;
  }
  xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onerror&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
    &lt;span class=&quot;hljs-comment&quot;&gt;/*you can try again the request*/&lt;/span&gt;
  };
  xhrPost.&lt;span class=&quot;hljs-property&quot;&gt;onabort&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
    &lt;span class=&quot;hljs-comment&quot;&gt;/*you can try again the request*/&lt;/span&gt;
  };
  xhrPost.&lt;span class=&quot;hljs-title function_&quot;&gt;overrideMimeType&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;text/plain; charset=x-user-defined-binary&quot;&lt;/span&gt;);
  xhrPost.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Content-disposition&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;form-data&quot;&lt;/span&gt;);
  xhrPost.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;X-Requested-With&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;xmlhttprequest&quot;&lt;/span&gt;);
  xhrPost.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(fdPost);
}

&lt;span class=&quot;hljs-comment&quot;&gt;/*PHP side
&amp;lt;?php
  //check if the variable $_POST[&quot;data&quot;] exists isset() &amp;amp;&amp;amp; !empty()
  $data        =$_POST[&quot;data&quot;];
  $decodedData =json_decode($_POST[&quot;data&quot;]);
  //show a single item from the form
  echo $decodedData-&amp;gt;name;

?&amp;gt;
*/&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;/*Usage*/&lt;/span&gt;
&lt;span class=&quot;hljs-title class_&quot;&gt;MakeRequest&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Fast code fetch without jQuery&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;  &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;product_serach&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;word&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; response = &lt;span class=&quot;hljs-keyword&quot;&gt;await&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;?php echo base_url(); ?&amp;gt;home/product_search?search=&apos;&lt;/span&gt;+word);
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; json = &lt;span class=&quot;hljs-keyword&quot;&gt;await&lt;/span&gt; response.&lt;span class=&quot;hljs-title function_&quot;&gt;json&lt;/span&gt;();
            &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; [key, value] &lt;span class=&quot;hljs-keyword&quot;&gt;of&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;entries&lt;/span&gt;(json)) 
            {
                &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json)
            }                                 
        }
&lt;/code&gt;&lt;/pre&gt;
