            &lt;span class=&quot;hljs-number&quot;&gt;0.849&lt;/span&gt;s [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;0.849&lt;/span&gt;]  &amp;lt;time.sleep&amp;gt;
