            &lt;span class=&quot;hljs-number&quot;&gt;1.552&lt;/span&gt;s [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;1.552&lt;/span&gt;]  mysleep  [/test/t2.py:&lt;span class=&quot;hljs-number&quot;&gt;15&lt;/span&gt;]  [/test/t2.py:&lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;]
