&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(myString.&lt;span class=&quot;hljs-title function_&quot;&gt;replaceAll&lt;/span&gt;(match_re, &lt;span class=&quot;hljs-string&quot;&gt;&apos;**PIE**&apos;&lt;/span&gt;))
