&lt;p&gt;Yes, it is possible.&lt;/p&gt;
&lt;p&gt;In your adapter getItemViewType Layout like this ....&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MultiViewTypeAdapter&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.Adapter {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; ArrayList&amp;lt;Model&amp;gt;dataSet;
    Context mContext;
    &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; total_types;
    MediaPlayer mPlayer;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;boolean&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fabStateVolume&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TextTypeViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

        TextView txtType;
        CardView cardView;

        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;TextTypeViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);

            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.txtType = (TextView) itemView.findViewById(R.id.type);
            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.cardView = (CardView) itemView.findViewById(R.id.card_view);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ImageTypeViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

        TextView txtType;
        ImageView image;

        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ImageTypeViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);

            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.txtType = (TextView) itemView.findViewById(R.id.type);
            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.image = (ImageView) itemView.findViewById(R.id.background);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AudioTypeViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

        TextView txtType;
        FloatingActionButton fab;

        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;AudioTypeViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);

            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.txtType = (TextView) itemView.findViewById(R.id.type);
            &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.fab = (FloatingActionButton) itemView.findViewById(R.id.fab);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;MultiViewTypeAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ArrayList&amp;lt;Model&amp;gt;data, Context context)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.dataSet = data;
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.mContext = context;
        total_types = dataSet.size();
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {

        View view;
        &lt;span class=&quot;hljs-keyword&quot;&gt;switch&lt;/span&gt; (viewType) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.TEXT_TYPE:
                view = LayoutInflater.from(parent.getContext()).inflate(R.layout.text_type, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TextTypeViewHolder&lt;/span&gt;(view);
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.IMAGE_TYPE:
                view = LayoutInflater.from(parent.getContext()).inflate(R.layout.image_type, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ImageTypeViewHolder&lt;/span&gt;(view);
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.AUDIO_TYPE:
                view = LayoutInflater.from(parent.getContext()).inflate(R.layout.audio_type, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AudioTypeViewHolder&lt;/span&gt;(view);
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemViewType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {

        &lt;span class=&quot;hljs-keyword&quot;&gt;switch&lt;/span&gt; (dataSet.get(position).type) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; Model.TEXT_TYPE;
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;:
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; Model.IMAGE_TYPE;
            &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;:
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; Model.AUDIO_TYPE;
            &lt;span class=&quot;hljs-keyword&quot;&gt;default&lt;/span&gt;:
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; RecyclerView.ViewHolder holder, &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; listPosition)&lt;/span&gt; {

        &lt;span class=&quot;hljs-type&quot;&gt;Model&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;object&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; dataSet.get(listPosition);
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (object != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;switch&lt;/span&gt; (object.type) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.TEXT_TYPE:
                    ((TextTypeViewHolder) holder).txtType.setText(object.text);

                    &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;
                &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.IMAGE_TYPE:
                    ((ImageTypeViewHolder) holder).txtType.setText(object.text);
                    ((ImageTypeViewHolder) holder).image.setImageResource(object.data);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;
                &lt;span class=&quot;hljs-keyword&quot;&gt;case&lt;/span&gt; Model.AUDIO_TYPE:

                    ((AudioTypeViewHolder) holder).txtType.setText(object.text);

            }
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemCount&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; dataSet.size();
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;For the reference link: &lt;em&gt;&lt;a href=&quot;https://www.journaldev.com/12372/android-recyclerview-example&quot; rel=&quot;nofollow noreferrer&quot;&gt;Android RecyclerView Example  Multiple ViewTypes&lt;/a&gt;&lt;/em&gt;&lt;/p&gt;

&lt;p&gt;Simpler than ever, forget about ViewTypes. It is not recommended to use multiple viewtypes inside one adapter. It will mess the code and break the single responsibility principle since now the adapter needs to handle logic to know which view to inflate.&lt;/p&gt;
&lt;p&gt;Now imagine working in large teams where each team has to work in one of those viewtypes features. It will be a mess to touch the same adapter by all the teams that work in the different viewtypes. This is solved using ConcatAdapter where you isolate the adapters. Code them one by one and then just merge them inside one view.&lt;/p&gt;
&lt;p&gt;From &lt;code&gt;recyclerview:1.2.0-alpha04&lt;/code&gt; you now can use &lt;code&gt;ConcatAdapter&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;If you need a view with different viewTypes, you can just write the Adapters for each section and just use ConcatAdapter to merge all of them inside one recyclerview.&lt;/p&gt;
&lt;h2&gt;ConcatAdapter&lt;/h2&gt;
&lt;p&gt;This image shows three different viewtypes that one recyclerview has, header, content and footer.&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/qdlpN.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/qdlpN.png&quot; alt=&quot;Enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;You only create one adapter for each section, and then just use ConcatAdapter to merge them inside one recyclerview:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;val firstAdapter: FirstAdapter = 
val secondAdapter: SecondAdapter = 
val thirdAdapter: ThirdAdapter = 
&lt;span class=&quot;hljs-type&quot;&gt;val&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;concatAdapter&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; ConcatAdapter(firstAdapter, secondAdapter,
                                  thirdAdapter)
recyclerView.adapter = concatAdapter
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/nnWnq.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/nnWnq.png&quot; alt=&quot;Enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;That&apos;s all you need to know. If you want to handle loading state, for example remove the last adapter after some loading happened, you can use &lt;a href=&quot;https://github.com/googlecodelabs/android-paging/pull/46/files&quot; rel=&quot;noreferrer&quot;&gt;LoadState&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;Following &lt;a href=&quot;https://stackoverflow.com/questions/26245139/how-to-create-recyclerview-with-multiple-view-types/26245463#26245463&quot;&gt;Anton&apos;s solution&lt;/a&gt;, I came up with this &lt;code&gt;ViewHolder&lt;/code&gt; which holds/handles/delegates different type of layouts.&lt;/p&gt;
&lt;p&gt;But I am not sure if the replacing new layout would work when the recycling view&apos;s &lt;code&gt;ViewHolder&lt;/code&gt; is not the type of the data roll in.&lt;/p&gt;
&lt;p&gt;So basically,
&lt;code&gt;onCreateViewHolder(ViewGroup parent, int viewType)&lt;/code&gt; is only called when new view layout is needed;&lt;/p&gt;
&lt;p&gt;&lt;code&gt;getItemViewType(int position)&lt;/code&gt; will be called for the &lt;code&gt;viewType&lt;/code&gt;;&lt;/p&gt;
&lt;p&gt;&lt;code&gt;onBindViewHolder(ViewHolder holder, int position)&lt;/code&gt; is always called when recycling the view (new data is brought in and try to display with that &lt;code&gt;ViewHolder&lt;/code&gt;).&lt;/p&gt;
&lt;p&gt;So when &lt;code&gt;onBindViewHolder&lt;/code&gt; is called it needs to be put in the right view layout and update the &lt;code&gt;ViewHolder&lt;/code&gt;.&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemViewType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
    &lt;span class=&quot;hljs-type&quot;&gt;TypedData&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;data&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mDataSource.get(position);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; data.type;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent,
    &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; ViewHolder.makeViewHolder(parent, viewType);
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewHolder holder,
    &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
    &lt;span class=&quot;hljs-type&quot;&gt;TypedData&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;data&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mDataSource.get(position);
    holder.updateData(data);
}

&lt;span class=&quot;hljs-comment&quot;&gt;///&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt;
    &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

    ViewGroup mParentViewGroup;
    View mCurrentViewThisViewHolderIsFor;
    &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; mDataType;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; TypeOneViewHolder mTypeOneViewHolder;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; TypeTwoViewHolder mTypeTwoViewHolder;

    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;makeViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup vwGrp,
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; dataType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;v&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getLayoutView(vwGrp, dataType);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder&lt;/span&gt;(vwGrp, v, viewType);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; View &lt;span class=&quot;hljs-title function_&quot;&gt;getLayoutView&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup vwGrp,
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; dataType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;layoutId&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getLayoutId(dataType);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; LayoutInflater.from(vwGrp.getContext())
                             .inflate(layoutId, &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getLayoutId&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; dataType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (dataType == TYPE_ONE) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; R.layout.type_one_layout;
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (dataType == TYPE_TWO) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; R.layout.type_two_layout;
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup vwGrp, View v,
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; dataType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(v);
        mDataType = dataType;
        mParentViewGroup = vwGrp;
        mCurrentViewThisViewHolderIsFor = v;

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (data.type == TYPE_ONE) {
            mTypeOneViewHolder = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TypeOneViewHolder&lt;/span&gt;(v);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (data.type == TYPE_TWO) {
            mTypeTwoViewHolder = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TypeTwoViewHolder&lt;/span&gt;(v);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;updateData&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(TypeData data)&lt;/span&gt; {
        mDataType = data.type;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (data.type == TYPE_ONE) {
            mTypeTwoViewHolder = &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mTypeOneViewHolder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;newView&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getLayoutView(mParentViewGroup,
                               data.type);

                &lt;span class=&quot;hljs-comment&quot;&gt;/**
                 *  How can I replace a new view with
                    the view in the parent
                    view container?
                 */&lt;/span&gt;
                replaceView(mCurrentViewThisViewHolderIsFor,
                            newView);
                mCurrentViewThisViewHolderIsFor = newView;

                mTypeOneViewHolder =
                    &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TypeOneViewHolder&lt;/span&gt;(newView);
            }
            mTypeOneViewHolder.updateDataTypeOne(data);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (data.type == TYPE_TWO){
            mTypeOneViewHolder = &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mTypeTwoViewHolder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;newView&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getLayoutView(mParentViewGroup,
                               data.type);

                &lt;span class=&quot;hljs-comment&quot;&gt;/**
                 *  How can I replace a new view with
                    the view in the parent view
                    container?
                 */&lt;/span&gt;
                replaceView(mCurrentViewThisViewHolderIsFor,
                            newView);
                mCurrentViewThisViewHolderIsFor = newView;

                mTypeTwoViewHolder =
                    &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;TypeTwoViewHolder&lt;/span&gt;(newView);
            }
            mTypeTwoViewHolder.updateDataTypeOne(data);
        }
    }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;replaceView&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View currentView,
    View newView)&lt;/span&gt; {
    &lt;span class=&quot;hljs-type&quot;&gt;ViewGroup&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;parent&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (ViewGroup)currentView.getParent();
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(parent == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;index&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; parent.indexOfChild(currentView);
    parent.removeView(currentView);
    parent.addView(newView, index);
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;ViewHolder has member mItemViewType to hold the view.&lt;/p&gt;
&lt;p&gt;It looks like in onBindViewHolder(ViewHolder holder, int position) the ViewHolder passed in has been picked up (or created) by looked at getItemViewType(int position) to make sure it is a match, so it may not need to worry there that ViewHolder&apos;s type does not match the data[position]&apos;s type.&lt;/p&gt;
&lt;p&gt;It looks like The recycle &lt;code&gt;ViewHolder&lt;/code&gt; is picked by type, so no warrior there.&lt;/p&gt;
&lt;p&gt;&lt;em&gt;&lt;a href=&quot;http://wiresareobsolete.com/2014/09/building-a-recyclerview-layoutmanager-part-1/&quot; rel=&quot;nofollow noreferrer&quot;&gt;Building a RecyclerView LayoutManager  Part 1&lt;/a&gt;&lt;/em&gt; answers this question.&lt;/p&gt;
&lt;p&gt;It gets the recycle &lt;code&gt;ViewHolder&lt;/code&gt; like:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;holder = getRecycledViewPool().getRecycledView(mAdapter.getItemViewType(offsetPosition));
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Or create a new one if not find recycle &lt;code&gt;ViewHolder&lt;/code&gt; of the right type.&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;getRecycledView&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; ArrayList&amp;lt;ViewHolder&amp;gt; scrapHeap = mScrap.get(viewType);
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (scrapHeap != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; &amp;amp;&amp;amp; !scrapHeap.isEmpty()) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;index&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; scrapHeap.size() - &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;scrap&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; scrapHeap.get(index);
            scrapHeap.remove(index);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; scrap;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
    }

View &lt;span class=&quot;hljs-title function_&quot;&gt;getViewForPosition&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position, &lt;span class=&quot;hljs-type&quot;&gt;boolean&lt;/span&gt; dryRun)&lt;/span&gt; {
    ......

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;offsetPosition&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mAdapterHelper.findPositionOffset(position);
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (offsetPosition &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; || offsetPosition &amp;gt;= mAdapter.getItemCount()) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;IndexOutOfBoundsException&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Inconsistency detected. Invalid item &quot;&lt;/span&gt;
                        + &lt;span class=&quot;hljs-string&quot;&gt;&quot;position &quot;&lt;/span&gt; + position + &lt;span class=&quot;hljs-string&quot;&gt;&quot;(offset:&quot;&lt;/span&gt; + offsetPosition + &lt;span class=&quot;hljs-string&quot;&gt;&quot;).&quot;&lt;/span&gt;
                        + &lt;span class=&quot;hljs-string&quot;&gt;&quot;state:&quot;&lt;/span&gt; + mState.getItemCount());
            }

            &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;type&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mAdapter.getItemViewType(offsetPosition);
            &lt;span class=&quot;hljs-comment&quot;&gt;// 2) Find from scrap via stable ids, if exists&lt;/span&gt;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mAdapter.hasStableIds()) {
                holder = getScrapViewForId(mAdapter.getItemId(offsetPosition), type, dryRun);
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                    &lt;span class=&quot;hljs-comment&quot;&gt;// update position&lt;/span&gt;
                    holder.mPosition = offsetPosition;
                    fromScrap = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
                }
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; &amp;amp;&amp;amp; mViewCacheExtension != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                &lt;span class=&quot;hljs-comment&quot;&gt;// We are NOT sending the offsetPosition because LayoutManager does not&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;// know it.&lt;/span&gt;
                &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mViewCacheExtension
                        .getViewForPositionAndType(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;, position, type);
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (view != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                    holder = getChildViewHolder(view);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                        &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;IllegalArgumentException&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;getViewForPositionAndType returned&quot;&lt;/span&gt;
                                + &lt;span class=&quot;hljs-string&quot;&gt;&quot; a view which does not have a ViewHolder&quot;&lt;/span&gt;);
                    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder.shouldIgnore()) {
                        &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;IllegalArgumentException&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;getViewForPositionAndType returned&quot;&lt;/span&gt;
                                + &lt;span class=&quot;hljs-string&quot;&gt;&quot; a view that is ignored. You must call stopIgnoring before&quot;&lt;/span&gt;
                                + &lt;span class=&quot;hljs-string&quot;&gt;&quot; returning this view.&quot;&lt;/span&gt;);
                    }
                }
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) { &lt;span class=&quot;hljs-comment&quot;&gt;// fallback to recycler&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;// try recycler.&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;// Head to the shared pool.&lt;/span&gt;
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (DEBUG) {
                    Log.d(TAG, &lt;span class=&quot;hljs-string&quot;&gt;&quot;getViewForPosition(&quot;&lt;/span&gt; + position + &lt;span class=&quot;hljs-string&quot;&gt;&quot;) fetching from shared &quot;&lt;/span&gt;
                            + &lt;span class=&quot;hljs-string&quot;&gt;&quot;pool&quot;&lt;/span&gt;);
                }
                holder = getRecycledViewPool()
                        .getRecycledView(mAdapter.getItemViewType(offsetPosition));
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                    holder.resetInternal();
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (FORCE_INVALIDATE_DISPLAY_LIST) {
                        invalidateDisplayListInt(holder);
                    }
                }
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (holder == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
                holder = mAdapter.createViewHolder(RecyclerView.&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;,
                        mAdapter.getItemViewType(offsetPosition));
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (DEBUG) {
                    Log.d(TAG, &lt;span class=&quot;hljs-string&quot;&gt;&quot;getViewForPosition created new ViewHolder&quot;&lt;/span&gt;);
                }
            }
        }
        &lt;span class=&quot;hljs-type&quot;&gt;boolean&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;bound&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mState.isPreLayout() &amp;amp;&amp;amp; holder.isBound()) {
            &lt;span class=&quot;hljs-comment&quot;&gt;// do not update unless we absolutely have to.&lt;/span&gt;
            holder.mPreLayoutPosition = position;
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!holder.isBound() || holder.needsUpdate() || holder.isInvalid()) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (DEBUG &amp;amp;&amp;amp; holder.isRemoved()) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;IllegalStateException&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Removed holder should be bound and it should&quot;&lt;/span&gt;
                        + &lt;span class=&quot;hljs-string&quot;&gt;&quot; come here only in pre-layout. Holder: &quot;&lt;/span&gt; + holder);
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;offsetPosition&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; mAdapterHelper.findPositionOffset(position);
            mAdapter.bindViewHolder(holder, offsetPosition);
            attachAccessibilityDelegate(holder.itemView);
            bound = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mState.isPreLayout()) {
                holder.mPreLayoutPosition = position;
            }
        }

        &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; ViewGroup.&lt;span class=&quot;hljs-type&quot;&gt;LayoutParams&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;lp&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; holder.itemView.getLayoutParams();
        &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; LayoutParams rvLayoutParams;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (lp == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
            rvLayoutParams = (LayoutParams) generateDefaultLayoutParams();
            holder.itemView.setLayoutParams(rvLayoutParams);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!checkLayoutParams(lp)) {
            rvLayoutParams = (LayoutParams) generateLayoutParams(lp);
            holder.itemView.setLayoutParams(rvLayoutParams);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            rvLayoutParams = (LayoutParams) lp;
        }
        rvLayoutParams.mViewHolder = holder;
        rvLayoutParams.mPendingInvalidate = fromScrap &amp;amp;&amp;amp; bound;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; holder.itemView;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Although the selected answer is correct, I just want to further elaborate it. I found a useful &lt;a href=&quot;https://handyopinion.com/recyclerview-custom-adapter-with-multiple-view-types-in-java-android/&quot; rel=&quot;nofollow noreferrer&quot;&gt;Custom Adapter for multiple View Types in RecyclerView&lt;/a&gt;.
Its &lt;a href=&quot;https://handyopinion.com/custom-adapter-with-multiple-view-types-for-recyclerview-in-kotlin-android/&quot; rel=&quot;nofollow noreferrer&quot;&gt;Kotlin version is here&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;The custom adapter is the following:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CustomAdapter&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.Adapter&amp;lt;RecyclerView.ViewHolder&amp;gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; Context context;
    ArrayList&amp;lt;String&amp;gt; list; &lt;span class=&quot;hljs-comment&quot;&gt;// ArrayList of your Data Model&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;VIEW_TYPE_ONE&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;VIEW_TYPE_TWO&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;CustomAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Context context, ArrayList&amp;lt;String&amp;gt; list)&lt;/span&gt; { &lt;span class=&quot;hljs-comment&quot;&gt;// you can pass other parameters in constructor&lt;/span&gt;
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.context = context;
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.list = list;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder1&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

        TextView yourView;
        ViewHolder1(&lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; View itemView) {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
            yourView = itemView.findViewById(R.id.yourView); &lt;span class=&quot;hljs-comment&quot;&gt;// Initialize your All views prensent in list items&lt;/span&gt;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;bind&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
            &lt;span class=&quot;hljs-comment&quot;&gt;// This method will be called anytime a list item is created or update its data&lt;/span&gt;
            &lt;span class=&quot;hljs-comment&quot;&gt;// Do your stuff here&lt;/span&gt;
            yourView.setText(list.get(position));
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder2&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

        TextView yourView;
        ViewHolder2(&lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; View itemView) {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
            yourView = itemView.findViewById(R.id.yourView); &lt;span class=&quot;hljs-comment&quot;&gt;// Initialize your All views prensent in list items&lt;/span&gt;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;bind&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
            &lt;span class=&quot;hljs-comment&quot;&gt;// This method will be called anytime a list item is created or update its data&lt;/span&gt;
            &lt;span class=&quot;hljs-comment&quot;&gt;//Do your stuff here&lt;/span&gt;
            yourView.setText(list.get(position));
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
       &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType == VIEW_TYPE_ONE) {
           &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder1&lt;/span&gt;(LayoutInflater.from(context).inflate(R.layout.your_list_item_1, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;));
       }
       &lt;span class=&quot;hljs-comment&quot;&gt;//if its not VIEW_TYPE_ONE then its VIEW_TYPE_TWO&lt;/span&gt;
       &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder2&lt;/span&gt;(LayoutInflater.from(context).inflate(R.layout.your_list_item_2, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;));

    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(RecyclerView.ViewHolder holder, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (list.get(position).type == Something) { &lt;span class=&quot;hljs-comment&quot;&gt;// Put your condition, according to your requirements&lt;/span&gt;
            ((ViewHolder1) holder).bind(position);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            ((ViewHolder2) holder).bind(position);
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemCount&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; list.size();
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemViewType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
        &lt;span class=&quot;hljs-comment&quot;&gt;// Here you can get decide from your model&apos;s ArrayList, which type of view you need to load. Like&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (list.get(position).type == Something) { &lt;span class=&quot;hljs-comment&quot;&gt;// Put your condition, according to your requirements&lt;/span&gt;
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; VIEW_TYPE_ONE;
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; VIEW_TYPE_TWO;
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I have a better solution which allows to create multiple view types in a declarative and type safe way. Its written in Kotlin which, by the way, is really nice.&lt;/p&gt;
&lt;p&gt;Simple view holders for all required view types&lt;/p&gt;

&lt;pre class=&quot;lang-scala s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ViewHolderMedium&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;itemView: &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;(itemView) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; icon: &lt;span class=&quot;hljs-type&quot;&gt;ImageView&lt;/span&gt; = itemView.findViewById(&lt;span class=&quot;hljs-type&quot;&gt;R&lt;/span&gt;.id.icon) as &lt;span class=&quot;hljs-type&quot;&gt;ImageView&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; label: &lt;span class=&quot;hljs-type&quot;&gt;TextView&lt;/span&gt; = itemView.findViewById(&lt;span class=&quot;hljs-type&quot;&gt;R&lt;/span&gt;.id.label) as &lt;span class=&quot;hljs-type&quot;&gt;TextView&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;There is an abstraction of adapter data item. Note that a view type is represented by a hashCode of particular view holder class (KClass in Kotlin)&lt;/p&gt;
&lt;pre class=&quot;lang-scala s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;trait&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;AdapterItem&lt;/span&gt; &lt;/span&gt;{
   &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; viewType: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;
   fun bindViewHolder(viewHolder: &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;)
}

&lt;span class=&quot;hljs-keyword&quot;&gt;abstract&lt;/span&gt; &lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;AdapterItemBase&amp;lt;T&amp;gt;&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;val viewHolderClass: &lt;span class=&quot;hljs-type&quot;&gt;KClass&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;&amp;gt;&lt;/span&gt;) &lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;AdapterItem&lt;/span&gt; {
   &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; viewType: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt; = viewHolderClass.hashCode()
   &lt;span class=&quot;hljs-keyword&quot;&gt;abstract&lt;/span&gt; fun bindViewHolder(viewHolder: &lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;)
   &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun bindViewHolder(viewHolder: &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;) {
       bindViewHolder(viewHolder as &lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;)
   }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Only &lt;code&gt;bindViewHolder&lt;/code&gt; needs to be overridden in concrete adapter item classes (type safe way).&lt;/p&gt;
&lt;pre class=&quot;lang-scala s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;AdapterItemMedium&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;val icon: &lt;span class=&quot;hljs-type&quot;&gt;Drawable&lt;/span&gt;, val label: &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt;, val onClick: (&lt;/span&gt;) &lt;span class=&quot;hljs-title&quot;&gt;-&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Unit&lt;/span&gt;) &lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;AdapterItemBase&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;ViewHolderMedium&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-type&quot;&gt;ViewHolderMedium&lt;/span&gt;::&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;) &lt;/span&gt;{
    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun bindViewHolder(viewHolder: &lt;span class=&quot;hljs-type&quot;&gt;ViewHolderMedium&lt;/span&gt;) {
        viewHolder.icon.setImageDrawable(icon)
        viewHolder.label.setText(label)
        viewHolder.itemView.setOnClickListener { onClick() }
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;List of such &lt;code&gt;AdapterItemMedium&lt;/code&gt; objects is a data source for the adapter which actually accepts &lt;code&gt;List&amp;lt;AdapterItem&amp;gt;&lt;/code&gt;. See below.&lt;/p&gt;
&lt;p&gt;The important part of this solution is a view holder factory which will provide fresh instances of a specific ViewHolder:&lt;/p&gt;
&lt;pre class=&quot;lang-scala s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ViewHolderProvider&lt;/span&gt; &lt;/span&gt;{
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; viewHolderFactories = hashMapOf&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;, &lt;span class=&quot;hljs-type&quot;&gt;Pair&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;, &lt;span class=&quot;hljs-type&quot;&gt;Any&lt;/span&gt;&amp;gt;&amp;gt;()

    fun provideViewHolder(viewGroup: &lt;span class=&quot;hljs-type&quot;&gt;ViewGroup&lt;/span&gt;, viewType: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;): &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; (layoutId: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;, f: &lt;span class=&quot;hljs-type&quot;&gt;Any&lt;/span&gt;) = viewHolderFactories.get(viewType)
        &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; viewHolderFactory = f as (&lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt;) -&amp;gt; &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; view = &lt;span class=&quot;hljs-type&quot;&gt;LayoutInflater&lt;/span&gt;.from(viewGroup.getContext()).inflate(layoutId, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;)
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; viewHolderFactory(view)
    }

    fun registerViewHolderFactory&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;&amp;gt;(key: &lt;span class=&quot;hljs-type&quot;&gt;KClass&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;&amp;gt;, layoutId: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;, viewHolderFactory: (&lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt;) -&amp;gt; &lt;span class=&quot;hljs-type&quot;&gt;T&lt;/span&gt;) {
        viewHolderFactories.put(key.hashCode(), &lt;span class=&quot;hljs-type&quot;&gt;Pair&lt;/span&gt;(layoutId, viewHolderFactory))
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;And the simple adapter class looks like this:&lt;/p&gt;
&lt;pre class=&quot;lang-scala s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scala&quot;&gt;public &lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;MultitypeAdapter&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;val items: &lt;span class=&quot;hljs-type&quot;&gt;List&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;AdapterItem&lt;/span&gt;&amp;gt;&lt;/span&gt;) &lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;Adapter&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;&amp;gt;() {

   &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; viewHolderProvider = &lt;span class=&quot;hljs-type&quot;&gt;ViewHolderProvider&lt;/span&gt;() &lt;span class=&quot;hljs-comment&quot;&gt;// inject ex Dagger2&lt;/span&gt;

   init {
        viewHolderProvider!!.registerViewHolderFactory(&lt;span class=&quot;hljs-type&quot;&gt;ViewHolderMedium&lt;/span&gt;::&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;, &lt;span class=&quot;hljs-title&quot;&gt;R&lt;/span&gt;.&lt;span class=&quot;hljs-title&quot;&gt;layout&lt;/span&gt;.&lt;span class=&quot;hljs-title&quot;&gt;item_medium&lt;/span&gt;, &lt;/span&gt;{ itemView -&amp;gt;
            &lt;span class=&quot;hljs-type&quot;&gt;ViewHolderMedium&lt;/span&gt;(itemView)
        })
   }

   &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun getItemViewType(position: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;): &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; items[position].viewType
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun getItemCount(): &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; items.size()
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun onCreateViewHolder(viewGroup: &lt;span class=&quot;hljs-type&quot;&gt;ViewGroup&lt;/span&gt;, viewType: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;): &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;? {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; viewHolderProvider!!.provideViewHolder(viewGroup, viewType)
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; fun onBindViewHolder(viewHolder: &lt;span class=&quot;hljs-type&quot;&gt;RecyclerView&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt;, position: &lt;span class=&quot;hljs-type&quot;&gt;Int&lt;/span&gt;) {
        items[position].bindViewHolder(viewHolder)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;There are &lt;em&gt;only&lt;/em&gt; three steps to create a new view type:&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;create a view holder class&lt;/li&gt;
&lt;li&gt;create an adapter item class (extending from AdapterItemBase)&lt;/li&gt;
&lt;li&gt;register the view holder class in &lt;code&gt;ViewHolderProvider&lt;/code&gt;&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;Here is an example of this concept: &lt;a href=&quot;https://github.com/michalfaber/android-drawer-template&quot; rel=&quot;nofollow noreferrer&quot; title=&quot;GitHub&quot;&gt;android-drawer-template&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;It goes even further - a view type which acts as a spinner component, with selectable adapter items.&lt;/p&gt;

&lt;p&gt;It is very simple and straightforward.&lt;/p&gt;
&lt;p&gt;Just &lt;em&gt;override&lt;/em&gt; the &lt;a href=&quot;https://developer.android.com/reference/android/support/v7/widget/RecyclerView.Adapter.html#getItemViewType(int)&quot; rel=&quot;nofollow noreferrer&quot;&gt;getItemViewType()&lt;/a&gt; method in your adapter. On the basis of data return different itemViewType values. E.g., consider an object of type Person with a member isMale, if isMale is true, return 1 and isMale is false, return 2 in the &lt;a href=&quot;https://developer.android.com/reference/android/support/v7/widget/RecyclerView.Adapter.html#getItemViewType(int)&quot; rel=&quot;nofollow noreferrer&quot;&gt;getItemViewType()&lt;/a&gt; method.&lt;/p&gt;
&lt;p&gt;Now coming to the &lt;a href=&quot;https://developer.android.com/reference/android/support/v7/widget/RecyclerView.Adapter.html#createViewHolder(android.view.ViewGroup,%20int)&quot; rel=&quot;nofollow noreferrer&quot;&gt;createViewHolder (ViewGroup parent, int viewType)&lt;/a&gt;, on the basis of different viewType yon can inflate the different layout file. Like the following:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
    &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(parent.getContext()).inflate(R.layout.male, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AdapterMaleViewHolder&lt;/span&gt;(view);
}
&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{
    &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(parent.getContext()).inflate(R.layout.female, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AdapterFemaleViewHolder&lt;/span&gt;(view);
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;in &lt;a href=&quot;https://developer.android.com/reference/android/support/v7/widget/RecyclerView.Adapter.html#onBindViewHolder(VH,%20int)&quot; rel=&quot;nofollow noreferrer&quot;&gt;onBindViewHolder (VH holder,int position)&lt;/a&gt; check where holder is an instance of &lt;code&gt;AdapterFemaleViewHolder&lt;/code&gt; or &lt;code&gt;AdapterMaleViewHolder&lt;/code&gt; by &lt;code&gt;instanceof&lt;/code&gt; and accordingly assign the values.&lt;/p&gt;
&lt;p&gt;ViewHolder &lt;em&gt;may&lt;/em&gt; be like this&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;    &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AdapterMaleViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {
            ...
            &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;AdapterMaleViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt;{
            ...
            }
        }

    &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AdapterFemaleViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {
         ...
         &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;AdapterFemaleViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt;{
            ...
         }
    }
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I recommend this library from Hannes Dorfmann. It encapsulates all the logic related to a particular view type in a separate object called &quot;AdapterDelegate&quot;.&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://github.com/sockeqwe/AdapterDelegates&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/sockeqwe/AdapterDelegates&lt;/a&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CatAdapterDelegate&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AdapterDelegate&lt;/span&gt;&amp;lt;List&amp;lt;Animal&amp;gt;&amp;gt; {

  &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; LayoutInflater inflater;

  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;CatAdapterDelegate&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Activity activity)&lt;/span&gt; {
    inflater = activity.getLayoutInflater();
  }

  &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;boolean&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;isForViewType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; List&amp;lt;Animal&amp;gt; items, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; items.get(position) &lt;span class=&quot;hljs-keyword&quot;&gt;instanceof&lt;/span&gt; Cat;
  }

  &lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CatViewHolder&lt;/span&gt;(inflater.inflate(R.layout.item_cat, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;));
  }

  &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; List&amp;lt;Animal&amp;gt; items, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position,
      &lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; RecyclerView.ViewHolder holder, &lt;span class=&quot;hljs-meta&quot;&gt;@Nullable&lt;/span&gt; List&amp;lt;Object&amp;gt; payloads)&lt;/span&gt; {

    &lt;span class=&quot;hljs-type&quot;&gt;CatViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;vh&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (CatViewHolder) holder;
    &lt;span class=&quot;hljs-type&quot;&gt;Cat&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;cat&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (Cat) items.get(position);

    vh.name.setText(cat.getName());
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CatViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; TextView name;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;CatViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt; {
      &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
      name = (TextView) itemView.findViewById(R.id.name);
    }
  }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AnimalAdapter&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ListDelegationAdapter&lt;/span&gt;&amp;lt;List&amp;lt;Animal&amp;gt;&amp;gt; {

  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;AnimalAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Activity activity, List&amp;lt;Animal&amp;gt; items)&lt;/span&gt; {

    &lt;span class=&quot;hljs-comment&quot;&gt;// DelegatesManager is a protected Field in ListDelegationAdapter&lt;/span&gt;
    delegatesManager.addDelegate(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CatAdapterDelegate&lt;/span&gt;(activity))
                    .addDelegate(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DogAdapterDelegate&lt;/span&gt;(activity))
                    .addDelegate(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;GeckoAdapterDelegate&lt;/span&gt;(activity))
                    .addDelegate(&lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;SnakeAdapterDelegate&lt;/span&gt;(activity));

    &lt;span class=&quot;hljs-comment&quot;&gt;// Set the items from super class.&lt;/span&gt;
    setItems(items);
  }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I firstly recommend you to read Hannes Dorfmann&apos;s &lt;a href=&quot;http://hannesdorfmann.com/android/adapter-delegates&quot; rel=&quot;nofollow noreferrer&quot;&gt;great article&lt;/a&gt; about this topic.&lt;/p&gt;
&lt;p&gt;When a new view type comes, you have to edit your adapter and you have to handle so many messy things. Your adapter should be &lt;em&gt;open&lt;/em&gt; for extension, but &lt;em&gt;closed&lt;/em&gt; for modification.&lt;/p&gt;
&lt;p&gt;You may check this two project, they can give the idea about how to handle different ViewTypes in Adapter:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;&lt;a href=&quot;https://github.com/sockeqwe/AdapterDelegates&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/sockeqwe/AdapterDelegates&lt;/a&gt;&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;&lt;a href=&quot;https://github.com/ibrahimyilmaz/kiel&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/ibrahimyilmaz/kiel&lt;/a&gt;&lt;/p&gt;
&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;Actually, I&apos;d like to improve on &lt;a href=&quot;https://stackoverflow.com/a/26245463/6523288&quot;&gt;Anton&apos;s answer&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;Since &lt;code&gt;getItemViewType(int position)&lt;/code&gt; returns an integer value, you can return the layout resource ID you&apos;d need to inflate. That way you&apos;d save some logic in &lt;code&gt;onCreateViewHolder(ViewGroup parent, int viewType)&lt;/code&gt; method.&lt;/p&gt;

&lt;p&gt;Also, I wouldn&apos;t suggest doing intensive calculations in &lt;code&gt;getItemCount()&lt;/code&gt; as that particular function is called at least 5 times while rendering the list, as well as while rendering each item beyond the visible items. Sadly since &lt;code&gt;notifyDatasetChanged()&lt;/code&gt; method is final, you can&apos;t really override it, but you can call it from another function within the adapter.&lt;/p&gt;

&lt;p&gt;You can use the library: &lt;a href=&quot;https://github.com/vivchar/RendererRecyclerViewAdapter&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/vivchar/RendererRecyclerViewAdapter&lt;/a&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;mRecyclerViewAdapter = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RendererRecyclerViewAdapter&lt;/span&gt;(); &lt;span class=&quot;hljs-comment&quot;&gt;/* Included from library */&lt;/span&gt;
mRecyclerViewAdapter.registerRenderer(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;SomeViewRenderer&lt;/span&gt;(SomeModel.TYPE, &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;));
mRecyclerViewAdapter.registerRenderer(...); &lt;span class=&quot;hljs-comment&quot;&gt;/* You can use several types of cells */&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;For each item, you should to implement a ViewRenderer, ViewHolder, SomeModel:&lt;/p&gt;
&lt;p&gt;ViewHolder - it is a simple view holder of recycler view.&lt;/p&gt;
&lt;p&gt;SomeModel - it is your model with &lt;code&gt;ItemModel&lt;/code&gt; interface&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;SomeViewRenderer&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewRenderer&lt;/span&gt;&amp;lt;SomeModel, SomeViewHolder&amp;gt; {

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;SomeViewRenderer&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; type, &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; Context context)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(type, context);
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;bindView&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; SomeModel model, &lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; SomeViewHolder holder)&lt;/span&gt; {
        holder.mTitle.setText(model.getTitle());
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt;
    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; SomeViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;createViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@Nullable&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; ViewGroup parent)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;SomeViewHolder&lt;/span&gt;(LayoutInflater.from(getContext()).inflate(R.layout.some_item, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;));
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;For more details you can look at the documentation.&lt;/p&gt;

&lt;p&gt;View types implementation becomes easier with Kotlin. Here is a sample with this light library &lt;a href=&quot;https://github.com/Link184/KidAdapter&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/Link184/KidAdapter&lt;/a&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;recyclerView.setUp {
    withViewType {
        withLayoutResId(R.layout.item_int)
        withItems(mutableListOf(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;))
        bind&amp;lt;Int&amp;gt; { &lt;span class=&quot;hljs-comment&quot;&gt;// this - is adapter view hoder itemView, it - current item&lt;/span&gt;
            intName.text = it.toString()
        }
    }


    withViewType(&lt;span class=&quot;hljs-string&quot;&gt;&quot;SECOND_STRING_TAG&quot;&lt;/span&gt;) {
        withLayoutResId(R.layout.item_text)
        withItems(mutableListOf(&lt;span class=&quot;hljs-string&quot;&gt;&quot;eight&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;nine&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;ten&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;eleven&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;twelve&quot;&lt;/span&gt;))
        bind&amp;lt;String&amp;gt; {
            stringName.text = it
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can deal with multipleViewTypes &lt;code&gt;RecyclerAdapter&lt;/code&gt; by making &lt;code&gt;getItemViewType()&lt;/code&gt; return the expected &lt;code&gt;viewType&lt;/code&gt; value for that position.&lt;/p&gt;
&lt;p&gt;I prepared an &lt;code&gt;MultipleViewTypeAdapter&lt;/code&gt; for constructing an MCQ list for examinations which may throw a question that may have two or more valid answers (checkbox options) and a single answer questions (radiobutton options).&lt;/p&gt;
&lt;p&gt;For this I get the type of question from the API response and I used that for deciding which view I have to show for that question.&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MultiViewTypeAdapter&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.Adapter {

    Context mContext;
    ArrayList&amp;lt;Question&amp;gt; dataSet;
    ArrayList&amp;lt;String&amp;gt; questions;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; Object radiobuttontype1;


    &lt;span class=&quot;hljs-comment&quot;&gt;//Viewholder to display Questions with checkboxes&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Checkboxtype2&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {
        ImageView imgclockcheck;
        CheckBox checkbox;

        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;Checkboxtype2&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; View itemView)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
            imgclockcheck = (ImageView) itemView.findViewById(R.id.clockout_cbox_image);
            checkbox = (CheckBox) itemView.findViewById(R.id.clockout_cbox);
        }
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;//Viewholder to display Questions with radiobuttons&lt;/span&gt;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Radiobuttontype1&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {
        ImageView clockout_imageradiobutton;
        RadioButton clockout_radiobutton;
        TextView sample;

        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;radiobuttontype1&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View itemView)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
            clockout_imageradiobutton = (ImageView) itemView.findViewById(R.id.clockout_imageradiobutton);
            clockout_radiobutton = (RadioButton) itemView.findViewById(R.id.clockout_radiobutton);
            sample = (TextView) itemView.findViewById(R.id.sample);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;MultiViewTypeAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ArrayList&amp;lt;QueDatum&amp;gt; data, Context context)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.dataSet = data;
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.mContext = context;
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt;
    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; ViewGroup viewGroup, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;1&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.clockout_radio_list_row, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;radiobuttontype1&lt;/span&gt;(view);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.clockout_cbox_list_row, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            view.setHorizontalFadingEdgeEnabled(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Checkboxtype2&lt;/span&gt;(view);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;3&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.clockout_radio_list_row, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Radiobuttontype1&lt;/span&gt;(view);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;4&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.clockout_radio_list_row, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Radiobuttontype1&lt;/span&gt;(view);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;5&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.clockout_radio_list_row, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Radiobuttontype1&lt;/span&gt;(view);
        }

        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; RecyclerView.ViewHolder viewHolder, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;1&quot;&lt;/span&gt;)) {
            options =  dataSet.get(i).getOptions();
            question = dataSet.get(i).getQuestion();
            image = options.get(i).getValue();
            ((radiobuttontype1) viewHolder).clockout_radiobutton.setChecked(&lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
            ((radiobuttontype1) viewHolder).sample.setText(question);
            &lt;span class=&quot;hljs-comment&quot;&gt;//Loading image bitmap in the ViewHolder&apos;s View&lt;/span&gt;
            Picasso.with(mContext)
                    .load(image)
                    .into(((radiobuttontype1) viewHolder).clockout_imageradiobutton);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2&quot;&lt;/span&gt;)) {
            options = (ArrayList&amp;lt;Clockout_questions_Option&amp;gt;) dataSet.get(i).getOptions();
            question = dataSet.get(i).getQuestion();
            image = options.get(i).getValue();
            &lt;span class=&quot;hljs-comment&quot;&gt;//Loading image bitmap in the ViewHolder&apos;s View&lt;/span&gt;
            Picasso.with(mContext)
                    .load(image)
                    .into(((Checkboxtype2) viewHolder).imgclockcheck);

        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;3&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-comment&quot;&gt;//Fit data to viewHolder for ViewType 3&lt;/span&gt;
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;4&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-comment&quot;&gt;//Fit data to viewHolder for ViewType 4&lt;/span&gt;
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType.equalsIgnoreCase(&lt;span class=&quot;hljs-string&quot;&gt;&quot;5&quot;&lt;/span&gt;)) {
            &lt;span class=&quot;hljs-comment&quot;&gt;//Fit data to viewHolder for ViewType 5&lt;/span&gt;
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemCount&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; dataSet.size();
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;/**
     * Returns viewType for that position by picking the viewType value from the
     *     dataset
     */&lt;/span&gt;
    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemViewType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; dataSet.get(position).getViewType();
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;You can avoid multiple conditionals based viewHolder data fillings in &lt;code&gt;onBindViewHolder()&lt;/code&gt; by assigning same ids for the similar views across viewHolders which differ in their positioning.&lt;/p&gt;

&lt;p&gt;If you want to use it in conjunction with Android Data Binding look into the &lt;a href=&quot;https://github.com/evant/binding-collection-adapter&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/evant/binding-collection-adapter&lt;/a&gt; - it is by far the best solution for the multiple view types &lt;code&gt;RecyclerView&lt;/code&gt; I have even seen.&lt;/p&gt;
&lt;p&gt;You may use it like&lt;/p&gt;
&lt;pre class=&quot;lang-kotlin s-code-block&quot;&gt;&lt;code class=&quot;hljs language-kotlin&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; items: AsyncDiffPagedObservableList&amp;lt;BaseListItem&amp;gt; =
        AsyncDiffPagedObservableList(GenericDiff)

    &lt;span class=&quot;hljs-keyword&quot;&gt;val&lt;/span&gt; onItemBind: OnItemBind&amp;lt;BaseListItem&amp;gt; =
        OnItemBind { itemBinding, _, item -&amp;gt; itemBinding.&lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;(BR.item, item.layoutRes) }
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;And then in the layout where the list is:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt; &amp;lt;androidx.recyclerview.widget.RecyclerView
                android:layout_width=&lt;span class=&quot;hljs-string&quot;&gt;&quot;match_parent&quot;&lt;/span&gt;
                android:layout_height=&lt;span class=&quot;hljs-string&quot;&gt;&quot;0dp&quot;&lt;/span&gt;
                android:layout_weight=&lt;span class=&quot;hljs-string&quot;&gt;&quot;1&quot;&lt;/span&gt;
                app:enableAnimations=&lt;span class=&quot;hljs-string&quot;&gt;&quot;@{false}&quot;&lt;/span&gt;
                app:scrollToPosition=&lt;span class=&quot;hljs-string&quot;&gt;&quot;@{viewModel.scrollPosition}&quot;&lt;/span&gt;

                app:itemBinding=&lt;span class=&quot;hljs-string&quot;&gt;&quot;@{viewModel.onItemBind}&quot;&lt;/span&gt;
                app:items=&lt;span class=&quot;hljs-string&quot;&gt;&quot;@{viewModel.items}&quot;&lt;/span&gt;

                app:reverseLayoutManager=&lt;span class=&quot;hljs-string&quot;&gt;&quot;@{true}&quot;&lt;/span&gt;/&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Your list items must implement the &lt;code&gt;BaseListItem&lt;/code&gt; interface which looks like this:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;interface&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;BaseListItem&lt;/span&gt; {
    val layoutRes: Int
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;And the item view should look something like this:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&amp;lt;layout xmlns:android=&lt;span class=&quot;hljs-string&quot;&gt;&quot;http://schemas.android.com/apk/res/android&quot;&lt;/span&gt;&amp;gt;

    &amp;lt;data&amp;gt;

        &amp;lt;variable
                name=&lt;span class=&quot;hljs-string&quot;&gt;&quot;item&quot;&lt;/span&gt;
                type=&lt;span class=&quot;hljs-string&quot;&gt;&quot;...presentation.somescreen.list.YourListItem&quot;&lt;/span&gt;/&amp;gt;
    &amp;lt;/data&amp;gt;

   ...

&amp;lt;/layout&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Where &lt;code&gt;YourListItem&lt;/code&gt; implements &lt;code&gt;BaseListItem&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;First you must create two layout XML files. After that inside recyclerview adapter TYPE_CALL and TYPE_EMAIL are two static values with 1 and 2 respectively in the adapter class.&lt;/p&gt;
&lt;p&gt;Now &lt;em&gt;define&lt;/em&gt; two static values at the Recycler view Adapter class level, for example: private static int TYPE_CALL = 1; private static int TYPE_EMAIL = 2;&lt;/p&gt;
&lt;p&gt;Now create the view holder with multiple views like this:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CallViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; TextView txtName;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; TextView txtAddress;

    CallViewHolder(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; View itemView) {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
        txtName = itemView.findViewById(R.id.txtName);
        txtAddress = itemView.findViewById(R.id.txtAddress);
    }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;EmailViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; TextView txtName;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; TextView txtAddress;

    EmailViewHolder(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; View itemView) {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(itemView);
        txtName = itemView.findViewById(R.id.txtName);
        txtAddress = itemView.findViewById(R.id.txtAddress);
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now code as below in onCreateViewHolder and onBindViewHolder method in the recyclerview adapter:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; ViewGroup viewGroup, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
    View view;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (viewType == TYPE_CALL) { &lt;span class=&quot;hljs-comment&quot;&gt;// for call layout&lt;/span&gt;
        view = LayoutInflater.from(context).inflate(R.layout.item_call, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;CallViewHolder&lt;/span&gt;(view);

    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; { &lt;span class=&quot;hljs-comment&quot;&gt;// for email layout&lt;/span&gt;
        view = LayoutInflater.from(context).inflate(R.layout.item_email, viewGroup, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;EmailViewHolder&lt;/span&gt;(view);
    }
}

&lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; RecyclerView.ViewHolder viewHolder, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (getItemViewType(position) == TYPE_CALL) {
        ((CallViewHolder) viewHolder).setCallDetails(employees.get(position));
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        ((EmailViewHolder) viewHolder).setEmailDetails(employees.get(position));
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I did something like this. I passed &quot;fragmentType&quot; and created two &lt;code&gt;ViewHolders&lt;/code&gt; and on basis of this, I classified my Layouts accordingly in a single adapter that can have different &lt;code&gt;Layouts&lt;/code&gt; and &lt;code&gt;LayoutManagers&lt;/code&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; Context mContext;
&lt;span class=&quot;hljs-keyword&quot;&gt;protected&lt;/span&gt; IOnLoyaltyCardCategoriesItemClicked mListener;
&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String fragmentType;
&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; View view;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;LoyaltyCardsCategoriesRecyclerViewAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Context context, IOnLoyaltyCardCategoriesItemClicked itemListener, String fragmentType)&lt;/span&gt; {
    &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.mContext = context;
    &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.mListener = itemListener;
    &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.fragmentType = fragmentType;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;LoyaltyCardCategoriesFragmentViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder &lt;span class=&quot;hljs-keyword&quot;&gt;implements&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;View&lt;/span&gt;.OnClickListener {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; ImageView lc_categories_iv;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; TextView lc_categories_name_tv;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; pos;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;LoyaltyCardCategoriesFragmentViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View v)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(v);
        view.setOnClickListener(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;);
        lc_categories_iv = (ImageView) v.findViewById(R.id.lc_categories_iv);
        lc_categories_name_tv = (TextView) v.findViewById(R.id.lc_categories_name_tv);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setData&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; pos)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.pos = pos;
        lc_categories_iv.setImageResource(R.mipmap.ic_launcher);
        lc_categories_name_tv.setText(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Loyalty Card Categories&quot;&lt;/span&gt;);
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onClick&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View view)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mListener != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
            mListener.onLoyaltyCardCategoriesItemClicked(pos);
        }
    }
}

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MyLoyaltyCardsFragmentTagViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder &lt;span class=&quot;hljs-keyword&quot;&gt;implements&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;View&lt;/span&gt;.OnClickListener {

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; ImageButton lc_categories_btn;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; pos;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;MyLoyaltyCardsFragmentTagViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View v)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(v);
        lc_categories_btn = (ImageButton) v.findViewById(R.id.lc_categories_btn);
        lc_categories_btn.setOnClickListener(&lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;);
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setData&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; pos)&lt;/span&gt; {
        &lt;span class=&quot;hljs-built_in&quot;&gt;this&lt;/span&gt;.pos = pos;
        lc_categories_btn.setImageResource(R.mipmap.ic_launcher);
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onClick&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(View view)&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (mListener != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) {
            mListener.onLoyaltyCardCategoriesItemClicked(pos);
        }
    }
}

&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; RecyclerView.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (fragmentType.equalsIgnoreCase(Constants.LoyaltyCardCategoriesFragmentTag)) {
        view = LayoutInflater.from(mContext).inflate(R.layout.loyalty_cards_categories_frag_item, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;LoyaltyCardCategoriesFragmentViewHolder&lt;/span&gt;(view);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (fragmentType.equalsIgnoreCase(Constants.MyLoyaltyCardsFragmentTag)) {
        view = LayoutInflater.from(mContext).inflate(R.layout.my_loyalty_cards_categories_frag_item, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MyLoyaltyCardsFragmentTagViewHolder&lt;/span&gt;(view);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
    }
}

&lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(&lt;span class=&quot;hljs-meta&quot;&gt;@NonNull&lt;/span&gt; RecyclerView.ViewHolder holder, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (fragmentType.equalsIgnoreCase(Constants.LoyaltyCardCategoriesFragmentTag)) {
        ((LoyaltyCardCategoriesFragmentViewHolder) holder).setData(position);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (fragmentType.equalsIgnoreCase(Constants.MyLoyaltyCardsFragmentTag)) {
        ((MyLoyaltyCardsFragmentTagViewHolder) holder).setData(position);
    }
}

&lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemCount&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

data.content:

&lt;p&gt;From &lt;em&gt;&lt;a href=&quot;https://developer.android.com/preview/material/ui-widgets.html&quot; rel=&quot;noreferrer&quot;&gt;Create dynamic lists with RecyclerView&lt;/a&gt;&lt;/em&gt;:&lt;/p&gt;
&lt;p&gt;When we create a &lt;code&gt;RecyclerView.Adapter&lt;/code&gt; we have to specify &lt;code&gt;ViewHolder&lt;/code&gt; that will bind with the adapter.&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MyAdapter&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.Adapter&amp;lt;MyAdapter.ViewHolder&amp;gt; {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String[] mDataset;

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;MyAdapter&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String[] myDataset)&lt;/span&gt; {
        mDataset = myDataset;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;extends&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RecyclerView&lt;/span&gt;.ViewHolder {
        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; TextView mTextView;
        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(TextView v)&lt;/span&gt; {
            &lt;span class=&quot;hljs-built_in&quot;&gt;super&lt;/span&gt;(v);
            mTextView = v;
        }
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; MyAdapter.ViewHolder &lt;span class=&quot;hljs-title function_&quot;&gt;onCreateViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewGroup parent, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; viewType)&lt;/span&gt; {
        &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;v&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; LayoutInflater.from(parent.getContext()).inflate(R.layout.some_layout, parent, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);

        &lt;span class=&quot;hljs-comment&quot;&gt;//findViewById...&lt;/span&gt;

        &lt;span class=&quot;hljs-type&quot;&gt;ViewHolder&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;vh&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewHolder&lt;/span&gt;(v);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; vh;
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onBindViewHolder&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ViewHolder holder, &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; position)&lt;/span&gt; {
        holder.mTextView.setText(mDataset[position]);
    }

    &lt;span class=&quot;hljs-meta&quot;&gt;@Override&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getItemCount&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; mDataset.length;
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Is it possible to create &lt;code&gt;RecyclerView&lt;/code&gt; with multiple view types?&lt;/p&gt;
