                i++;
            }
            res = Double.parseDouble(exp[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].substring(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, i));
