&lt;h3&gt;F - Demo Code&lt;/h3&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;package&lt;/span&gt; com.levo.jsonex;

&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.io.File;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.io.IOException;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.util.Arrays;

&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; com.fasterxml.jackson.databind.ObjectMapper;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; com.levo.jsonex.model.Page;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; com.levo.jsonex.model.PageInfo;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; com.levo.jsonex.model.Post;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JSONDemo&lt;/span&gt; {

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;main&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String[] args)&lt;/span&gt; {
        &lt;span class=&quot;hljs-type&quot;&gt;ObjectMapper&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;objectMapper&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ObjectMapper&lt;/span&gt;();

        &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
            &lt;span class=&quot;hljs-type&quot;&gt;Page&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;page&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; objectMapper.readValue(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;File&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;sampleJSONFile.json&quot;&lt;/span&gt;), Page.class);

            printParsedObject(page);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (IOException e) {
            e.printStackTrace();
        }

    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;printParsedObject&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Page page)&lt;/span&gt; {
        printPageInfo(page.getPageInfo());
        System.out.println();
        printPosts(page.getPosts());
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;printPageInfo&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(PageInfo pageInfo)&lt;/span&gt; {
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Page Info;&quot;&lt;/span&gt;);
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;**********&quot;&lt;/span&gt;);
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tPage Name : &quot;&lt;/span&gt; + pageInfo.getPageName());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tPage Pic  : &quot;&lt;/span&gt; + pageInfo.getPagePic());
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;printPosts&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Post[] posts)&lt;/span&gt; {
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Page Posts;&quot;&lt;/span&gt;);
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;**********&quot;&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(Post post : posts) {
            printPost(post);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;printPost&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(Post post)&lt;/span&gt; {
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tPost Id                   : &quot;&lt;/span&gt; + post.getPost_id());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tActor Id                  : &quot;&lt;/span&gt; + post.getActor_id());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tPic Of Person Who Posted  : &quot;&lt;/span&gt; + post.getPicOfPersonWhoPosted());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tName Of Person Who Posted : &quot;&lt;/span&gt; + post.getNameOfPersonWhoPosted());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tMessage                   : &quot;&lt;/span&gt; + post.getMessage());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tLikes Count               : &quot;&lt;/span&gt; + post.getLikesCount());
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tComments                  : &quot;&lt;/span&gt; + Arrays.toString(post.getComments()));
        System.out.println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;\tTime Of Post              : &quot;&lt;/span&gt; + post.getTimeOfPost());
    }

}
&lt;/code&gt;&lt;/pre&gt;
&lt;h3&gt;G - Demo Output&lt;/h3&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;Page Info;
****(*****
    Page Name : abc
    Page Pic  : http:&lt;span class=&quot;hljs-comment&quot;&gt;//example.com/content.jpg&lt;/span&gt;
Page Posts;
**********
    Post Id                   : &lt;span class=&quot;hljs-number&quot;&gt;123456789012_123456789012&lt;/span&gt;
    Actor Id                  : &lt;span class=&quot;hljs-number&quot;&gt;1234567890&lt;/span&gt;
    Pic Of Person Who Posted  : http:&lt;span class=&quot;hljs-comment&quot;&gt;//example.com/photo.jpg&lt;/span&gt;
    Name Of Person Who Posted : Jane Doe
    Message                   : Sounds cool. Can&lt;span class=&quot;hljs-string&quot;&gt;&apos;t wait to see it!
    Likes Count               : 2
    Comments                  : []
    Time Of Post              : 1234567890
&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Almost all the answers given requires a full deserialization of the JSON into a Java object before accessing the value in the property of interest. Another alternative, which does not go this route is to use &lt;a href=&quot;http://goessner.net/articles/JsonPath/&quot; rel=&quot;nofollow noreferrer&quot;&gt;JsonPATH&lt;/a&gt; which is like XPath for JSON and allows traversing of JSON objects.&lt;/p&gt;
&lt;p&gt;It is a specification and the good folks at JayWay have created a Java implementation for the specification which you can find here: &lt;a href=&quot;https://github.com/jayway/JsonPath&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/jayway/JsonPath&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;So basically to use it, add it to your project, eg:&lt;/p&gt;
&lt;pre class=&quot;lang-xml s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;com.jayway.jsonpath&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;json-path&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;${version}&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;and to use:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;pageName&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; JsonPath.read(yourJsonString, &lt;span class=&quot;hljs-string&quot;&gt;&quot;$.pageInfo.pageName&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;pagePic&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; JsonPath.read(yourJsonString, &lt;span class=&quot;hljs-string&quot;&gt;&quot;$.pageInfo.pagePic&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;post_id&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; JsonPath.read(yourJsonString, &lt;span class=&quot;hljs-string&quot;&gt;&quot;$.pagePosts[0].post_id&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;etc...&lt;/p&gt;
&lt;p&gt;Check the JsonPath specification page for more information on the other ways to transverse JSON.&lt;/p&gt;

&lt;p&gt;Use &lt;a href=&quot;https://github.com/ralfstx/minimal-json&quot; rel=&quot;nofollow noreferrer&quot;&gt;minimal-json&lt;/a&gt; which is very fast and easy to use.
You can parse from String obj and Stream.&lt;/p&gt;
&lt;p&gt;Sample data:&lt;/p&gt;
&lt;pre class=&quot;lang-json s-code-block&quot;&gt;&lt;code class=&quot;hljs language-json&quot;&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;{&lt;/span&gt;
  &lt;span class=&quot;hljs-attr&quot;&gt;&quot;order&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;4711&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
  &lt;span class=&quot;hljs-attr&quot;&gt;&quot;items&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;
    &lt;span class=&quot;hljs-punctuation&quot;&gt;{&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;name&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;NE555 Timer IC&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;cat-id&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;645723&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;quantity&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
    &lt;span class=&quot;hljs-punctuation&quot;&gt;}&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
    &lt;span class=&quot;hljs-punctuation&quot;&gt;{&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;name&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;LM358N OpAmp IC&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;cat-id&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;764525&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
      &lt;span class=&quot;hljs-attr&quot;&gt;&quot;quantity&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;:&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;
    &lt;span class=&quot;hljs-punctuation&quot;&gt;}&lt;/span&gt;
  &lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;
&lt;span class=&quot;hljs-punctuation&quot;&gt;}&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Parsing:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;JsonObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;object&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; Json.parse(input).asObject();
&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;orders&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; object.get(&lt;span class=&quot;hljs-string&quot;&gt;&quot;order&quot;&lt;/span&gt;).asInt();
&lt;span class=&quot;hljs-type&quot;&gt;JsonArray&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;items&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; object.get(&lt;span class=&quot;hljs-string&quot;&gt;&quot;items&quot;&lt;/span&gt;).asArray();
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Creating JSON:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;JsonObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;user&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; Json.object().add(&lt;span class=&quot;hljs-string&quot;&gt;&quot;name&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;Sakib&quot;&lt;/span&gt;).add(&lt;span class=&quot;hljs-string&quot;&gt;&quot;age&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Maven:&lt;/p&gt;
&lt;pre class=&quot;lang-xml s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;com.eclipsesource.minimal-json&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;groupId&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;minimal-json&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;artifactId&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;0.9.4&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;version&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;dependency&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The below example shows how to read the text in the question, represented as the &quot;jsonText&quot; variable.  This solution uses the Java EE7 &lt;a href=&quot;http://docs.oracle.com/javaee/7/api/javax/json/package-summary.html&quot; rel=&quot;noreferrer&quot;&gt;javax.json&lt;/a&gt; API (which is mentioned in some of the other answers).  The reason I&apos;ve added it as a separate answer is that the following code shows how to &lt;em&gt;actually&lt;/em&gt; access some of the values shown in the question.  An &lt;a href=&quot;https://jsonp.java.net/download.html&quot; rel=&quot;noreferrer&quot;&gt;implementation of the javax.json API&lt;/a&gt; would be required to make this code run.  The full package for each of the classes required was included as I didn&apos;t want to declare &quot;import&quot; statements.&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;javax.json.&lt;span class=&quot;hljs-type&quot;&gt;JsonReader&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;jr&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; 
    javax.json.Json.createReader(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;StringReader&lt;/span&gt;(jsonText));
javax.json.&lt;span class=&quot;hljs-type&quot;&gt;JsonObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;jo&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; jr.readObject();

&lt;span class=&quot;hljs-comment&quot;&gt;//Read the page info.&lt;/span&gt;
javax.json.&lt;span class=&quot;hljs-type&quot;&gt;JsonObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;pageInfo&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; jo.getJsonObject(&lt;span class=&quot;hljs-string&quot;&gt;&quot;pageInfo&quot;&lt;/span&gt;);
System.out.println(pageInfo.getString(&lt;span class=&quot;hljs-string&quot;&gt;&quot;pageName&quot;&lt;/span&gt;));

&lt;span class=&quot;hljs-comment&quot;&gt;//Read the posts.&lt;/span&gt;
javax.json.&lt;span class=&quot;hljs-type&quot;&gt;JsonArray&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;posts&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; jo.getJsonArray(&lt;span class=&quot;hljs-string&quot;&gt;&quot;posts&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-comment&quot;&gt;//Read the first post.&lt;/span&gt;
javax.json.&lt;span class=&quot;hljs-type&quot;&gt;JsonObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;post&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; posts.getJsonObject(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;);
&lt;span class=&quot;hljs-comment&quot;&gt;//Read the post_id field.&lt;/span&gt;
&lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;postId&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; post.getString(&lt;span class=&quot;hljs-string&quot;&gt;&quot;post_id&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Now, before anyone goes and downvotes this answer because it doesn&apos;t use GSON, org.json, Jackson, or any of the other 3rd party frameworks available, it&apos;s an example of &quot;required code&quot; per the question to parse the provided text.  I am well aware that &lt;a href=&quot;http://openjdk.java.net/jeps/198&quot; rel=&quot;noreferrer&quot;&gt;adherence to the current standard JSR 353 was not being considered for JDK 9&lt;/a&gt; and as such the &lt;a href=&quot;https://jcp.org/en/jsr/detail?id=353&quot; rel=&quot;noreferrer&quot;&gt;JSR 353 spec&lt;/a&gt; should be treated the same as any other 3rd party JSON handling implementation.&lt;/p&gt;

&lt;p&gt;Since nobody mentioned it yet, here is a beginning of a solution using &lt;a href=&quot;http://openjdk.java.net/projects/nashorn/&quot; rel=&quot;noreferrer&quot;&gt;Nashorn&lt;/a&gt; (JavaScript runtime part of Java 8, but deprecated in Java 11).&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Solution&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;EXTRACTOR_SCRIPT&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;var fun = function(raw) { &quot;&lt;/span&gt; +
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;var json = JSON.parse(raw); &quot;&lt;/span&gt; +
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;return [json.pageInfo.pageName, json.pageInfo.pagePic, json.posts[0].post_id];};&quot;&lt;/span&gt;;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;run&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; ScriptException, NoSuchMethodException {
    &lt;span class=&quot;hljs-type&quot;&gt;ScriptEngine&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;engine&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ScriptEngineManager&lt;/span&gt;().getEngineByName(&lt;span class=&quot;hljs-string&quot;&gt;&quot;nashorn&quot;&lt;/span&gt;);
    engine.eval(EXTRACTOR_SCRIPT);
    &lt;span class=&quot;hljs-type&quot;&gt;Invocable&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;invocable&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (Invocable) engine;
    &lt;span class=&quot;hljs-type&quot;&gt;JSObject&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;result&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (JSObject) invocable.invokeFunction(&lt;span class=&quot;hljs-string&quot;&gt;&quot;fun&quot;&lt;/span&gt;, JSON);
    result.values().forEach(e -&amp;gt; System.out.println(e));
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Performance comparison&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;I wrote JSON content containing three arrays of respectively 20, 20 and 100 elements. I only want to get the 100 elements from the third array. I use the following JavaScript function to parse and get my entries.&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;fun&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; function(raw) {JSON.parse(raw).entries};
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Running the call a million times using &lt;strong&gt;Nashorn&lt;/strong&gt; takes 7.5~7.8 seconds&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;(JSObject) invocable.invokeFunction(&lt;span class=&quot;hljs-string&quot;&gt;&quot;fun&quot;&lt;/span&gt;, json);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;org.json&lt;/strong&gt; takes 20~21 seconds&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JSONObject&lt;/span&gt;(JSON).getJSONArray(&lt;span class=&quot;hljs-string&quot;&gt;&quot;entries&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Jackson&lt;/strong&gt; takes 6.5~7 seconds&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;mapper.readValue(JSON, Entries.class).getEntries();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;In this case Jackson performs better than Nashorn, which performs much better than org.json.
Nashorn API is harder to use than org.json&apos;s or Jackson&apos;s. Depending on your requirements Jackson and Nashorn both can be viable solutions.&lt;/p&gt;

&lt;p&gt;This blew my mind with how easy it was. You can just pass a &lt;code&gt;String&lt;/code&gt; holding your JSON to the constructor of a JSONObject in the default org.json package. &lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;JSONArray&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;rootOfPage&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;  &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JSONArray&lt;/span&gt;(JSONString);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Done. &lt;em&gt;Drops microphone&lt;/em&gt;.
This works with &lt;code&gt;JSONObjects&lt;/code&gt; as well. After that, you can just look through your hierarchy of &lt;code&gt;Objects&lt;/code&gt; using the &lt;code&gt;get()&lt;/code&gt; methods on your objects.&lt;/p&gt;

&lt;p&gt;There are many JSON libraries available in Java.&lt;/p&gt;

&lt;p&gt;The most notorious ones are: Jackson, GSON, Genson, FastJson and org.json.&lt;/p&gt;

&lt;p&gt;There are typically three things one should look at for choosing any library:&lt;/p&gt;

&lt;ol&gt;
&lt;li&gt;Performance&lt;/li&gt;
&lt;li&gt;Ease of use (code is simple to write and legible) - that goes with features.&lt;/li&gt;
&lt;li&gt;For mobile apps: dependency/jar size&lt;/li&gt;
&lt;/ol&gt;

&lt;p&gt;Specifically for JSON libraries (and any serialization/deserialization libs), databinding is also usually of interest as it removes the need of writing boiler-plate code to pack/unpack the data.&lt;/p&gt;

&lt;p&gt;For 1, see this benchmark: &lt;a href=&quot;https://github.com/fabienrenaud/java-json-benchmark&quot; rel=&quot;noreferrer&quot;&gt;https://github.com/fabienrenaud/java-json-benchmark&lt;/a&gt; I did using &lt;a href=&quot;http://openjdk.java.net/projects/code-tools/jmh/&quot; rel=&quot;noreferrer&quot;&gt;JMH&lt;/a&gt; which compares (jackson, gson, genson, fastjson, org.json, jsonp) performance of serializers and deserializers using stream and databind APIs.
For 2, you can find numerous examples on the Internet. The benchmark above can also be used as a source of examples...&lt;/p&gt;

&lt;p&gt;Quick takeaway of the benchmark: &lt;strong&gt;Jackson&lt;/strong&gt; performs 5 to 6 times better than org.json and more than twice better than GSON.&lt;/p&gt;

&lt;p&gt;For your particular example, the following code decodes your json with jackson:&lt;/p&gt;

&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;MyObj&lt;/span&gt; {

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; PageInfo pageInfo;
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; List&amp;lt;Post&amp;gt; posts;

    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;PageInfo&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String pageName;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String pagePic;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Post&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String post_id;
        &lt;span class=&quot;hljs-meta&quot;&gt;@JsonProperty(&quot;actor_id&quot;)&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String actorId;
        &lt;span class=&quot;hljs-meta&quot;&gt;@JsonProperty(&quot;picOfPersonWhoPosted&quot;)&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String pictureOfPoster;
        &lt;span class=&quot;hljs-meta&quot;&gt;@JsonProperty(&quot;nameOfPersonWhoPosted&quot;)&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String nameOfPoster;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String likesCount;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; List&amp;lt;String&amp;gt; comments;
        &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; String timeOfPost;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ObjectMapper&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;JACKSON&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ObjectMapper&lt;/span&gt;();
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;main&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String[] args)&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; IOException {
        &lt;span class=&quot;hljs-type&quot;&gt;MyObj&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;o&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; JACKSON.readValue(args[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;], MyObj.class); &lt;span class=&quot;hljs-comment&quot;&gt;// assumes args[0] contains your json payload provided in your question.&lt;/span&gt;
    }
}
&lt;/code&gt;&lt;/pre&gt;

