  },
  {
    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Kyle&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;occupation&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Fashion Designer&apos;&lt;/span&gt;
  },
  {
    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Emily&apos;&lt;/span&gt;,
