  },
  {
    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Tom&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;occupation&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Web Developer&apos;&lt;/span&gt;
  },
  {
    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Tom&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;occupation&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Web Developer&apos;&lt;/span&gt;
  }
]
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;table&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;arrayUnique&lt;/span&gt;(data, &lt;span class=&quot;hljs-string&quot;&gt;&apos;name&apos;&lt;/span&gt;))&lt;span class=&quot;hljs-comment&quot;&gt;// work well&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
