    2     &lt;span class=&quot;hljs-string&quot;&gt;&apos;Melissa&apos;&lt;/span&gt;  &lt;span class=&quot;hljs-string&quot;&gt;&apos;Fashion Designer&apos;&lt;/span&gt; 
    3       &lt;span class=&quot;hljs-string&quot;&gt;&apos;Tom&apos;&lt;/span&gt;     &lt;span class=&quot;hljs-string&quot;&gt;&apos;Web Developer&apos;&lt;/span&gt;   

