        df1 = pd.concat([
            pd.DataFrame({x: np.concatenate(df[x].values)}) &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; x &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; explode], axis=&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
