        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; df1.join(df.drop(explode, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;), how=&lt;span class=&quot;hljs-string&quot;&gt;&apos;left&apos;&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;
