pd.concat([df[&lt;span class=&quot;hljs-string&quot;&gt;&apos;A&apos;&lt;/span&gt;], pd.DataFrame(df[&lt;span class=&quot;hljs-string&quot;&gt;&apos;B&apos;&lt;/span&gt;].values.tolist())], axis = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)\
