       &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt;           #&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;                  &lt;span class=&quot;hljs-comment&quot;&gt;// class java/util/ArrayList&lt;/span&gt;
