   ]
};

&lt;span class=&quot;hljs-comment&quot;&gt;// correct&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&apos;value:&apos;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&apos;help&quot;&apos;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&quot;help\&quot;&quot;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&apos;hello\&apos;&apos;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&quot;hello&apos;&quot;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&quot;data+&quot;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;]);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json[&lt;span class=&quot;hljs-string&quot;&gt;&quot;a[]&quot;&lt;/span&gt;]);

&lt;span class=&quot;hljs-comment&quot;&gt;// wrong&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(json.&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;:);
