&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(person[&lt;span class=&quot;hljs-string&quot;&gt;&apos;my-name&apos;&lt;/span&gt;]); &lt;span class=&quot;hljs-comment&quot;&gt;// &apos;John&apos;&lt;/span&gt;
