&lt;p&gt;That&apos;s the code that fills a &lt;code&gt;select&lt;/code&gt; &lt;code&gt;option&lt;/code&gt; tag in &lt;code&gt;HTML&lt;/code&gt; using &lt;code&gt;ajax&lt;/code&gt; and &lt;code&gt;XMLHttpRequest&lt;/code&gt; with the &lt;code&gt;API&lt;/code&gt; is written in &lt;code&gt;PHP&lt;/code&gt; and &lt;code&gt;PDO&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;conn.php&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;?php
$servername = &lt;span class=&quot;hljs-string&quot;&gt;&quot;localhost&quot;&lt;/span&gt;;
$username = &lt;span class=&quot;hljs-string&quot;&gt;&quot;root&quot;&lt;/span&gt;;
$password = &lt;span class=&quot;hljs-string&quot;&gt;&quot;root&quot;&lt;/span&gt;;
$database = &lt;span class=&quot;hljs-string&quot;&gt;&quot;db_event&quot;&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
    $conn = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;PDO&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;mysql:host=$servername;dbname=$database&quot;&lt;/span&gt;, $username, $password);
    $conn-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;setAttribute&lt;/span&gt;(&lt;span class=&quot;hljs-attr&quot;&gt;PDO&lt;/span&gt;::&lt;span class=&quot;hljs-variable constant_&quot;&gt;ATTR_ERRMODE&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;PDO&lt;/span&gt;::&lt;span class=&quot;hljs-variable constant_&quot;&gt;ERRMODE_EXCEPTION&lt;/span&gt;);
} &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (&lt;span class=&quot;hljs-title class_&quot;&gt;PDOException&lt;/span&gt; $e) {
    echo &lt;span class=&quot;hljs-string&quot;&gt;&quot;Connection failed: &quot;&lt;/span&gt; . $e-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;getMessage&lt;/span&gt;();
}
?&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;category.php&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;?php
 include &lt;span class=&quot;hljs-string&quot;&gt;&apos;conn.php&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
    $data = &lt;span class=&quot;hljs-title function_&quot;&gt;json_decode&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;file_get_contents&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;php://input&quot;&lt;/span&gt;));
    $stmt = $conn-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;prepare&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;SELECT *  FROM events &quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-title function_&quot;&gt;http_response_code&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;);
    $stmt-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;execute&lt;/span&gt;();

    &lt;span class=&quot;hljs-title function_&quot;&gt;header&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type: application/json&apos;&lt;/span&gt;);

    $arr=[];
    &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt;($value=$stmt-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-attr&quot;&gt;PDO&lt;/span&gt;::&lt;span class=&quot;hljs-variable constant_&quot;&gt;FETCH_ASSOC&lt;/span&gt;)){
        &lt;span class=&quot;hljs-title function_&quot;&gt;array_push&lt;/span&gt;($arr,$value);
    }
    echo &lt;span class=&quot;hljs-title function_&quot;&gt;json_encode&lt;/span&gt;($arr);

  } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;PDOException&lt;/span&gt; $e) {
    echo &lt;span class=&quot;hljs-string&quot;&gt;&quot;Error: &quot;&lt;/span&gt; . $e-&amp;gt;&lt;span class=&quot;hljs-title function_&quot;&gt;getMessage&lt;/span&gt;();
  }
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;script.js&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xhttp = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
xhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
        data = &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;parse&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);

        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; i &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; data) {


            $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#cars&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(
                &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;option value=&quot;&apos;&lt;/span&gt; + data[i].&lt;span class=&quot;hljs-property&quot;&gt;category&lt;/span&gt; + &lt;span class=&quot;hljs-string&quot;&gt;&apos;&quot;&amp;gt;&apos;&lt;/span&gt; + data[i].&lt;span class=&quot;hljs-property&quot;&gt;category&lt;/span&gt; + &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/option&amp;gt;&apos;&lt;/span&gt;

            )
        }
    }
};
xhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;http://127.0.0.1:8000/category.php&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
xhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;index.html&lt;/p&gt;
