          &lt;span class=&quot;hljs-string&quot;&gt;&quot;Defense&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Defense&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Striker&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
    Name&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;James&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Frank&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Jean&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
           &lt;span class=&quot;hljs-string&quot;&gt;&quot;Steve&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;John&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Tim&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

p1 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; ggplot&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;theTable&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; aes&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;x &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; Position&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; geom_bar&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
p2 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; ggplot&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;theTable&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; aes&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;x &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; fct_infreq&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;Position&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; geom_bar&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
p3 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; ggplot&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;theTable&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; aes&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;x &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; fct_rev&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;fct_infreq&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;Position&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; geom_bar&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

gridExtra&lt;span class=&quot;hljs-operator&quot;&gt;::&lt;/span&gt;grid.arrange&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;p1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; p2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; p3&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; nrow&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;             
