                String[][] data = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;String&lt;/span&gt;[names.length][&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;];
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; ii=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; ii&amp;lt;names.length; ii++) {
