                &lt;span class=&quot;hljs-type&quot;&gt;Dimension&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;tablePreferred&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; tableScroll.getPreferredSize();
                tableScroll.setPreferredSize(
                    &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Dimension&lt;/span&gt;(tablePreferred.width, tablePreferred.height/&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;) );

                &lt;span class=&quot;hljs-type&quot;&gt;JPanel&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;imagePanel&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JPanel&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;GridBagLayout&lt;/span&gt;());
                imagePanel.setBorder(
