                &lt;span class=&quot;hljs-type&quot;&gt;GradientPaint&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;gp&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;GradientPaint&lt;/span&gt;(
                    &lt;span class=&quot;hljs-number&quot;&gt;20f&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20f&lt;/span&gt;,Color.red, &lt;span class=&quot;hljs-number&quot;&gt;180f&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;180f&lt;/span&gt;,Color.yellow);
                g.setPaint(gp);
                g.fillRect(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;);
                &lt;span class=&quot;hljs-type&quot;&gt;ImageIcon&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;ii&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ImageIcon&lt;/span&gt;(bi);
                &lt;span class=&quot;hljs-type&quot;&gt;JLabel&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;imageLabel&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JLabel&lt;/span&gt;(ii);
                imagePanel.add( imageLabel, &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; );

                &lt;span class=&quot;hljs-type&quot;&gt;JSplitPane&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;splitPane&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;JSplitPane&lt;/span&gt;(
