                gui.add( splitPane, BorderLayout.CENTER );

                frame.setContentPane(gui);

                frame.pack();

                frame.setLocationRelativeTo(&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
