    &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addItemViewController&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;controller&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt;?, &lt;span class=&quot;hljs-params&quot;&gt;didFinishEnteringItem&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt;?) {
        &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Value from ViewControllerB&apos;s Delegate&quot;&lt;/span&gt;, item&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;)
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;@IBAction&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;goToViewControllerForDelegate&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;sender&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;Any&lt;/span&gt;) {

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; viewControllerB &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;UIStoryboard&lt;/span&gt;(name: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Main&quot;&lt;/span&gt;, bundle: &lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;).instantiateViewController(withIdentifier: &lt;span class=&quot;hljs-string&quot;&gt;&quot;ViewControllerB&quot;&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as?&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt; {
            viewControllerB.delegate &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; navigator &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; navigationController {
                navigator.pushViewController(viewControllerB, animated: &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)
            }
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerB&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-comment&quot;&gt;//Protocol decleare&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;protocol&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerBDelegate&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;NSObjectProtocol&lt;/span&gt; {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Classes that adopt this protocol MUST define&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// this method -- and hopefully do something in&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// that definition.&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addItemViewController&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;controller&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt;?, &lt;span class=&quot;hljs-params&quot;&gt;didFinishEnteringItem&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt;?)
}

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerB&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; delegate: &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerBDelegate&lt;/span&gt;?

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()
        &lt;span class=&quot;hljs-comment&quot;&gt;// MARK:  - - - -  Set Data for Passing Data through Delegate  - - - - - -&lt;/span&gt;
        delegate&lt;span class=&quot;hljs-operator&quot;&gt;?&lt;/span&gt;.addItemViewController(&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;, didFinishEnteringItem: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Data for ViewControllerA&quot;&lt;/span&gt;)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;hr&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;Passing Data through Notification Observer&lt;/strong&gt;: &lt;em&gt;From ViewControllerB to ViewControllerA&lt;/em&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;Step 1. Set and post data in the notification observer in ViewControllerB&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; objToBeSent = &lt;span class=&quot;hljs-string&quot;&gt;&quot;Test Message from Notification&quot;&lt;/span&gt;
        NotificationCenter.&lt;span class=&quot;hljs-literal&quot;&gt;default&lt;/span&gt;.post(name: Notification.Name(&lt;span class=&quot;hljs-string&quot;&gt;&quot;NotificationIdentifier&quot;&lt;/span&gt;), &lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt;: objToBeSent)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Step 2. Add Notification Observer in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;NotificationCenter.&lt;span class=&quot;hljs-keyword&quot;&gt;default&lt;/span&gt;.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;addObserver&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;selector&lt;/span&gt;: #&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;selector&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;methodOfReceivedNotification&lt;/span&gt;(&lt;span class=&quot;hljs-attr&quot;&gt;notification&lt;/span&gt;:)), &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: Notification.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;Name&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;NotificationIdentifier&quot;&lt;/span&gt;), &lt;span class=&quot;hljs-attr&quot;&gt;object&lt;/span&gt;: nil)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Step 3. Receive Notification data value in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@objc&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;methodOfReceivedNotification&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;notification&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;Notification&lt;/span&gt;) {
    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Value of notification: &quot;&lt;/span&gt;, notification.object &lt;span class=&quot;hljs-operator&quot;&gt;??&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;So here is the complete code for:&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerA&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerA&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt;{

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()

        &lt;span class=&quot;hljs-comment&quot;&gt;// Add observer in controller(s) where you want to receive data&lt;/span&gt;
        &lt;span class=&quot;hljs-type&quot;&gt;NotificationCenter&lt;/span&gt;.default.addObserver(&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;, selector: &lt;span class=&quot;hljs-keyword&quot;&gt;#selector&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.methodOfReceivedNotification(notification:)), name: &lt;span class=&quot;hljs-type&quot;&gt;Notification&lt;/span&gt;.&lt;span class=&quot;hljs-type&quot;&gt;Name&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;NotificationIdentifier&quot;&lt;/span&gt;), object: &lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;)
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;// MARK: Method for receiving Data through Post Notification&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;@objc&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;methodOfReceivedNotification&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;notification&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;Notification&lt;/span&gt;) {
        &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Value of notification: &quot;&lt;/span&gt;, notification.object &lt;span class=&quot;hljs-operator&quot;&gt;??&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerB&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;import UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ViewControllerB&lt;/span&gt;: &lt;span class=&quot;hljs-title&quot;&gt;UIViewController&lt;/span&gt; {

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; func &lt;span class=&quot;hljs-title&quot;&gt;viewDidLoad&lt;/span&gt;()&lt;/span&gt; {
        super.viewDidLoad()

        &lt;span class=&quot;hljs-comment&quot;&gt;// MARK:Set data for Passing Data through Post Notification&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; objToBeSent = &lt;span class=&quot;hljs-string&quot;&gt;&quot;Test Message from Notification&quot;&lt;/span&gt;
        NotificationCenter.&lt;span class=&quot;hljs-literal&quot;&gt;default&lt;/span&gt;.post(name: Notification.Name(&lt;span class=&quot;hljs-string&quot;&gt;&quot;NotificationIdentifier&quot;&lt;/span&gt;), &lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt;: objToBeSent)
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;hr&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;Passing Data through Block&lt;/strong&gt;: &lt;em&gt;From ViewControllerB to ViewControllerA&lt;/em&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;Step 1. Declare block in ViewControllerB&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-coffeescript&quot;&gt;var authorizationCompletionBlock:&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;(Bool)&lt;/span&gt;-&amp;gt;&lt;/span&gt;())? = {_ &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt;}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Step 2. Set data in block in ViewControllerB&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-go&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; authorizationCompletionBlock != &lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;
{
    authorizationCompletionBlock!(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Step 3. Receive block data in ViewControllerA&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-go&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Receiver Block&lt;/span&gt;
controller!.authorizationCompletionBlock = { isGranted in
    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Data received from Block is: &quot;&lt;/span&gt;, isGranted)
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;So &lt;em&gt;here&lt;/em&gt; is the complete code for:&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerA&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerA&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt;  {

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;// MARK:Method for receiving Data through Block&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;prepare&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;for&lt;/span&gt; &lt;span class=&quot;hljs-params&quot;&gt;segue&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;UIStoryboardSegue&lt;/span&gt;, &lt;span class=&quot;hljs-params&quot;&gt;sender&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;Any&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;?&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (segue.identifier &lt;span class=&quot;hljs-operator&quot;&gt;==&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;showDetailSegue&quot;&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; controller &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; segue.destination &lt;span class=&quot;hljs-keyword&quot;&gt;as?&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerB&lt;/span&gt;
            controller&lt;span class=&quot;hljs-operator&quot;&gt;?&lt;/span&gt;.isSomethingEnabled &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;

            &lt;span class=&quot;hljs-comment&quot;&gt;// Receiver Block&lt;/span&gt;
            controller&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;.authorizationCompletionBlock &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; { isGranted &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt;
                &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Data received from Block is: &quot;&lt;/span&gt;, isGranted)
            }
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;ViewControllerB&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerB&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt; {

    &lt;span class=&quot;hljs-comment&quot;&gt;// MARK: Variable for Passing Data through Block&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; authorizationCompletionBlock:((&lt;span class=&quot;hljs-type&quot;&gt;Bool&lt;/span&gt;)-&amp;gt;())&lt;span class=&quot;hljs-operator&quot;&gt;?&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; {&lt;span class=&quot;hljs-keyword&quot;&gt;_&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt;}

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()

        &lt;span class=&quot;hljs-comment&quot;&gt;// MARK: Set data for Passing Data through Block&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; authorizationCompletionBlock &lt;span class=&quot;hljs-operator&quot;&gt;!=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;
        {
            authorizationCompletionBlock&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;a href=&quot;https://github.com/RamdhanChoudhary/A-Simple-iOS-App-for-Passing-Data-Between-Controllers-in-Swift&quot; rel=&quot;nofollow noreferrer&quot;&gt;You can find complete sample Application at my GitHub&lt;/a&gt; Please let me know if you have any question(s) on this.&lt;/p&gt;

&lt;p&gt;I was searching this solution for long time, and at last I found it. First of all, declare all the objects in your SecondViewController.h file like&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-css&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@interface&lt;/span&gt; &lt;span class=&quot;hljs-attribute&quot;&gt;SecondViewController&lt;/span&gt;: UIviewController
{
    NSMutableArray *myAray;
    CustomObject *&lt;span class=&quot;hljs-selector-tag&quot;&gt;object&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now in your implementation file, allocate the memory for those objects like this:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-operator&quot;&gt;-&lt;/span&gt; (id)initWithNibName:(&lt;span class=&quot;hljs-type&quot;&gt;NSString&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;*&lt;/span&gt;)nibNameOrNil bundle:(&lt;span class=&quot;hljs-type&quot;&gt;NSBundle&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;*&lt;/span&gt;)nibBundleOrNil
{
     &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; [&lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt; initWithNibName:nibNameOrNil bundle:nibBundleOrNil];
     &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;)
     {
         &lt;span class=&quot;hljs-comment&quot;&gt;// Custom initialization&lt;/span&gt;
         myAray&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;[[&lt;span class=&quot;hljs-type&quot;&gt;NSMutableArray&lt;/span&gt; alloc] &lt;span class=&quot;hljs-keyword&quot;&gt;init&lt;/span&gt;];
         object&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;[[&lt;span class=&quot;hljs-type&quot;&gt;CustomObject&lt;/span&gt; alloc] &lt;span class=&quot;hljs-keyword&quot;&gt;init&lt;/span&gt;];
     }
     &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now you have allocated the memory for &lt;code&gt;Array&lt;/code&gt; and object. Now you can fill that memory before pushing this &lt;code&gt;ViewController&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;Go to your SecondViewController.h and write two methods:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scss&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;-&lt;/span&gt;(void)setMyArray:(NSArray *)_myArray;
&lt;span class=&quot;hljs-built_in&quot;&gt;-&lt;/span&gt;(void)setMyObject:(CustomObject *)_myObject;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;In the implementation file, you can implement the function:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scss&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;-&lt;/span&gt;(void)setMyArray:(NSArray *)_myArray
{
     &lt;span class=&quot;hljs-selector-attr&quot;&gt;[myArra addObjectsFromArray:_myArray]&lt;/span&gt;;
}

&lt;span class=&quot;hljs-built_in&quot;&gt;-&lt;/span&gt;(void)setMyObject:(CustomObject *)_myObject
{
     &lt;span class=&quot;hljs-selector-attr&quot;&gt;[object setCustomObject:_myObject]&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Expecting that your &lt;code&gt;CustomObject&lt;/code&gt; must have a setter function with it.&lt;/p&gt;
&lt;p&gt;Now your basic work is done. Go to the place where you want to push the &lt;code&gt;SecondViewController&lt;/code&gt; and do the following stuff:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-lua&quot;&gt;SecondViewController *secondView= &lt;span class=&quot;hljs-string&quot;&gt;[[SecondViewController alloc] initWithNibName:@&quot;SecondViewController &quot; bundle:[NSBundle MainBundle]]&lt;/span&gt; ;
[secondView setMyArray:ArrayToPass];
[secondView setMyObject:objectToPass];
[&lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;.navigationController pushViewController:secondView animated:YES ];
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Take care for spelling mistakes.&lt;/p&gt;

&lt;p&gt;This is not the way to do it. You should use delegates.&lt;/p&gt;
&lt;p&gt;I&apos;ll assume we have two view controllers, ViewController1 and ViewController2, and this check thing is in the first one and when its state changes, you want to do something in ViewController2. To achieve that in the proper way, you should do the below:&lt;/p&gt;
&lt;p&gt;Add a new file to your project (Objective-C Protocol) menu &lt;em&gt;File&lt;/em&gt;  &lt;em&gt;New&lt;/em&gt;. Now name it ViewController1Delegate or whatever you want and write these between the @interface and @end directives:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;@optional&lt;/span&gt;

- (void)&lt;span class=&quot;hljs-attribute&quot;&gt;checkStateDidChange&lt;/span&gt;:(BOOL)checked;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now go to ViewController2.h and add:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-cpp&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;#import &lt;span class=&quot;hljs-string&quot;&gt;&quot;ViewController1Delegate.h&quot;&lt;/span&gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Then change its definition to:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;@interface&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ViewController2&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;UIViewController&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-type&quot;&gt;ViewController1Delegate&lt;/span&gt;&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now go to ViewController2.m and inside the implementation add:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;- (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)checkStateDidChange:(&lt;span class=&quot;hljs-type&quot;&gt;BOOL&lt;/span&gt;)checked {
     &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (checked) {
           &lt;span class=&quot;hljs-comment&quot;&gt;// Do whatever you want here&lt;/span&gt;
           &lt;span class=&quot;hljs-built_in&quot;&gt;NSLog&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;@&quot;Checked&quot;&lt;/span&gt;);
     }
     &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
           &lt;span class=&quot;hljs-comment&quot;&gt;// Also do whatever you want here&lt;/span&gt;
           &lt;span class=&quot;hljs-built_in&quot;&gt;NSLog&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;@&quot;Not checked&quot;&lt;/span&gt;);
     }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now go to ViewController1.h and add the following property:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;weak&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;) &lt;span class=&quot;hljs-type&quot;&gt;id&lt;/span&gt;&amp;lt;ViewController1Delegate&amp;gt; delegate;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now if you are creating ViewController1 inside ViewController2 after some event, then you should do it this way using NIB files:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;ViewController1* controller = [[&lt;span class=&quot;hljs-built_in&quot;&gt;NSBundle&lt;/span&gt; mainBundle] loadNibNamed:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;ViewController1&quot;&lt;/span&gt; owner:&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt; options:&lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;];
controller.delegate = &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;;
[&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt; presentViewController:controller animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt; completion:&lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;];
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now you are all set. Whenever you detect the event of check changed in ViewController1, all you have to do is the below:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;[&lt;span class=&quot;hljs-meta&quot;&gt;delegate checkStateDidChange:checked&lt;/span&gt;]; &lt;span class=&quot;hljs-comment&quot;&gt;// You pass here YES or NO based on the check state of your control&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If you want to send data from one to another viewController, here&apos;s a way to do it:&lt;/p&gt;
&lt;p&gt;Say we have viewControllers: viewControllerA and viewControllerB&lt;/p&gt;
&lt;p&gt;Now in file &lt;em&gt;viewControllerB.h&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@interface&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;viewControllerB&lt;/span&gt; : &lt;span class=&quot;hljs-title&quot;&gt;UIViewController&lt;/span&gt; &lt;/span&gt;{

  &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *string;
  &lt;span class=&quot;hljs-built_in&quot;&gt;NSArray&lt;/span&gt; *array;

}

- (&lt;span class=&quot;hljs-type&quot;&gt;id&lt;/span&gt;)initWithArray:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSArray&lt;/span&gt;)a andString:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt;)s;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;In file &lt;em&gt;viewControllerB.m&lt;/em&gt;:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;#import &lt;span class=&quot;hljs-string&quot;&gt;&quot;viewControllerB.h&quot;&lt;/span&gt;&lt;/span&gt;

&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@implementation&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;viewControllerB&lt;/span&gt;&lt;/span&gt;

- (&lt;span class=&quot;hljs-type&quot;&gt;id&lt;/span&gt;)initWithArray:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSArray&lt;/span&gt;)a andString:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt;)s {

   array = [[&lt;span class=&quot;hljs-built_in&quot;&gt;NSArray&lt;/span&gt; alloc] init];
   array = a;

   string = [[&lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; alloc] init];
   string = s;

}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;In file &lt;em&gt;viewControllerA.m&lt;/em&gt;:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;#import &lt;span class=&quot;hljs-string&quot;&gt;&quot;viewControllerA.h&quot;&lt;/span&gt;&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;#import &lt;span class=&quot;hljs-string&quot;&gt;&quot;viewControllerB.h&quot;&lt;/span&gt;&lt;/span&gt;

&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@implementation&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;viewControllerA&lt;/span&gt;&lt;/span&gt;

- (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)someMethod {

  someArray = [&lt;span class=&quot;hljs-built_in&quot;&gt;NSArray&lt;/span&gt; arrayWithObjects:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;One&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;@&quot;Two&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;@&quot;Three&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;];
  someString = [&lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; stringWithFormat:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;Hahahahaha&quot;&lt;/span&gt;];

  viewControllerB *vc = [[viewControllerB alloc] initWithArray:someArray andString:someString];

  [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.navigationController pushViewController:vc animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt;];
  [vc release];
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;So this is how you can pass data from viewControllerA to viewControllerB without setting any delegate. ;)&lt;/p&gt;

&lt;p&gt;With a &lt;em&gt;Swift&lt;/em&gt; slant and want a bare-bones example, here is my go-to method for passing data if you are using a segue to get around.&lt;/p&gt;
&lt;p&gt;It is similar to the above but without the buttons, labels and such. Just simply passing data from one view to the next.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Setup The Storyboard&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;There are three parts.&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;The Sender&lt;/li&gt;
&lt;li&gt;The Segue&lt;/li&gt;
&lt;li&gt;The Receiver&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;This is a very simple view layout with a segue between them.&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/N6G2p.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/N6G2p.jpg&quot; alt=&quot;Very simple view layout. Note: No navigation controller&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;Here is the setup for the sender&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/dGH1N.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/dGH1N.jpg&quot; alt=&quot;The Sender&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;Here is the setup for the receiver.&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/nWIib.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/nWIib.jpg&quot; alt=&quot;The Receiver&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;Lastly, the setup for the segue.&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/cvYVj.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/cvYVj.jpg&quot; alt=&quot;The Segue Identifier&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;strong&gt;The View Controllers&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;We are keeping this simple so no buttons and not actions. We are simply moving data from the sender to the receiver when the application loads and then outputting the transmitted value to the console.&lt;/p&gt;
&lt;p&gt;This page takes the initially loaded value and passes it along.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit


&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerSender&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt; {

    &lt;span class=&quot;hljs-comment&quot;&gt;// THE STUFF - put some information into a variable&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; favoriteMovie &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Ghost Busters&quot;&lt;/span&gt;

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidAppear&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;animated&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;Bool&lt;/span&gt;) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// PASS IDENTIFIER - go to the receiving view controller.&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.performSegueWithIdentifier(&lt;span class=&quot;hljs-string&quot;&gt;&quot;goToReciever&quot;&lt;/span&gt;, sender: &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;)
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;prepareForSegue&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;segue&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;UIStoryboardSegue&lt;/span&gt;, &lt;span class=&quot;hljs-params&quot;&gt;sender&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;AnyObject&lt;/span&gt;?) {

        &lt;span class=&quot;hljs-comment&quot;&gt;// GET REFERENCE - ...to the receiver view.&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; viewControllerReceiver &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; segue.destinationViewController &lt;span class=&quot;hljs-keyword&quot;&gt;as?&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;ViewControllerReceiver&lt;/span&gt;

        &lt;span class=&quot;hljs-comment&quot;&gt;// PASS STUFF - pass the variable along to the target.&lt;/span&gt;
        viewControllerReceiver&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;.yourFavMovie &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.favoriteMovie

    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;This page just sends the value of the variable to the console when it loads. By this point, our favorite movie should be in that variable.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; UIKit

&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ViewControllerReceiver&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;UIViewController&lt;/span&gt; {

    &lt;span class=&quot;hljs-comment&quot;&gt;// Basic empty variable waiting for you to pass in your fantastic favorite movie.&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; yourFavMovie &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;

    &lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;() {
        &lt;span class=&quot;hljs-keyword&quot;&gt;super&lt;/span&gt;.viewDidLoad()

        &lt;span class=&quot;hljs-comment&quot;&gt;// And now we can view it in the console.&lt;/span&gt;
        println(&lt;span class=&quot;hljs-string&quot;&gt;&quot;The Movie is &lt;span class=&quot;hljs-subst&quot;&gt;\(&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.yourFavMovie)&lt;/span&gt;&quot;&lt;/span&gt;)

    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;That is how you can tackle it if you want to use a segue and you don&apos;t have your pages under a navigation controller.&lt;/p&gt;
&lt;p&gt;Once it is run, it should switch to the receiver view automatically and pass the value from the sender to the receiver, displaying the value in the console.&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/edubX.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/edubX.jpg&quot; alt=&quot;Ghost Busters is a classic folks.&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;In my case, I used a singleton class which could work as a global object allowing accesses to the data from almost everywhere in the app.&lt;/p&gt;
&lt;p&gt;The first thing is to build a singleton class. Please refer to the page &lt;em&gt;&lt;a href=&quot;https://stackoverflow.com/questions/145154/what-should-my-objective-c-singleton-look-like&quot;&gt;What should my Objective-C singleton look like?&lt;/a&gt;&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;And to make the object globally accessible, I simply imported it in &lt;code&gt;appName_Prefix.pch&lt;/code&gt; which was for applying import statement in every classes.&lt;/p&gt;
&lt;p&gt;To access this object and to use it, I simply implemented a class method to return the shared instance, which contains its own variables.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Passing data between FirstViewController to SecondViewController as below&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;For example:&lt;/p&gt;
&lt;p&gt;FirstViewController String value as&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-ini&quot;&gt;&lt;span class=&quot;hljs-attr&quot;&gt;StrFirstValue&lt;/span&gt; = @&lt;span class=&quot;hljs-string&quot;&gt;&quot;first&quot;&lt;/span&gt;&lt;span class=&quot;hljs-comment&quot;&gt;;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;So we can pass this value in the second class using the below steps:&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;&lt;p&gt;We need to create a string object in the &lt;em&gt;SecondViewController.h&lt;/em&gt; file&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *strValue;
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;Need to declare a property as the below declaration in the &lt;em&gt;.h&lt;/em&gt; file&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;strong&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;)  &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *strSecondValue;
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;Need synthesize that value in the &lt;em&gt;FirstViewController.m&lt;/em&gt; file below the header declaration&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@synthesize&lt;/span&gt; strValue;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;And in file &lt;em&gt;FirstViewController.h&lt;/em&gt;:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;strong&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;)  &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *strValue;
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;In FirstViewController, from which method we navigate to the second view, please write the below code in that method.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-lua&quot;&gt; SecondViewController *secondView= &lt;span class=&quot;hljs-string&quot;&gt;[[SecondViewController alloc]
 initWithNibName:@&quot;SecondViewController &quot; bundle:[NSBundle MainBundle]]&lt;/span&gt;;

 [secondView setStrSecondValue:StrFirstValue];

 [&lt;span class=&quot;hljs-built_in&quot;&gt;self&lt;/span&gt;.navigationController pushViewController:secondView animated:YES ];
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
&lt;/ol&gt;

&lt;p&gt;I am currently contributing to an open source solution to this problem through a project called MCViewFactory, which may be found here:&lt;/p&gt;
&lt;p&gt;&lt;em&gt;&lt;a href=&quot;https://github.com/YetiHQ/manticore-iosviewfactory&quot; rel=&quot;nofollow noreferrer&quot;&gt;Manticore iOS View Factory&lt;/a&gt;&lt;/em&gt;&lt;/p&gt;
&lt;p&gt;The idea is imitate Android&apos;s intent paradigm, using a global factory to manage which view you are looking at and using &quot;intents&quot; to switch and pass data between views. All the documentation is on the GitHub page, but here are some highlights:&lt;/p&gt;
&lt;p&gt;You setup all your views in .XIB files and register them in the app delegate, while initializing the factory.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-dart&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Register activities&lt;/span&gt;

MCViewFactory *&lt;span class=&quot;hljs-keyword&quot;&gt;factory&lt;/span&gt; = [MCViewFactory sharedFactory];

&lt;span class=&quot;hljs-comment&quot;&gt;// The following two lines are optional.&lt;/span&gt;
[&lt;span class=&quot;hljs-keyword&quot;&gt;factory&lt;/span&gt; registerView:@&lt;span class=&quot;hljs-string&quot;&gt;&quot;YourSectionViewController&quot;&lt;/span&gt;];
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Now, in your view controller (VC), anytime you want to move to a new VC and pass data, you create a new intent and add data to its dictionary (savedInstanceState). Then, just set the current intent of factory:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;MCIntent* intent = [MCIntent intentWithSectionName:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;YourSectionViewController&quot;&lt;/span&gt;];
[&lt;span class=&quot;hljs-meta&quot;&gt;intent setAnimationStyle:UIViewAnimationOptionTransitionFlipFromLeft&lt;/span&gt;];
[[intent savedInstanceState] setObject:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;someValue&quot;&lt;/span&gt; forKey:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;yourKey&quot;&lt;/span&gt;];
[[intent savedInstanceState] setObject:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;anotherValue&quot;&lt;/span&gt; forKey:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;anotherKey&quot;&lt;/span&gt;];
&lt;span class=&quot;hljs-comment&quot;&gt;// ...&lt;/span&gt;
[[MCViewModel sharedModel] setCurrentSection:intent];
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;All of your views that conform to this need to be subclasses of MCViewController, which allow you to override the new onResume: method, allowing you access to the data you&apos;ve passed in.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;-(&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)onResume:(MCIntent *)intent {
    &lt;span class=&quot;hljs-built_in&quot;&gt;NSObject&lt;/span&gt;* someValue = [intent.savedInstanceState objectForKey:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;yourKey&quot;&lt;/span&gt;];
    &lt;span class=&quot;hljs-built_in&quot;&gt;NSObject&lt;/span&gt;* anotherValue = [intent.savedInstanceState objectForKey:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;anotherKey&quot;&lt;/span&gt;];

    &lt;span class=&quot;hljs-comment&quot;&gt;// ...&lt;/span&gt;

    &lt;span class=&quot;hljs-comment&quot;&gt;// Ensure the following line is called, especially for MCSectionViewController&lt;/span&gt;
    [&lt;span class=&quot;hljs-variable language_&quot;&gt;super&lt;/span&gt; onResume:intent];
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Create the property in the next &lt;code&gt;view controller .h&lt;/code&gt; file and define getters and setters.&lt;/p&gt;
&lt;p&gt;Add this &lt;code&gt;property&lt;/code&gt; in NextVC.h on nextVC:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;strong&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;) &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *indexNumber;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Add&lt;/p&gt;
&lt;p&gt;&lt;code&gt;@synthesize  indexNumber;&lt;/code&gt;           in NextVC.m&lt;/p&gt;
&lt;p&gt;And last&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;NextVC *vc = [[NextVC alloc]&lt;span class=&quot;hljs-keyword&quot;&gt;init&lt;/span&gt;];

vc.indexNumber = &lt;span class=&quot;hljs-string&quot;&gt;@&quot;123&quot;&lt;/span&gt;;

[&lt;span class=&quot;hljs-meta&quot;&gt;self.navigationController vc animated:YES&lt;/span&gt;];
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;There are tons of ways to do this and it&apos;s important to pick the right one. Probably one of the biggest architectural decisions lies on how the model code will be shared or accessed throughout the app.&lt;/p&gt;
&lt;p&gt;I wrote a blog post about this a while back: &lt;a href=&quot;http://koreyhinton.com/blog/sharing-model-code.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;Sharing Model Code&lt;/a&gt;. Here&apos;s a brief summary:&lt;/p&gt;
&lt;h2&gt;Shared data&lt;/h2&gt;
&lt;p&gt;One approach is to share pointers to the model objects between view controllers.&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Brute force iteration on view controllers (in Navigation or Tab Bar Controller) to set the data&lt;/li&gt;
&lt;li&gt;Set data in prepareForSegue (if storyboards) or init (if programmatic)&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;Since prepare for segue is the most common here is an example:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-swift&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;override&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;func&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;prepareForSegue&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;segue&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;UIStoryboardSegue&lt;/span&gt;, &lt;span class=&quot;hljs-params&quot;&gt;sender&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;AnyObject&lt;/span&gt;?) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; next &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; segue.destinationViewController &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;NextViewController&lt;/span&gt;
    next.dataSource &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; dataSource
}
&lt;/code&gt;&lt;/pre&gt;
&lt;h2&gt;Independent access&lt;/h2&gt;
&lt;p&gt;Another approach is to handle a screen full of data at a time and instead of coupling the view controllers to each other couple each view controller to single data source that they can get to independently.&lt;/p&gt;
&lt;p&gt;The most common way I&apos;ve seen this done is a &lt;a href=&quot;http://koreyhinton.com/blog/singleton-pattern-in-swift.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;singleton&lt;/a&gt; instance. So if your singleton object was &lt;code&gt;DataAccess&lt;/code&gt; you could do the following in the viewDidLoad method of UIViewController:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;func &lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;super&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;viewDidLoad&lt;/span&gt;()
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; data = dataAccess.&lt;span class=&quot;hljs-title function_&quot;&gt;requestData&lt;/span&gt;()
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;There are addition tools that also help pass along data:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Key-Value Observing&lt;/li&gt;
&lt;li&gt;NSNotification&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://developer.apple.com/documentation/coredata&quot; rel=&quot;nofollow noreferrer&quot;&gt;Core Data&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;NSFetchedResultsController&lt;/li&gt;
&lt;li&gt;Data Source&lt;/li&gt;
&lt;/ul&gt;
&lt;h2&gt;Core Data&lt;/h2&gt;
&lt;p&gt;The nice thing about Core Data is that it has inverse relationships. So if you want to just give a NotesViewController the notes object you can because it&apos;ll have an inverse relationship to something else like the notebook. If you need data on the notebook in the NotesViewController you can walk back up the object graph by doing the following:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-bash&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;let&lt;/span&gt; notebookName = note.notebook.name
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Read more about this in my blog post: &lt;a href=&quot;http://koreyhinton.com/blog/sharing-model-code.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;Sharing Model Code&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;If you want to pass data from ViewControlerOne to ViewControllerTwo, try these...&lt;/p&gt;
&lt;p&gt;Do these in ViewControlerOne.h:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;strong&lt;/span&gt;) &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *str1;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Do these in ViewControllerTwo.h:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;@property&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;nonatomic&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;strong&lt;/span&gt;) &lt;span class=&quot;hljs-built_in&quot;&gt;NSString&lt;/span&gt; *str2;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Synthesize str2 in ViewControllerTwo.m:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;@interface&lt;/span&gt; ViewControllerTwo ()
&lt;span class=&quot;hljs-variable&quot;&gt;@end&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;@implementation&lt;/span&gt; ViewControllerTwo
&lt;span class=&quot;hljs-variable&quot;&gt;@synthesize&lt;/span&gt; str2;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Do these in ViewControlerOne.m:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt; - (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)viewDidLoad
 {
   [&lt;span class=&quot;hljs-variable language_&quot;&gt;super&lt;/span&gt; viewDidLoad];

   &lt;span class=&quot;hljs-comment&quot;&gt;// Data or string you wants to pass in ViewControllerTwo...&lt;/span&gt;
   &lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.str1 = &lt;span class=&quot;hljs-string&quot;&gt;@&quot;hello world&quot;&lt;/span&gt;;
 }
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;O the buttons click event, do this:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;-(&lt;span class=&quot;hljs-keyword&quot;&gt;IBAction&lt;/span&gt;)ButtonClicked
{
  &lt;span class=&quot;hljs-comment&quot;&gt;// Navigation on buttons click event from ViewControlerOne to ViewControlerTwo with transferring data or string..&lt;/span&gt;
  ViewControllerTwo *objViewTwo = [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.storyboard instantiateViewControllerWithIdentifier:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;ViewControllerTwo&quot;&lt;/span&gt;];
  obj.str2 = str1;
  [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.navigationController pushViewController: objViewTwo animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt;];
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Do these in ViewControllerTwo.m:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;- (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)viewDidLoad
{
  [&lt;span class=&quot;hljs-variable language_&quot;&gt;super&lt;/span&gt; viewDidLoad];
  &lt;span class=&quot;hljs-built_in&quot;&gt;NSLog&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;@&quot;%@&quot;&lt;/span&gt;, str2);
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can save data in an App delegate to access it across view controllers in your application. All you have to do is create a shared instance of an app delegate:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;AppDelegate *appDelegate = (AppDelegate *)[UIApplication sharedApplication].&lt;span class=&quot;hljs-built_in&quot;&gt;delegate&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;For Example&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;If you declare a &lt;code&gt;NSArray object *arrayXYZ&lt;/code&gt;, then you can access it in any view controller by &lt;code&gt;appDelegate.arrayXYZ&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;NewsViewController&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;- (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)tableView:(&lt;span class=&quot;hljs-built_in&quot;&gt;UITableView&lt;/span&gt; *)tableView didSelectRowAtIndexPath:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSIndexPath&lt;/span&gt; *)indexPath
{
  [tbl_View deselectRowAtIndexPath:indexPath animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt;];
  News *newsObj = [newstitleArr objectAtIndex:indexPath.row];
  NewsDetailViewController *newsDetailView = [[NewsDetailViewController alloc] initWithNibName:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;NewsDetailViewController&quot;&lt;/span&gt; bundle:&lt;span class=&quot;hljs-literal&quot;&gt;nil&lt;/span&gt;];

  newsDetailView.newsHeadlineStr = newsObj.newsHeadline;

  [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.navigationController pushViewController:newsDetailView animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt;];
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;NewsDetailViewController.h&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;@interface&lt;/span&gt; &lt;span class=&quot;hljs-attribute&quot;&gt;NewsDetailViewController &lt;/span&gt;: UIViewController
&lt;span class=&quot;hljs-variable&quot;&gt;@property&lt;/span&gt;(nonatomic,retain) NSString *newsHeadlineStr;
&lt;span class=&quot;hljs-variable&quot;&gt;@end&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;NewsDetailViewController.m&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;@synthesize&lt;/span&gt; newsHeadlineStr;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I have seen a lot of people over complicating this using the &lt;code&gt;didSelectRowAtPath&lt;/code&gt; method. I am using &lt;a href=&quot;https://developer.apple.com/documentation/coredata&quot; rel=&quot;nofollow noreferrer&quot;&gt;Core Data&lt;/a&gt; in my example.&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-objectivec&quot;&gt;- (&lt;span class=&quot;hljs-type&quot;&gt;void&lt;/span&gt;)tableView:(&lt;span class=&quot;hljs-built_in&quot;&gt;UITableView&lt;/span&gt; *)tableView didSelectRowAtIndexPath:(&lt;span class=&quot;hljs-built_in&quot;&gt;NSIndexPath&lt;/span&gt; *)indexPath{

    &lt;span class=&quot;hljs-comment&quot;&gt;// This solution is for using Core Data&lt;/span&gt;
    YourCDEntityName * value = (YourCDEntityName *)[[&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt; fetchedResultsController] objectAtIndexPath: indexPath];

    YourSecondViewController * details = [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.storyboard instantiateViewControllerWithIdentifier:&lt;span class=&quot;hljs-string&quot;&gt;@&quot;nameOfYourSecondVC&quot;&lt;/span&gt;]; &lt;span class=&quot;hljs-comment&quot;&gt;// Make sure in storyboards you give your second VC an identifier&lt;/span&gt;

    &lt;span class=&quot;hljs-comment&quot;&gt;// Make sure you declare your value in the second view controller&lt;/span&gt;
    details.selectedValue = value;

    &lt;span class=&quot;hljs-comment&quot;&gt;// Now that you have said to pass value all you need to do is change views&lt;/span&gt;
    [&lt;span class=&quot;hljs-keyword&quot;&gt;self&lt;/span&gt;.navigationController pushViewController: details animated:&lt;span class=&quot;hljs-literal&quot;&gt;YES&lt;/span&gt;];

}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Four lines of code inside the method and you are done.&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;There are 3 types for passing data one ViewController to another
  ViewController.&lt;/p&gt;
&lt;/blockquote&gt;

&lt;ol&gt;
&lt;li&gt;Programatically&lt;/li&gt;
&lt;li&gt;Segue&lt;/li&gt;
&lt;li&gt;UserDefaults&lt;/li&gt;
&lt;/ol&gt;

&lt;p&gt;&lt;strong&gt;Demo Project Link Here&lt;/strong&gt; - &lt;a href=&quot;https://github.com/kamanijasmin13/Swift-Pass-data-between-viewcontrollers&quot; rel=&quot;noreferrer&quot;&gt;https://github.com/kamanijasmin13/Swift-Pass-data-between-viewcontrollers&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Programatically&lt;/strong&gt;
&lt;a href=&quot;https://i.stack.imgur.com/2gFjo.jpg&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/2gFjo.jpg&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Segue&lt;/strong&gt;
&lt;a href=&quot;https://i.stack.imgur.com/3jweJ.jpg&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/3jweJ.jpg&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;UserDefaults&lt;/strong&gt;
&lt;a href=&quot;https://i.stack.imgur.com/pBk3v.jpg&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/pBk3v.jpg&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Demo Project Link Here&lt;/strong&gt; - &lt;a href=&quot;https://github.com/kamanijasmin13/Swift-Pass-data-between-viewcontrollers&quot; rel=&quot;noreferrer&quot;&gt;https://github.com/kamanijasmin13/Swift-Pass-data-between-viewcontrollers&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;For SwiftUI&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;Think of &lt;code&gt;@EnvironmentObject&lt;/code&gt; as a smarter, simpler way of using &lt;code&gt;@ObservedObject&lt;/code&gt; on lots of views. Rather than creating some data in view A, then passing it to view B, then view C, then view D before finally using it, you can create it in view and put it into the environment so that views B, C, and D will automatically have access to it.&lt;/p&gt;

&lt;p&gt;Note: Environment objects must be supplied by an ancestor view  if &lt;strong&gt;SwiftUI&lt;/strong&gt; cant find an environment object of the correct type youll get a crash. This applies for previews too, so be careful.&lt;/p&gt;

&lt;p&gt;As an example, heres an observable object that stores user settings:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-kotlin&quot;&gt;&lt;span class=&quot;hljs-class&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;UserSettings&lt;/span&gt;: &lt;span class=&quot;hljs-type&quot;&gt;ObservableObject {&lt;/span&gt;&lt;/span&gt;
     &lt;span class=&quot;hljs-meta&quot;&gt;@Published&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; score = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
