      [&lt;span class=&quot;hljs-string&quot;&gt;&apos;key&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;row&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;item&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;col&apos;&lt;/span&gt;]
