&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;  c  &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;  &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;
