&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; url = &lt;span class=&quot;hljs-string&quot;&gt;&quot;http://www.test.com/folder/mypage.html?myparam1=1&amp;amp;myparam2=2#something&quot;&lt;/span&gt;;

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;URLParser&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;u&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; path=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;,query=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;,hash=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;,params;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt;) &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;){
        hash = u.&lt;span class=&quot;hljs-title function_&quot;&gt;substr&lt;/span&gt;(u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt;) + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);
        u = u.&lt;span class=&quot;hljs-title function_&quot;&gt;substr&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; , u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt;));
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt;) &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;){
        path = u.&lt;span class=&quot;hljs-title function_&quot;&gt;substr&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; , u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt;));
        query = u.&lt;span class=&quot;hljs-title function_&quot;&gt;substr&lt;/span&gt;(u.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt;) + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);
        params= query.&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;amp;&apos;&lt;/span&gt;);
    }&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
        path = u;
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; {
        &lt;span class=&quot;hljs-attr&quot;&gt;getHost&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; hostexp = &lt;span class=&quot;hljs-regexp&quot;&gt;/\/\/([\w.-]*)/&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; match = hostexp.&lt;span class=&quot;hljs-title function_&quot;&gt;exec&lt;/span&gt;(path);
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (match != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; &amp;amp;&amp;amp; match.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; match[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;];
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;getPath&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; pathexp = &lt;span class=&quot;hljs-regexp&quot;&gt;/\/\/[\w.-]*(?:\/([^?]*))/&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; match = pathexp.&lt;span class=&quot;hljs-title function_&quot;&gt;exec&lt;/span&gt;(path);
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (match != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; &amp;amp;&amp;amp; match.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; match[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;];
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;getHash&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; hash;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;getParams&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; params
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;getQuery&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; query;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;setHash&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;value&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = &lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt; + query;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(value.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = query + &lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt; + value;
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; path + query;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;setParam&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;name, value&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!params){
                params= &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;();
            }
            params.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(name + &lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt; + value);
            &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; params.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                    query += &lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;amp;&quot;&lt;/span&gt;;
                query += params[i];
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = &lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt; + query;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(hash.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = query + &lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt; + hash;
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; path + query;
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;getParam&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;name&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(params){
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; params.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; pair = params[i].&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt;);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;decodeURIComponent&lt;/span&gt;(pair[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) == name)
                        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;decodeURIComponent&lt;/span&gt;(pair[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]);
                }
            }
            &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Query variable %s not found&apos;&lt;/span&gt;, name);
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;hasParam&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;name&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(params){
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; params.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; pair = params[i].&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt;);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;decodeURIComponent&lt;/span&gt;(pair[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) == name)
                        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
                }
            }
            &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Query variable %s not found&apos;&lt;/span&gt;, name);
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;removeParam&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;name&lt;/span&gt;){
            query = &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(params){
                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; newparams = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;();
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;i &amp;lt; params.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;;i++) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; pair = params[i].&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt;);
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;decodeURIComponent&lt;/span&gt;(pair[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) != name)
                          newparams .&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(params[i]);
                }
                params = newparams;
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; params.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                        query += &lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;amp;&quot;&lt;/span&gt;;
                    query += params[i];
                }
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = &lt;span class=&quot;hljs-string&quot;&gt;&quot;?&quot;&lt;/span&gt; + query;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(hash.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
                query = query + &lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt; + hash;
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; path + query;
        },
    }
}


