&lt;span class=&quot;hljs-meta&quot;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;id&lt;/span&gt;(a[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]), &lt;span class=&quot;hljs-built_in&quot;&gt;id&lt;/span&gt;(b[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]), &lt;span class=&quot;hljs-built_in&quot;&gt;id&lt;/span&gt;(c[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;])
(&lt;span class=&quot;hljs-number&quot;&gt;4422785208&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4422785208&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4422785208&lt;/span&gt;) &lt;span class=&quot;hljs-comment&quot;&gt;# all referring to same &apos;h&apos;&lt;/span&gt;
     |           |           |
      -----------------------

&lt;span class=&quot;hljs-meta&quot;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] += &lt;span class=&quot;hljs-string&quot;&gt;&apos;o&apos;&lt;/span&gt;
&lt;span class=&quot;hljs-meta&quot;&gt;&amp;gt;&amp;gt;&amp;gt; &lt;/span&gt;a,b,c
([&lt;span class=&quot;hljs-string&quot;&gt;&apos;hello&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;word&apos;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&apos;hello&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;word&apos;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&apos;hell&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;word&apos;&lt;/span&gt;])  &lt;span class=&quot;hljs-comment&quot;&gt;# b changed too&lt;/span&gt;
