&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Binds `wordButtons` to an (array-like) HTMLCollection of buttons&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; wordButtons = &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementsByClassName&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;word&quot;&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Applies the `slantWord` function to the first word button&lt;/span&gt;
&lt;span class=&quot;hljs-title function_&quot;&gt;slantWord&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);

&lt;span class=&quot;hljs-comment&quot;&gt;// Defines the `slantWord` function&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;slantWord&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;wordNumber&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; index = wordNumber - &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;; &lt;span class=&quot;hljs-comment&quot;&gt;// Collection index is zero-based&lt;/span&gt;
  wordButtons[index].&lt;span class=&quot;hljs-property&quot;&gt;style&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;transform&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;rotate(7deg)&quot;&lt;/span&gt;; &lt;span class=&quot;hljs-comment&quot;&gt;// Transforms the specified button&lt;/span&gt;
}&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;snippet-code-html lang-html s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;wordGameContainer&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;
