                &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; s+&lt;span class=&quot;hljs-string&quot;&gt;&apos;[   :   ]    &apos;&lt;/span&gt; + &lt;span class=&quot;hljs-string&quot;&gt;&apos; = &apos;&lt;/span&gt;, s[triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]:triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]]
            &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] == &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] != &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;:
                &lt;span class=&quot;hljs-comment&quot;&gt;# 01&lt;/span&gt;
                &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; s+&lt;span class=&quot;hljs-string&quot;&gt;&apos;[   :{0:2d} ]    &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-built_in&quot;&gt;format&lt;/span&gt;(triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]) + &lt;span class=&quot;hljs-string&quot;&gt;&apos; = &apos;&lt;/span&gt;, s[triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]:triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]]
            &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] != &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] == &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;:
                &lt;span class=&quot;hljs-comment&quot;&gt;# 10&lt;/span&gt;
                &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; s+&lt;span class=&quot;hljs-string&quot;&gt;&apos;[{0:2d} :   ]    &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-built_in&quot;&gt;format&lt;/span&gt;(triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) + &lt;span class=&quot;hljs-string&quot;&gt;&apos; = &apos;&lt;/span&gt;, s[triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]:triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]]
            &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] != &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] != &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;:
                &lt;span class=&quot;hljs-comment&quot;&gt;# 11&lt;/span&gt;
                &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; s+&lt;span class=&quot;hljs-string&quot;&gt;&apos;[{0:2d} :{1:2d} ]    &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-built_in&quot;&gt;format&lt;/span&gt;(triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;],triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]) + &lt;span class=&quot;hljs-string&quot;&gt;&apos; = &apos;&lt;/span&gt;, s[triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]:triada[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]]

        &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;len&lt;/span&gt;(triada) == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;:
            &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; s+&lt;span class=&quot;hljs-string&quot;&gt;&apos;[{0:2d} ]        &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-built_in&quot;&gt;format&lt;/span&gt;(triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]) + &lt;span class=&quot;hljs-string&quot;&gt;&apos; = &apos;&lt;/span&gt;, s[triada[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]]


&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; __name__ == &lt;span class=&quot;hljs-string&quot;&gt;&apos;__main__&apos;&lt;/span&gt;:
    &lt;span class=&quot;hljs-comment&quot;&gt;# Change &quot;s&quot; to what ever string you like, make it 9 characters for&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;# better representation.&lt;/span&gt;
    s = &lt;span class=&quot;hljs-string&quot;&gt;&apos;COMPUTERS&apos;&lt;/span&gt;

    &lt;span class=&quot;hljs-comment&quot;&gt;# add to this list different lists to experement with indexes&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;# to represent ex. s[::], use s[None, None,None], otherwise you get an error&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;# for s[2:] use s[2:None]&lt;/span&gt;

    lista = [[&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;],[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],[&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;], [&lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],[-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,-&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]]

    slicegraphical(s, lista)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can run this script and experiment with it, below is some samples that I got from the script.&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;  +---+---+---+---+---+---+---+---+---+
  | C | O | M | P | U | T | E | R | S |
  +---+---+---+---+---+---+---+---+---+
  &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;   
 -&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; 

COMPUTERS[ &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt; ]     =  UTE
COMPUTERS[ &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; ] =  MU
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  UPM
COMPUTERS[ &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; ]         =  U
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  TU
COMPUTERS[ &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  MPUT
COMPUTERS[ &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  
COMPUTERS[   :   :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  SRETUPMOC
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; :   ]     =  UTERS
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  UPMO
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; :   :-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ] =  UPMOC
COMPUTERS[-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; :-&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; ] =  SEUM
[Finished &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0.9&lt;/span&gt;s]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;When using a negative step, notice that the answer is shifted to the right by 1.&lt;/p&gt;

&lt;p&gt;My brain seems happy to accept that &lt;code&gt;lst[start:end]&lt;/code&gt; contains the &lt;code&gt;start&lt;/code&gt;-th item. I might even say that it is a &apos;natural assumption&apos;.&lt;/p&gt;

&lt;p&gt;But occasionally a doubt creeps in and my brain asks for reassurance that it does not contain the &lt;code&gt;end&lt;/code&gt;-th element.&lt;/p&gt;

&lt;p&gt;In these moments I rely on this simple theorem:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;any&lt;/span&gt; n,    lst = lst[:n] + lst[n:]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This pretty property tells me that &lt;code&gt;lst[start:end]&lt;/code&gt; does not contain the &lt;code&gt;end&lt;/code&gt;-th item because it is in &lt;code&gt;lst[end:]&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;Note that this theorem is true for any &lt;code&gt;n&lt;/code&gt; at all. For example, you can check that&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;lst = &lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)
lst[:-&lt;span class=&quot;hljs-number&quot;&gt;42&lt;/span&gt;] + lst[-&lt;span class=&quot;hljs-number&quot;&gt;42&lt;/span&gt;:] == lst
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;returns &lt;code&gt;True&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;In Python, the most basic form for slicing is the following:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;l[start:end]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;where &lt;code&gt;l&lt;/code&gt; is some collection, &lt;code&gt;start&lt;/code&gt; is an inclusive index, and &lt;code&gt;end&lt;/code&gt; is an exclusive index.&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]: l = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;))

In [&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]: l[:&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# First five elements&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;]: l[-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;:] &lt;span class=&quot;hljs-comment&quot;&gt;# Last five elements&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;When slicing from the start, you can omit the zero index, and when slicing to the end, you can omit the final index since it is redundant, so do not be verbose:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;]: l[:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;] == l[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;]
Out[&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;]: &lt;span class=&quot;hljs-literal&quot;&gt;True&lt;/span&gt;

In [&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;]: l[&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;:] == l[&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;:&lt;span class=&quot;hljs-built_in&quot;&gt;len&lt;/span&gt;(l)]
Out[&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;]: &lt;span class=&quot;hljs-literal&quot;&gt;True&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Negative integers are useful when doing offsets relative to the end of a collection:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;]: l[:-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# Include all elements but the last one&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;]: l[-&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;:] &lt;span class=&quot;hljs-comment&quot;&gt;# Take the last three elements&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;It is possible to provide indices that are out of bounds when slicing such as:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]: l[:&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# 20 is out of index bounds, and l[20] will raise an IndexError exception&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;]: l[-&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;:] &lt;span class=&quot;hljs-comment&quot;&gt;# -20 is out of index bounds, and l[-20] will raise an IndexError exception&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Keep in mind that the result of slicing a collection is a whole new collection. In addition, when using slice notation in assignments, the length of the slice assignments do not need to be the same. The values before and after the assigned slice will be kept, and the collection will shrink or grow to contain the new values:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;16&lt;/span&gt;]: l[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;] = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;abc&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-comment&quot;&gt;# Assigning fewer elements than the ones contained in the sliced collection l[2:6]&lt;/span&gt;

In [&lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;]: l
Out[&lt;span class=&quot;hljs-number&quot;&gt;17&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;a&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;b&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;c&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;18&lt;/span&gt;]: l[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;] = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;hello&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-comment&quot;&gt;# Assigning more elements than the ones contained in the sliced collection l [2:5]&lt;/span&gt;

In [&lt;span class=&quot;hljs-number&quot;&gt;19&lt;/span&gt;]: l
Out[&lt;span class=&quot;hljs-number&quot;&gt;19&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;h&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;e&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;l&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;l&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;o&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If you omit the start and end index, you will make a copy of the collection:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;14&lt;/span&gt;]: l_copy = l[:]

In [&lt;span class=&quot;hljs-number&quot;&gt;15&lt;/span&gt;]: l == l_copy &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; l &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;not&lt;/span&gt; l_copy
Out[&lt;span class=&quot;hljs-number&quot;&gt;15&lt;/span&gt;]: &lt;span class=&quot;hljs-literal&quot;&gt;True&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If the start and end indexes are omitted when performing an assignment operation, the entire content of the collection will be replaced with a copy of what is referenced:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;]: l[:] = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;hello...&apos;&lt;/span&gt;)

In [&lt;span class=&quot;hljs-number&quot;&gt;21&lt;/span&gt;]: l
Out[&lt;span class=&quot;hljs-number&quot;&gt;21&lt;/span&gt;]: [&lt;span class=&quot;hljs-string&quot;&gt;&apos;h&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;e&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;l&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;l&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;o&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Besides basic slicing, it is also possible to apply the following notation:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;l[start:end:step]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;where &lt;code&gt;l&lt;/code&gt; is a collection, &lt;code&gt;start&lt;/code&gt; is an inclusive index, &lt;code&gt;end&lt;/code&gt; is an exclusive index, and &lt;code&gt;step&lt;/code&gt; is a stride that can be used to take every &lt;em&gt;nth&lt;/em&gt; item in &lt;code&gt;l&lt;/code&gt;.&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;22&lt;/span&gt;]: l = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;))

In [&lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;]: l[::&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# Take the elements which indexes are even&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;23&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;]: l[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;::&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# Take the elements which indexes are odd&lt;/span&gt;
Out[&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Using &lt;code&gt;step&lt;/code&gt; provides a useful trick to reverse a collection in Python:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;25&lt;/span&gt;]: l[::-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]
Out[&lt;span class=&quot;hljs-number&quot;&gt;25&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;It is also possible to use negative integers for &lt;code&gt;step&lt;/code&gt; as the following example:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In[&lt;span class=&quot;hljs-number&quot;&gt;28&lt;/span&gt;]:  l[::-&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]
Out[&lt;span class=&quot;hljs-number&quot;&gt;28&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;However, using a negative value for &lt;code&gt;step&lt;/code&gt; could become very confusing. Moreover, in order to be &lt;a href=&quot;https://en.wiktionary.org/wiki/Pythonic#Adjective&quot; rel=&quot;nofollow noreferrer&quot;&gt;Pythonic&lt;/a&gt;, you should avoid using &lt;code&gt;start&lt;/code&gt;, &lt;code&gt;end&lt;/code&gt;, and &lt;code&gt;step&lt;/code&gt; in a single slice. In case this is required, consider doing this in two assignments (one to slice, and the other to stride).&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;In [&lt;span class=&quot;hljs-number&quot;&gt;29&lt;/span&gt;]: l = l[::&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# This step is for striding&lt;/span&gt;

In [&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;]: l
Out[&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;]

In [&lt;span class=&quot;hljs-number&quot;&gt;31&lt;/span&gt;]: l = l[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;:-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] &lt;span class=&quot;hljs-comment&quot;&gt;# This step is for slicing&lt;/span&gt;

In [&lt;span class=&quot;hljs-number&quot;&gt;32&lt;/span&gt;]: l
Out[&lt;span class=&quot;hljs-number&quot;&gt;32&lt;/span&gt;]: [&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I want to add one &lt;em&gt;Hello, World!&lt;/em&gt; example that explains the basics of slices for the very beginners. It helped me a lot.&lt;/p&gt;

&lt;p&gt;Let&apos;s have a list with six values &lt;code&gt;[&apos;P&apos;, &apos;Y&apos;, &apos;T&apos;, &apos;H&apos;, &apos;O&apos;, &apos;N&apos;]&lt;/code&gt;:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;+---+---+---+---+---+---+
| P | Y | T | H | O | N |
+---+---+---+---+---+---+
  &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Now the simplest slices of that list are its sublists. The notation is &lt;code&gt;[&amp;lt;index&amp;gt;:&amp;lt;index&amp;gt;]&lt;/code&gt; and the key is to read it like this:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;[ start cutting before this index : end cutting before this index ]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Now if you make a slice &lt;code&gt;[2:5]&lt;/code&gt; of the list above, this will happen:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;        |           |
+---+---|---+---+---|---+
| P | Y | T | H | O | N |
+---+---|---+---+---|---+
  &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; | &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; | &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You made a cut &lt;strong&gt;before&lt;/strong&gt; the element with index &lt;code&gt;2&lt;/code&gt; and another cut &lt;strong&gt;before&lt;/strong&gt; the element with index &lt;code&gt;5&lt;/code&gt;. So the result will be a slice between those two cuts, a list &lt;code&gt;[&apos;T&apos;, &apos;H&apos;, &apos;O&apos;]&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;Most of the previous answers clears up questions about slice notation.&lt;/p&gt;

&lt;p&gt;The extended indexing syntax used for slicing is &lt;code&gt;aList[start:stop:step]&lt;/code&gt;, and basic examples are:&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/IVkET.jpg&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/IVkET.jpg&quot; alt=&quot;Enter image description here&quot;&gt;&lt;/a&gt;:&lt;/p&gt;

&lt;p&gt;More slicing examples: &lt;a href=&quot;https://docs.python.org/2.3/whatsnew/section-slices.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;15 Extended Slices&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;The below is the example of an index of a string:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt; +---+---+---+---+---+
 | H | e | l | p | A |
 +---+---+---+---+---+
 &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;   &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;
-&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;  -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;

&lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;Name string&quot;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Slicing example: [start:end:step]&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[start:end] &lt;span class=&quot;hljs-comment&quot;&gt;# Items start through end-1&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[start:]    &lt;span class=&quot;hljs-comment&quot;&gt;# Items start through the rest of the array&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[:end]      &lt;span class=&quot;hljs-comment&quot;&gt;# Items from the beginning through end-1&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[:]         &lt;span class=&quot;hljs-comment&quot;&gt;# A copy of the whole array&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Below is the example usage:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] = N
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] = Na
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;] = Name st
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] = Nm t
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;str&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;:-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] = Nm ti
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If you feel negative indices in slicing is confusing, here&apos;s a very easy way to think about it: just replace the negative index with &lt;code&gt;len - index&lt;/code&gt;. So for example, replace -3 with &lt;code&gt;len(list) - 3&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;The best way to illustrate what slicing does internally is just show it in code that implements this operation:&lt;/p&gt;

&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;slice&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;, start = &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;, end = &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;, step = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;&lt;/span&gt;):
  &lt;span class=&quot;hljs-comment&quot;&gt;# Take care of missing start/end parameters&lt;/span&gt;
  start = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; start &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; start
  end = &lt;span class=&quot;hljs-built_in&quot;&gt;len&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; end &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; end

  &lt;span class=&quot;hljs-comment&quot;&gt;# Take care of negative start/end parameters&lt;/span&gt;
  start = &lt;span class=&quot;hljs-built_in&quot;&gt;len&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;) + start &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; start &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; start
  end = &lt;span class=&quot;hljs-built_in&quot;&gt;len&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;) + end &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; end &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; end

  &lt;span class=&quot;hljs-comment&quot;&gt;# Now just execute a for-loop with start, end and step&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; [&lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;[i] &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; i &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(start, end, step)]
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The basic slicing technique is to define the starting point, the stopping point, and the step sizealso known as stride.&lt;/p&gt;
&lt;p&gt;First, we will create a list of values to use in our slicing.&lt;/p&gt;
&lt;p&gt;Create two lists to slice. The first is a numeric list from 1 to 9 (List A). The second is also a numeric list, from 0 to 9 (List B):&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;A = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)) &lt;span class=&quot;hljs-comment&quot;&gt;# Start, stop, and step&lt;/span&gt;
B = &lt;span class=&quot;hljs-built_in&quot;&gt;list&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))

&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;This is List A:&quot;&lt;/span&gt;, A)
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;This is List B:&quot;&lt;/span&gt;, B)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Index the number 3 from A and the number 6 from B.&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(A[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;])
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(B[&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;])
&lt;/code&gt;&lt;/pre&gt;
&lt;h3&gt;Basic Slicing&lt;/h3&gt;
&lt;p&gt;Extended indexing syntax used for slicing is &lt;code&gt;aList[start:stop:step]&lt;/code&gt;. The start argument and the step argument both default to &lt;code&gt;None&lt;/code&gt;the only required argument is stop. Did you notice this is similar to how range was used to define lists A and B? This is because the slice object represents the set of indices specified by &lt;code&gt;range(start, stop, step)&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;As you can see, defining only stop returns one element. Since the start defaults to none, this translates into retrieving only one element.&lt;/p&gt;
&lt;p&gt;It is important to note, the first element is index 0, &lt;em&gt;not&lt;/em&gt; index 1. This is why we are using 2 lists for this exercise. List A&apos;s elements are numbered according to the ordinal position (the first element is 1, the second element is 2, etc.) while List B&apos;s elements are the numbers that would be used to index them (&lt;code&gt;[0]&lt;/code&gt; for the first element, 0, etc.).&lt;/p&gt;
&lt;p&gt;With extended indexing syntax, we retrieve a range of values. For example, all values are retrieved with a colon.&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;A[:]
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;To retrieve a subset of elements, the start and stop positions need to be defined.&lt;/p&gt;
&lt;p&gt;Given the pattern &lt;code&gt;aList[start:stop]&lt;/code&gt;, retrieve the first two elements from List A.&lt;/p&gt;

49:
&lt;p&gt;&lt;em&gt;This answer describes the process of retrieving the stack trace. Already have the stack trace? Read up on stack traces in &quot;&lt;a href=&quot;https://stackoverflow.com/questions/3988788/what-is-a-stack-trace-and-how-can-i-use-it-to-debug-my-application-errors&quot;&gt;What is a stack trace, and how can I use it to debug my application errors?&lt;/a&gt;&quot;&lt;/em&gt;&lt;/p&gt;

&lt;h1&gt;The Problem&lt;/h1&gt;

&lt;p&gt;Your application quit because an uncaught &lt;code&gt;RuntimeException&lt;/code&gt; was thrown.&lt;br&gt;
The most common of these is the &lt;a href=&quot;https://stackoverflow.com/questions/218384/what-is-a-nullpointerexception-and-how-do-i-fix-it&quot;&gt;&lt;code&gt;NullPointerException&lt;/code&gt;&lt;/a&gt;.&lt;/p&gt;

&lt;h1&gt;How to solve it?&lt;/h1&gt;

&lt;p&gt;Every time an Android application crashes (or any Java application for that matter), a &lt;code&gt;Stack trace&lt;/code&gt; is written to the console (in this case, logcat). This stack trace contains vital information for solving your problem.&lt;/p&gt;

&lt;h2&gt;Android Studio&lt;/h2&gt;

&lt;p&gt;&lt;img src=&quot;https://i.stack.imgur.com/ZL3Y8.png&quot; alt=&quot;Finding the stack trace in Android Studio&quot;&gt;&lt;/p&gt;

&lt;p&gt;In the bottom bar of the window, click on the &lt;code&gt;Logcat&lt;/code&gt; button. Alternatively, you can press &lt;kbd&gt;alt&lt;/kbd&gt;+&lt;kbd&gt;6&lt;/kbd&gt;. Make sure your emulator or device is selected in the &lt;code&gt;Devices&lt;/code&gt; panel. Next, try to find the stack trace, which is shown in red. There may be a lot of stuff logged into logcat, so you may need to scroll a bit. An easy way to find the stack trace is to clear the logcat (using the recycle bin on the right), and let the app crash again.&lt;/p&gt;

&lt;h1&gt;I have found the stack trace, now what?&lt;/h1&gt;

&lt;p&gt;Yay! You&apos;re halfway to solving your problem.&lt;br&gt;
You only need to find out what exactly made your application crash, by analyzing the stack trace.&lt;/p&gt;

&lt;p&gt;Read up on stack traces in &quot;&lt;a href=&quot;https://stackoverflow.com/questions/3988788/what-is-a-stack-trace-and-how-can-i-use-it-to-debug-my-application-errors&quot;&gt;What is a stack trace, and how can I use it to debug my application errors?&lt;/a&gt;&quot;&lt;/p&gt;

&lt;h1&gt;I still can&apos;t solve my problem!&lt;/h1&gt;

&lt;p&gt;If you&apos;ve found your &lt;code&gt;Exception&lt;/code&gt; and the line where it occurred, and still cannot figure out how to fix it, don&apos;t hesitate to ask a question on StackOverflow.  &lt;/p&gt;

&lt;p&gt;Try to be as concise as possible: post the stack trace, and the &lt;em&gt;relevant&lt;/em&gt; code (e.g. a few lines up to the line which threw the &lt;code&gt;Exception&lt;/code&gt;).&lt;/p&gt;

&lt;p&gt;You can use &lt;a href=&quot;https://developer.android.com/studio/command-line/adb.html&quot; rel=&quot;noreferrer&quot;&gt;Google&apos;s ADB tool&lt;/a&gt; to get &lt;code&gt;Logcat file&lt;/code&gt; to analyze the issue.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-undefined&quot;&gt;adb logcat &amp;gt; logcat.txt
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;open &lt;code&gt;logcat.txt&lt;/code&gt; file and search for your application name. There should be information on why it failed, the line number, Class name, etc.&lt;/p&gt;

&lt;p&gt;Just check the error in log cat. &lt;/p&gt;

&lt;p&gt;You get the log cat option from in eclipse:&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;window-&amp;gt;show view-&amp;gt;others-&amp;gt;Android-&amp;gt;Logcat&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;Log cat contains error.&lt;/p&gt;

&lt;p&gt;Other wise you can also check the error by executing an application in debug mode.
Firstly set breakpoint after that by doing:&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;right click on project-&amp;gt;debug as-&amp;gt;Android application&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Note:&lt;/em&gt;&lt;/strong&gt;&lt;em&gt; This answer is using Android Studio 2.2.2&lt;/em&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Note 2:&lt;/em&gt;&lt;/strong&gt; &lt;em&gt;I am considering that your device is successfully connected.&lt;/em&gt; &lt;/p&gt;

&lt;hr&gt;

&lt;p&gt;The first thing you do when your application crashes is look into the LogCat, at the bottom of Android Studio there&apos;s a toolbar with a list of menus:&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/1hRMI.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/1hRMI.png&quot; alt=&quot;image&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Click on the &quot;Android Monitor&quot; (The one I underlined in the image above. ^)&lt;/p&gt;

&lt;p&gt;Now, you&apos;ll get something like this:&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/f71Qd.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/f71Qd.png&quot; alt=&quot;image&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Change &quot;&lt;code&gt;Verbose&lt;/code&gt;&quot; to &quot;&lt;code&gt;Error&lt;/code&gt;&quot; Now it will only show you logged errors. Don&apos;t worry about all these errors (if you got them) now.&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/gpkIa.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/gpkIa.png&quot; alt=&quot;image&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Ok. Now, do what you did to crash your app. After your app crashes, go to your logcat. You should find a new crash log that has a lot of &lt;code&gt;at:x.x.x&lt;/code&gt;: and &lt;code&gt;Caused by: TrumpIsPresidentException&lt;/code&gt; for example. Go to that &lt;code&gt;Caused by:&lt;/code&gt; statement in your logcat.&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/dmNaz.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/dmNaz.png&quot; alt=&quot;image&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Next to&lt;/strong&gt; that &lt;code&gt;Caused By:&lt;/code&gt;, there should be the Exception that happened. In my case, it&apos;s a &lt;code&gt;RuntimeException&lt;/code&gt; and &lt;strong&gt;under it&lt;/strong&gt; there should be a line which contains a &lt;strong&gt;blue link&lt;/strong&gt; such as:&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/VC156.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/VC156.png&quot; alt=&quot;image&quot;&gt;&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;If that &lt;code&gt;Caused by:&lt;/code&gt;&lt;/strong&gt; DOESN&apos;T have a line with a blue text somewhere under it, then look for another &lt;code&gt;Caused by:&lt;/code&gt; that does.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Click on that blue link&lt;/strong&gt;. It should take you to where the problem occured. In my case, it was due to this line:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;RuntimeException&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So, now I know why it&apos;s crashing. It&apos;s because I&apos;m throwing the exception myself. &lt;strong&gt;This was an obvious error&lt;/strong&gt;.&lt;/p&gt;

&lt;hr&gt;

&lt;p&gt;However, let&apos;s say I got another error:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-undefined&quot;&gt;java.lang.NullPointerException
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I checked my logcat, I clicked on the blue link it gave me, and it took me here:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scss&quot;&gt;mTextView&lt;span class=&quot;hljs-selector-class&quot;&gt;.setText&lt;/span&gt;(myString);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So, now I want to debug. According to &lt;a href=&quot;https://stackoverflow.com/questions/218384/what-is-a-nullpointerexception-and-how-do-i-fix-it&quot;&gt;this StackOverflow question&lt;/a&gt;, a NullPointerException says that something is &lt;code&gt;null&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;So, let&apos;s find out &lt;strong&gt;what is null&lt;/strong&gt;. There&apos;s two possibilities. Either &lt;code&gt;mTextView&lt;/code&gt; is null, or &lt;code&gt;myString&lt;/code&gt; is null. To find out, before the &lt;code&gt;mTextView.setText(mString)&lt;/code&gt; line, I add these two lines:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-coffeescript&quot;&gt;Log.d(&lt;span class=&quot;hljs-string&quot;&gt;&quot;AppDebug&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;mTextView is null: &quot;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;String&lt;/span&gt;.valueOf(mTextView == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
Log.d(&lt;span class=&quot;hljs-string&quot;&gt;&quot;AppDebug&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;myString is null: &quot;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;String&lt;/span&gt;.valueOf(myString== &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Now, like we did previously (We changed Verose to Error), we want to change &quot;Error&quot; to &quot;Debug&quot;. Since we&apos;re logging by debugging. Here&apos;s all the Log methods:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-vhdl&quot;&gt;Log.
  d means Debug
  e means &lt;span class=&quot;hljs-literal&quot;&gt;error&lt;/span&gt;
  w means &lt;span class=&quot;hljs-literal&quot;&gt;warning&lt;/span&gt;
  v means verbose
  i means information
  wtf means &lt;span class=&quot;hljs-string&quot;&gt;&quot;What a terrible failure&quot;&lt;/span&gt;. This &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; similar &lt;span class=&quot;hljs-keyword&quot;&gt;to&lt;/span&gt; Log.e
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So, since we used &lt;code&gt;Log.d&lt;/code&gt;, we&apos;re checking in Debug. That&apos;s why we changed it to debug.&lt;/p&gt;

&lt;p&gt;Notice &lt;code&gt;Log.d&lt;/code&gt; has a first parameter,in our case &quot;AppDebug&quot;. Click on the &quot;No Filters&quot; drop down menu on the top-right of the logcat. Select &quot;Edit Filter Configuration&quot;, give a name to your filter, and in &quot;Log Tag&quot; put &quot;App Debug&quot;. Click &quot;OK&quot;. Now, you should see two lines in the logcat:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;yourPackageNameAndApp: mTextView &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
yourPackageNameAndApp: myString &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So now we know that mTextView is null. &lt;/p&gt;

&lt;p&gt;I observe my code, now I notice something.&lt;/p&gt;

&lt;p&gt;I have &lt;code&gt;private TextView mTextView&lt;/code&gt; declared at the top of my class. But, I&apos;m not defining it.&lt;/p&gt;

&lt;p&gt;Basically I forgot to do this in my onCreate():&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-ini&quot;&gt;&lt;span class=&quot;hljs-attr&quot;&gt;mTextView&lt;/span&gt; = (TextView) findViewById(R.id.textview_id_in_xml)&lt;span class=&quot;hljs-comment&quot;&gt;;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;So THAT&apos;S why &lt;code&gt;mTextView&lt;/code&gt; is null, because I forgot to tell my app what it is. So I add that line, run my app, and now the app doesn&apos;t crash.&lt;/p&gt;

&lt;hr&gt;

&lt;p&gt;This popup shows only when you get a fatal exception in your code which stops the execution of the app. It could be any exception &lt;code&gt;NullPointerException&lt;/code&gt;, &lt;code&gt;OutOfMemoryException&lt;/code&gt; etc.&lt;/p&gt;

&lt;p&gt;Best way to check is through &lt;em&gt;Logcat&lt;/em&gt; if you are still developing the app in Android studio which is quick way to read stack trace and check the cause of the app.&lt;/p&gt;

&lt;p&gt;If your app is already live, then you can not use &lt;em&gt;logcat&lt;/em&gt;. So, for that you can implement &lt;code&gt;Crashlytics&lt;/code&gt; to provide you bug reports of any exception that occurs.&lt;/p&gt;

&lt;p&gt;You can use any of these tools:&lt;/p&gt;
&lt;blockquote&gt;
&lt;ol&gt;
&lt;li&gt;&lt;p&gt;adb logcat&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;adb logcat &amp;gt; logs.txt (you can use editors to open and search errors.)&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;eclipse logcat (If not visible in eclipse, Go to Windows-&amp;gt;Show View-&amp;gt;Others-&amp;gt;Android-&amp;gt;LogCat)&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;Android Debug Monitor or Android Device Monitor(type command &lt;strong&gt;monitor&lt;/strong&gt; or open through UI)&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/4RoQc.png&quot; rel=&quot;noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/4RoQc.png&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;ol start=&quot;5&quot;&gt;
&lt;li&gt;Android Studio&lt;/li&gt;
&lt;/ol&gt;
&lt;/blockquote&gt;
&lt;p&gt;I suggest to use &lt;strong&gt;Android Debug Monitor&lt;/strong&gt;, it is good. Because eclipse hangs when too many logs are there, and through adb logcat filter and all difficult.&lt;/p&gt;

&lt;p&gt;You have to check the &lt;code&gt;Stack trace&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;How to do that?&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;on Your IDE Check the windows form LOGCAT &lt;/p&gt;

&lt;p&gt;If you cant see the logcat windows go to this path and open it &lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-rust&quot;&gt;window&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;show view&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;others&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;Android&lt;span class=&quot;hljs-punctuation&quot;&gt;-&amp;gt;&lt;/span&gt;Logcat
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;if you are using Google-Api go to this path &lt;/p&gt;

&lt;p&gt;&lt;em&gt;adb logcat &amp;gt; logcat.txt&lt;/em&gt;&lt;/p&gt;

&lt;p&gt;In below showToast() method you have to pass another parameter for context or application context by doing so you can try it.&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;showToast&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String error, Context applicationContext)&lt;/span&gt;{
        &lt;span class=&quot;hljs-type&quot;&gt;LayoutInflater&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;inflater&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; getLayoutInflater();
        &lt;span class=&quot;hljs-type&quot;&gt;View&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;view&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; inflater.inflate(R.layout.custom_toast, (ViewGroup)      
        findViewById(R.id.toast_root));
        &lt;span class=&quot;hljs-type&quot;&gt;TextView&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;text&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (TextView) findViewById(R.id.toast_error);
        text.setText(error);
        &lt;span class=&quot;hljs-type&quot;&gt;Toast&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;toast&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Toast&lt;/span&gt;(applicationContext);
        toast.setGravity(Gravity.TOP | Gravity.FILL_HORIZONTAL, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;);
        toast.setDuration(Toast.LENGTH_SHORT);
        toast.setView(view);
        toast.show();
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Let me share a basic Logcat analysis for when you meet a Force Close (when the app stops working).&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;DOCS&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;The basic tool from Android to collect/analyze logs is the logcat. &lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;https://developer.android.com/studio/command-line/logcat.html&quot; rel=&quot;nofollow noreferrer&quot;&gt;HERE&lt;/a&gt; is the Android&apos;s page about logcat&lt;/p&gt;

&lt;p&gt;If you use android Studio, you can also check this &lt;a href=&quot;https://developer.android.com/studio/debug/index.html#systemLogView&quot; rel=&quot;nofollow noreferrer&quot;&gt;LINK&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Capturing&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;Basically, you can MANUALLY capture logcat with the following command (or just check AndroidMonitor window in AndroidStudio):&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-undefined&quot;&gt;adb logcat
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;There&apos;s a lot of parameters you can add to the command which helps you to filter and display the message that you want... This is personal...  I always use the command below to get the message timestamp:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-css&quot;&gt;adb logcat -v &lt;span class=&quot;hljs-selector-tag&quot;&gt;time&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can redirect the output to a file and analyze it in a Text Editor.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Analyzing&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;If you app is Crashing, you&apos;ll get something like:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;07&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;09&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;08&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;29&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;13.474&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;/com.example.khan.abc D/AndroidRuntime: Shutting down VM
&lt;span class=&quot;hljs-number&quot;&gt;07&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;09&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;08&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;29&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;13.475&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;/com.example.khan.abc E/AndroidRuntime: FATAL EXCEPTION: main
    Process: com.example.khan.abc, PID: &lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;
    java.lang.NullPointerException: Attempt to invoke virtual method &lt;span class=&quot;hljs-string&quot;&gt;&apos;void android.support.v4.app.FragmentActivity.onBackPressed()&apos;&lt;/span&gt; on a &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;object&lt;/span&gt; reference
     at com.example.khan.abc.AudioFragment$&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;onClick&lt;/span&gt;(AudioFragment.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;125&lt;/span&gt;)
     at android.view.View.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;performClick&lt;/span&gt;(View.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;4848&lt;/span&gt;)
     at android.view.View&lt;span class=&quot;hljs-variable&quot;&gt;$PerformClick&lt;/span&gt;.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;run&lt;/span&gt;(View.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;20262&lt;/span&gt;)
     at android.os.Handler.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;handleCallback&lt;/span&gt;(Handler.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;815&lt;/span&gt;)
     at android.os.Handler.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;dispatchMessage&lt;/span&gt;(Handler.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;104&lt;/span&gt;)
     at android.os.Looper.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;loop&lt;/span&gt;(Looper.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;194&lt;/span&gt;)
     at android.app.ActivityThread.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;main&lt;/span&gt;(ActivityThread.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;5631&lt;/span&gt;)
     at java.lang.reflect.Method.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;invoke&lt;/span&gt;(Native Method)
     at java.lang.reflect.Method.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;invoke&lt;/span&gt;(Method.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;372&lt;/span&gt;)
     at com.android.internal.os.ZygoteInit&lt;span class=&quot;hljs-variable&quot;&gt;$MethodAndArgsCaller&lt;/span&gt;.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;run&lt;/span&gt;(ZygoteInit.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;959&lt;/span&gt;)
     at com.android.internal.os.ZygoteInit.&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;main&lt;/span&gt;(ZygoteInit.&lt;span class=&quot;hljs-attr&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;754&lt;/span&gt;)
&lt;span class=&quot;hljs-number&quot;&gt;07&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;09&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;08&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;29&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;15.195&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt;/com.example.khan.abc I/Process: Sending signal. PID: &lt;span class=&quot;hljs-number&quot;&gt;21144&lt;/span&gt; SIG: &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This part of the log shows you a lot of information:&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;When the issue happened: &lt;code&gt;07-09 08:29:13.475&lt;/code&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;It is important to check when the issue happened... You may find several errors in a log... you must be sure that you are checking the proper messages :)&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Which app crashed: &lt;code&gt;com.example.khan.abc&lt;/code&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;This way, you know which app crashed (to be sure that you are checking the logs about your message)&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Which ERROR: &lt;code&gt;java.lang.NullPointerException&lt;/code&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;A NULL Pointer Exception error&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Detailed info about the error: &lt;code&gt;Attempt to invoke virtual method &apos;void android.support.v4.app.FragmentActivity.onBackPressed()&apos; on a null object reference&lt;/code&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;You tried to call method &lt;code&gt;onBackPressed()&lt;/code&gt; from a &lt;code&gt;FragmentActivity&lt;/code&gt; object. However, that object was &lt;code&gt;null&lt;/code&gt; when you did it.&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;&lt;p&gt;Stack Trace: Stack Trace shows you the method invocation order... Sometimes, the error happens in the calling method (and not in the called method).&lt;/p&gt;

&lt;p&gt;at com.example.khan.abc.AudioFragment$1.onClick(AudioFragment.java:125)&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;Error happened in file &lt;code&gt;com.example.khan.abc.AudioFragment.java&lt;/code&gt;, inside &lt;code&gt;onClick()&lt;/code&gt; method at line: &lt;code&gt;125&lt;/code&gt; (stacktrace shows the line that error happened)&lt;/p&gt;

&lt;p&gt;It was called by:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-vhdl&quot;&gt;at android.&lt;span class=&quot;hljs-keyword&quot;&gt;view&lt;/span&gt;.&lt;span class=&quot;hljs-keyword&quot;&gt;View&lt;/span&gt;.performClick(&lt;span class=&quot;hljs-keyword&quot;&gt;View&lt;/span&gt;.java:&lt;span class=&quot;hljs-number&quot;&gt;4848&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Which was called by:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-vhdl&quot;&gt;at android.&lt;span class=&quot;hljs-keyword&quot;&gt;view&lt;/span&gt;.&lt;span class=&quot;hljs-keyword&quot;&gt;View&lt;/span&gt;$PerformClick.run(&lt;span class=&quot;hljs-keyword&quot;&gt;View&lt;/span&gt;.java:&lt;span class=&quot;hljs-number&quot;&gt;20262&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;which was called by:&lt;/p&gt;

&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-less&quot;&gt;&lt;span class=&quot;hljs-selector-tag&quot;&gt;at&lt;/span&gt; &lt;span class=&quot;hljs-selector-tag&quot;&gt;android&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.os&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.Handler&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.handleCallback&lt;/span&gt;(Handler.&lt;span class=&quot;hljs-attribute&quot;&gt;java&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;815&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;etc....&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Overview&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;This was just an overview... Not all logs are simple but the error gives specific problem and verbose shows up all problem ... It is just to share the idea and provide entry-level information to you... &lt;/p&gt;

&lt;p&gt;I hope I could help you someway...
Regards&lt;/p&gt;

&lt;p&gt;Use the &lt;strong&gt;LogCat&lt;/strong&gt; and try to find what is causing the app to crash.&lt;/p&gt;

&lt;p&gt;To see Logcat if you use &lt;strong&gt;Android Studio&lt;/strong&gt; then Press &lt;strong&gt;ALT + 6&lt;/strong&gt;
or&lt;/p&gt;

&lt;p&gt;if you use &lt;strong&gt;Eclipse&lt;/strong&gt; then 
&lt;strong&gt;Window -&amp;gt; Open Perspective -&amp;gt; Other - LogCat&lt;/strong&gt; &lt;/p&gt;

&lt;p&gt;Go to the LogCat, from the drop down menu select error. This will contain all the required information to help you debug. If that doesn&apos;t help, post the LogCat as an edit to your question and somebody will help you out.&lt;/p&gt;

&lt;p&gt;People make mistakes, and so coding as well. &lt;/p&gt;

&lt;p&gt;When ever any &lt;code&gt;error&lt;/code&gt; happened, always check with the logcat with the text in red color however u can find out the &lt;em&gt;real problem in blue color text with underline&lt;/em&gt; in those red color text. &lt;/p&gt;

&lt;p&gt;Make sure if u create a new &lt;code&gt;activity&lt;/code&gt;, always declare the &lt;code&gt;activity&lt;/code&gt; in the &lt;code&gt;AndroidManifest&lt;/code&gt; file.&lt;/p&gt;

&lt;p&gt;If adding Permission, declare it in the &lt;code&gt;AndroidMainifest&lt;/code&gt; file as well.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Logcat&lt;/strong&gt; - &lt;strong&gt;To check the logs in the development phase of Android Studio&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;Initially clear the Logcat and let the app crash again so you can get only crashed log detail. You have to check the Stack trace&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;While, Unfortunately, MyApp has stopped. There are many reasons for it. You can check same in logs. For this, you can use the Log.e(&quot;TAG&quot;,&quot;Message&quot;);&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;&lt;strong&gt;Common error during app crash like:&lt;/strong&gt;&lt;/p&gt;

&lt;ol&gt;
&lt;li&gt;Coding mistake(Wrong use of keywords).&lt;/li&gt;
&lt;li&gt;Mismatch property name.&lt;/li&gt;
&lt;li&gt;Unsupported plugin(maybe).&lt;/li&gt;
&lt;li&gt;Mismatch version(maybe).&lt;/li&gt;
&lt;li&gt;Activity missing in AndroidManifest file.&lt;/li&gt;
&lt;li&gt;Permission missing in AndroidManifest file.&lt;/li&gt;
&lt;li&gt;Most common NullPointerException.&lt;/li&gt;
&lt;li&gt;Declared but not defined.&lt;/li&gt;
&lt;/ol&gt;

&lt;p&gt;&lt;strong&gt;To resolve app crash error:&lt;/strong&gt;&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Keep in mind above points and go through it.&lt;/li&gt;
&lt;li&gt;With the error, you will get the file name also in blue colour (click on them and jump to code from error is occurring).&lt;/li&gt;
&lt;/ul&gt;

&lt;h3&gt;Crash during development&lt;/h3&gt;

&lt;p&gt;Try my favourite tool &lt;a href=&quot;https://bitbucket.org/mlopatkin/android-log-viewer/downloads/&quot; rel=&quot;nofollow noreferrer&quot;&gt;logview&lt;/a&gt; to get the logs and analyze them during development.&lt;br&gt;
Make sure to mark &lt;code&gt;./logview&lt;/code&gt; and &lt;code&gt;./lib/logview.jar&lt;/code&gt; as executable when running in Linux.&lt;/p&gt;

&lt;p&gt;If you don&apos;t like it, there&apos;re a lot of alternative &lt;a href=&quot;https://stackoverflow.com/q/4325073/1429387&quot;&gt;desktop log viewers for Android&lt;/a&gt;.&lt;/p&gt;

&lt;h3&gt;Crash in the wild&lt;/h3&gt;

&lt;p&gt;Integrate a real-time crash reporting tool such as &lt;a href=&quot;https://firebase.google.com/docs/crashlytics&quot; rel=&quot;nofollow noreferrer&quot;&gt;Firebase Crashlytics&lt;/a&gt; in order to get stacktraces of unhandled exceptions which occurred on users&apos; devices.&lt;/p&gt;

&lt;p&gt;Read &lt;a href=&quot;https://tech.trello.com/how-to-release-a-buggy-app-and-live-to-tell-the-story/&quot; rel=&quot;nofollow noreferrer&quot;&gt;How to Release a Buggy App (And Live to Tell the Tale)&lt;/a&gt; to know more about handling bugs in the field.&lt;/p&gt;

&lt;p&gt;If you don&apos;t have any kind of interesting log in your terminal (or they are not directly related to your app), maybe your problem is due to a native library. In that case, you should check for the &quot;tombstone&quot; files within your terminal. &lt;/p&gt;

&lt;p&gt;The default location for the tombstone files depends on every device, but if that&apos;s the case, you will have a log telling: &lt;code&gt;Tombstone written to: /data/tombstones/tombstone_06&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;For more information, check on &lt;a href=&quot;https://source.android.com/devices/tech/debug&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://source.android.com/devices/tech/debug&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;Also running this command in terminal can help find the problem:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-css&quot;&gt;gradlew build &amp;gt; log&lt;span class=&quot;hljs-selector-class&quot;&gt;.txt&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;&amp;gt;&lt;span class=&quot;hljs-selector-tag&quot;&gt;details&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.txt&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;then you should go to gradlew file location in read two above log files.&lt;/p&gt;

&lt;p&gt;I&apos;ll Suggest something like this.&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;&lt;p&gt;Check if your phone has good enough space that the app can run----prior/&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;Check the logcat when the app crashes. It will show the exact line where the app crashed.&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;Check if you are using something on the main thread that uses a lot of memory due to ANR.&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;

&lt;p&gt;If your app crashed &lt;strong&gt;without any errors&lt;/strong&gt;, and &lt;strong&gt;you didn&apos;t use an asset manager&lt;/strong&gt; but loaded the textures like:&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;Texture&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;texture&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Texture&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;myImage.png&quot;&lt;/span&gt;); &lt;span class=&quot;hljs-comment&quot;&gt;//dont to this all the time&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;then that&apos;s the problem. I had that happen to me. You should always use an asset manager to avoid a memory overload.&lt;/p&gt;

50:
&lt;p&gt;By using the &lt;code&gt;merge&lt;/code&gt; function and its optional parameters:&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Inner join:&lt;/em&gt;&lt;/strong&gt; &lt;code&gt;merge(df1, df2)&lt;/code&gt; will work for these examples because R automatically joins the frames by common variable names, but you would most likely want to specify &lt;code&gt;merge(df1, df2, by = &quot;CustomerId&quot;)&lt;/code&gt; to make sure that you were matching on only the fields you desired.  You can also use the &lt;code&gt;by.x&lt;/code&gt; and &lt;code&gt;by.y&lt;/code&gt; parameters if the matching variables have different names in the different data frames.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Outer join:&lt;/em&gt;&lt;/strong&gt; &lt;code&gt;merge(x = df1, y = df2, by = &quot;CustomerId&quot;, all = TRUE)&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Left outer:&lt;/em&gt;&lt;/strong&gt; &lt;code&gt;merge(x = df1, y = df2, by = &quot;CustomerId&quot;, all.x = TRUE)&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Right outer:&lt;/em&gt;&lt;/strong&gt; &lt;code&gt;merge(x = df1, y = df2, by = &quot;CustomerId&quot;, all.y = TRUE)&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;em&gt;Cross join:&lt;/em&gt;&lt;/strong&gt; &lt;code&gt;merge(x = df1, y = df2, by = NULL)&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;&lt;strike&gt;Just as with the inner join, you would probably want to explicitly pass &quot;CustomerId&quot; to R as the matching variable.&lt;/strike&gt;  I think it&apos;s almost always best to explicitly state the identifiers on which you want to merge; it&apos;s safer if the input data.frames change unexpectedly and easier to read later on.&lt;/p&gt;

&lt;p&gt;You can merge on multiple columns by giving &lt;code&gt;by&lt;/code&gt; a vector, e.g., &lt;code&gt;by = c(&quot;CustomerId&quot;, &quot;OrderId&quot;)&lt;/code&gt;. &lt;/p&gt;

&lt;p&gt;If the column names to merge on are not the same, you can specify, e.g., &lt;code&gt;by.x = &quot;CustomerId_in_df1&quot;, by.y = &quot;CustomerId_in_df2&quot;&lt;/code&gt; where &lt;code&gt;CustomerId_in_df1&lt;/code&gt; is the name of the column in the first data frame and &lt;code&gt;CustomerId_in_df2&lt;/code&gt; is the name of the column in the second data frame. (These can also be vectors if you need to merge on multiple columns.)&lt;/p&gt;

&lt;p&gt;I would recommend checking out &lt;a href=&quot;http://cran.r-project.org/web/packages/sqldf/index.html&quot; rel=&quot;noreferrer&quot;&gt;Gabor Grothendieck&apos;s sqldf package&lt;/a&gt;, which allows you to express these operations in SQL.&lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;sqldf&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;## inner join&lt;/span&gt;
df3 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; sqldf&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;SELECT CustomerId, Product, State 
              FROM df1
              JOIN df2 USING(CustomerID)&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;## left join (substitute &apos;right&apos; for right join)&lt;/span&gt;
df4 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; sqldf&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;SELECT CustomerId, Product, State 
              FROM df1
              LEFT JOIN df2 USING(CustomerID)&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I find the SQL syntax to be simpler and more natural than its R equivalent (but this may just reflect my RDBMS bias).&lt;/p&gt;

&lt;p&gt;See &lt;a href=&quot;https://github.com/ggrothendieck/sqldf#example-4-join&quot; rel=&quot;noreferrer&quot;&gt;Gabor&apos;s sqldf GitHub&lt;/a&gt; for more information on joins. &lt;/p&gt;

&lt;p&gt;There is the &lt;strong&gt;data.table&lt;/strong&gt; approach for an inner join, which is very time and memory efficient (and necessary for some larger data.frames):&lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

dt1 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; key &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; 
dt2 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; key &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

joined.dt1.dt.2 &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;code&gt;merge&lt;/code&gt; also works on data.tables (as it is generic and calls &lt;code&gt;merge.data.table&lt;/code&gt;)&lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;data.table documented on stackoverflow:&lt;br&gt;
&lt;a href=&quot;https://stackoverflow.com/questions/2232699/r-how-to-do-a-data-table-merge-operation&quot;&gt;How to do a data.table merge operation&lt;/a&gt;&lt;br&gt;
&lt;a href=&quot;https://stackoverflow.com/questions/9914734/translating-sql-joins-on-foreign-keys-to-r-data-table-syntax&quot;&gt;Translating SQL joins on foreign keys to R data.table syntax&lt;/a&gt;&lt;br&gt;
&lt;a href=&quot;https://stackoverflow.com/questions/11146967/efficient-alternatives-to-merge-for-larger-data-frames-r&quot;&gt;Efficient alternatives to merge for larger data.frames R&lt;/a&gt;&lt;br&gt;
&lt;a href=&quot;https://stackoverflow.com/questions/7090621/how-to-do-a-basic-left-outer-join-with-data-table-in-r&quot;&gt;How to do a basic left outer join with data.table in R?&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Yet another option is the &lt;code&gt;join&lt;/code&gt; function found in the &lt;a href=&quot;http://cran.r-project.org/web/packages/plyr/index.html&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;plyr&lt;/strong&gt;&lt;/a&gt; package&lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;plyr&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;
     type &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;inner&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#   CustomerId Product   State&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# 1          2 Toaster Alabama&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# 2          4   Radio Alabama&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# 3          6   Radio    Ohio&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Options for &lt;code&gt;type&lt;/code&gt;: &lt;code&gt;inner&lt;/code&gt;, &lt;code&gt;left&lt;/code&gt;, &lt;code&gt;right&lt;/code&gt;, &lt;code&gt;full&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;From &lt;code&gt;?join&lt;/code&gt;: Unlike &lt;code&gt;merge&lt;/code&gt;, [&lt;code&gt;join&lt;/code&gt;] preserves the order of x no matter what join type is used.&lt;/p&gt;

&lt;p&gt;You can do joins as well using Hadley Wickham&apos;s awesome &lt;a href=&quot;http://blog.rstudio.org/2014/01/17/introducing-dplyr/&quot;&gt;dplyr&lt;/a&gt; package.  &lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dplyr&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#make sure that CustomerId cols are both type numeric&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;#they ARE not using the provided code in question and dplyr will complain&lt;/span&gt;
df1&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;CustomerId &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;as.numeric&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;CustomerId&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
df2&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;CustomerId &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;as.numeric&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df2&lt;span class=&quot;hljs-operator&quot;&gt;$&lt;/span&gt;CustomerId&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;Mutating joins: add columns to df1 using matches in df2&lt;/h2&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;#inner&lt;/span&gt;
inner_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#left outer&lt;/span&gt;
left_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#right outer&lt;/span&gt;
right_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#alternate right outer&lt;/span&gt;
left_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df1&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;#full join&lt;/span&gt;
full_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;Filtering joins: filter out rows in df1, don&apos;t modify columns&lt;/h2&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;semi_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-comment&quot;&gt;#keep only observations in df1 that match in df2.&lt;/span&gt;
anti_join&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-comment&quot;&gt;#drops all observations in df1 that match in df2.&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;There are some good examples of doing this over at the &lt;a href=&quot;http://wiki.r-project.org/rwiki/doku.php?id=tips:data-frames:merge&quot; rel=&quot;noreferrer&quot;&gt;R Wiki&lt;/a&gt;. I&apos;ll steal a couple here:&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Merge Method&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;Since your keys are named the same the short way to do an inner join is merge():&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;a full inner join (all records from both tables) can be created with the &quot;all&quot; keyword:&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;all&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;a left outer join of df1 and df2:&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; all.x&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;a right outer join of df1 and df2:&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; df2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; all.y&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;you can flip &apos;em, slap &apos;em and rub &apos;em down to get the other two outer joins you asked about :)&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Subscript Method&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;A left outer join with df1 on the left using a subscript method would be:&lt;/p&gt;
&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;State&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt;df2&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt; &lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Product&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;State&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;The other combination of outer joins can be created by mungling the left outer join subscript example. (yeah, I know that&apos;s the equivalent of saying &quot;I&apos;ll leave it as an exercise for the reader...&quot;)&lt;/p&gt;

&lt;p&gt;New in 2014: &lt;/p&gt;

&lt;p&gt;Especially if you&apos;re also interested in data manipulation in general (including sorting, filtering, subsetting, summarizing etc.), you should definitely take a look at &lt;code&gt;dplyr&lt;/code&gt;, which comes with a variety of functions all designed to facilitate your work specifically with data frames and certain other database types. It even offers quite an elaborate SQL interface, and even a function to convert (most) SQL code directly into R.&lt;/p&gt;

&lt;p&gt;The four joining-related functions in the dplyr package are (to quote):&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;&lt;code&gt;inner_join(x, y, by = NULL, copy = FALSE, ...)&lt;/code&gt;: return all rows from
x where there are matching values in y, and all columns from x and y &lt;/li&gt;
&lt;li&gt;&lt;code&gt;left_join(x, y, by = NULL, copy = FALSE, ...)&lt;/code&gt;: return all rows from x, and all columns from x and y &lt;/li&gt;
&lt;li&gt;&lt;code&gt;semi_join(x, y, by = NULL, copy = FALSE, ...)&lt;/code&gt;: return all rows from x where there are matching values in
y, keeping just columns from x.  &lt;/li&gt;
&lt;li&gt;&lt;code&gt;anti_join(x, y, by = NULL, copy = FALSE, ...)&lt;/code&gt;: return all rows from x
where there are not matching values in y, keeping just columns from x&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;It&apos;s all &lt;a href=&quot;http://cran.r-project.org/web/packages/dplyr/vignettes/introduction.html&quot; rel=&quot;noreferrer&quot;&gt;here&lt;/a&gt; in great detail.&lt;/p&gt;

&lt;p&gt;Selecting columns can be done by &lt;code&gt;select(df,&quot;column&quot;)&lt;/code&gt;. If that&apos;s not SQL-ish enough for you, then there&apos;s the &lt;code&gt;sql()&lt;/code&gt; function, into which you can enter SQL code as-is, and it will do the operation you specified just like you were writing in R all along (for more information, please refer to the &lt;a href=&quot;http://cran.r-project.org/web/packages/dplyr/vignettes/databases.html&quot; rel=&quot;noreferrer&quot;&gt;dplyr/databases vignette&lt;/a&gt;). For example, if applied correctly, &lt;code&gt;sql(&quot;SELECT * FROM hflights&quot;)&lt;/code&gt; will select all the columns from the &quot;hflights&quot; dplyr table (a &quot;tbl&quot;).&lt;/p&gt;

&lt;p&gt;Update on data.table methods for joining datasets. See below examples for each type of join. There are two methods, one from &lt;code&gt;[.data.table&lt;/code&gt; when passing second data.table as the first argument to subset, another way is to use &lt;code&gt;merge&lt;/code&gt; function which dispatches to fast data.table method.  &lt;/p&gt;

&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-r s-code-block&quot;&gt;&lt;code class=&quot;hljs language-r&quot;&gt;df1 &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; data.frame&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;CustomerId &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;:&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; Product &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;rep&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Toaster&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;rep&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Radio&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
df2 &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; data.frame&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;CustomerId &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;2L&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;4L&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;7L&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; State &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;c&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;rep&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Alabama&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;rep&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;Ohio&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt; &lt;span class=&quot;hljs-comment&quot;&gt;# one value changed to show full outer join&lt;/span&gt;

library&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

dt1 &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; as.data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df1&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
dt2 &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; as.data.table&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df2&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
setkey&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; CustomerId&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
setkey&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; CustomerId&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# right outer join keyed data.tables&lt;/span&gt;
dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;

setkey&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;NULL&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
setkey&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;NULL&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;# right outer join unkeyed data.tables - use `on` argument&lt;/span&gt;
dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; on &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# left outer join - swap dt1 with dt2&lt;/span&gt;
dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; on &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# inner join - use `nomatch` argument&lt;/span&gt;
dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; nomatch&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-literal&quot;&gt;NULL&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; on &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# anti join - use `!` operator&lt;/span&gt;
dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;!&lt;/span&gt;dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; on &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;]&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# inner join - using merge method&lt;/span&gt;
merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; by &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# full outer join&lt;/span&gt;
merge&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;dt1&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; dt2&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; by &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;CustomerId&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;all&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;# see ?merge.data.table arguments for other cases&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Below benchmark tests base R, sqldf, dplyr and data.table.&lt;br&gt;
