    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;scanDouble&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; IOException {
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;c&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; scan();
        &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; (isWhiteSpace(c)) c = scan();
        &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;sgn&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;-&apos;&lt;/span&gt;) {
            sgn = -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
            c = scan();
        }
        &lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;res&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; (!isWhiteSpace(c) &amp;amp;&amp;amp; c != &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;e&apos;&lt;/span&gt; || c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;E&apos;&lt;/span&gt;) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; res * Math.pow(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;, scanInt());
            }
            res *= &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;;
            res += c - &lt;span class=&quot;hljs-string&quot;&gt;&apos;0&apos;&lt;/span&gt;;
            c = scan();
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;) {
            c = scan();
            &lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;m&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; (!isWhiteSpace(c)) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;e&apos;&lt;/span&gt; || c == &lt;span class=&quot;hljs-string&quot;&gt;&apos;E&apos;&lt;/span&gt;) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; res * Math.pow(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;, scanInt());
                }
                m /= &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;;
                res += (c - &lt;span class=&quot;hljs-string&quot;&gt;&apos;0&apos;&lt;/span&gt;) * m;
                c = scan();
            }
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; res * sgn;
    }

}
&lt;/code&gt;&lt;/pre&gt;

