&lt;span class=&quot;hljs-keyword&quot;&gt;use&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;PHPMailer&lt;/span&gt;\&lt;span class=&quot;hljs-title&quot;&gt;PHPMailer&lt;/span&gt;\&lt;span class=&quot;hljs-title&quot;&gt;Exception&lt;/span&gt;;

&lt;span class=&quot;hljs-keyword&quot;&gt;require&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;vendor/phpmailer/phpmailer/src/Exception.php&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;require&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;vendor/phpmailer/phpmailer/src/PHPMailer.php&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;require&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;vendor/phpmailer/phpmailer/src/SMTP.php&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-variable&quot;&gt;$editor&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$_POST&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&quot;editor&quot;&lt;/span&gt;];
&lt;span class=&quot;hljs-variable&quot;&gt;$subject&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$_POST&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&quot;subject&quot;&lt;/span&gt;];
&lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$_POST&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&quot;to&quot;&lt;/span&gt;];

&lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$_SERVER&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&quot;REQUEST_METHOD&quot;&lt;/span&gt;] == &lt;span class=&quot;hljs-string&quot;&gt;&quot;POST&quot;&lt;/span&gt;) {

