        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;SMTPSecure = &lt;span class=&quot;hljs-string&quot;&gt;&quot;tls&quot;&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Port       = &lt;span class=&quot;hljs-number&quot;&gt;587&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Host       = &lt;span class=&quot;hljs-string&quot;&gt;&quot;smtp.gmail.com&quot;&lt;/span&gt;;&lt;span class=&quot;hljs-comment&quot;&gt;//using smtp server&lt;/span&gt;
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Username   = &lt;span class=&quot;hljs-string&quot;&gt;&quot;XXXXXXXXXX@gmail.com&quot;&lt;/span&gt;;&lt;span class=&quot;hljs-comment&quot;&gt;//the email which will send the email &lt;/span&gt;
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Password   = &lt;span class=&quot;hljs-string&quot;&gt;&quot;XXXXXXXXXX&quot;&lt;/span&gt;;&lt;span class=&quot;hljs-comment&quot;&gt;//the password&lt;/span&gt;

        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;IsHTML&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;AddAddress&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;recipient-name&quot;&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;SetFrom&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;XXXXXXXXXX@gmail.com&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;from-name&quot;&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;AddReplyTo&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;XXXXXXXXXX@gmail.com&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;reply-to-name&quot;&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Subject = &lt;span class=&quot;hljs-variable&quot;&gt;$subject&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;MsgHTML&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$editor&lt;/span&gt;);




        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!&lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;Send&lt;/span&gt;()) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Error while sending Email.&quot;&lt;/span&gt;;
            &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;var_dump&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;Email sent successfully&quot;&lt;/span&gt;;
        }
    }
} &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (&lt;span class=&quot;hljs-built_in&quot;&gt;Exception&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$e&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$e&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;getMessage&lt;/span&gt;();
}
