    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;PHPMailer&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;IsSMTP&lt;/span&gt;();
    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;SMTPDebug = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;                   &lt;span class=&quot;hljs-comment&quot;&gt;// Set mailer to use SMTP&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Host = &lt;span class=&quot;hljs-string&quot;&gt;&apos;smtp.gmail.com&apos;&lt;/span&gt;;         &lt;span class=&quot;hljs-comment&quot;&gt;// Specify main and backup server&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;Port = &lt;span class=&quot;hljs-number&quot;&gt;587&lt;/span&gt;;                      &lt;span class=&quot;hljs-comment&quot;&gt;// Set the SMTP port&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$mail&lt;/span&gt;-&amp;gt;SMTPAuth = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;                 &lt;span class=&quot;hljs-comment&quot;&gt;// Enable SMTP authentication&lt;/span&gt;
