  &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;: Timestamp(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2011-01-25 00:00:00&apos;&lt;/span&gt;)},
 &lt;span class=&quot;hljs-string&quot;&gt;&apos;price&apos;&lt;/span&gt;: {&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;10.284260107718254&lt;/span&gt;,
