&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;SELECT * FROM sites WHERE kind IN (&quot;&lt;/span&gt;;

&lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$array&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$field&lt;/span&gt;){
    &lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;.=&lt;span class=&quot;hljs-string&quot;&gt;&quot;?,&quot;&lt;/span&gt;;
}
&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;substr&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strlen&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;)-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;.=&lt;span class=&quot;hljs-string&quot;&gt;&quot;)&quot;&lt;/span&gt;;
&lt;span class=&quot;hljs-variable&quot;&gt;$tbl&lt;/span&gt;=&lt;span class=&quot;hljs-variable&quot;&gt;$db&lt;/span&gt;-&amp;gt;connection-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;prepare&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$query&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;=&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;&amp;lt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;count&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$array&lt;/span&gt;);&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;++)
    &lt;span class=&quot;hljs-variable&quot;&gt;$tbl&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;bindParam&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$array&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;-&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;],PDO::&lt;span class=&quot;hljs-variable constant_&quot;&gt;PARAM_STR&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$tbl&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;execute&lt;/span&gt;();
&lt;span class=&quot;hljs-variable&quot;&gt;$row&lt;/span&gt;=&lt;span class=&quot;hljs-variable&quot;&gt;$tbl&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;fetchAll&lt;/span&gt;(PDO::&lt;span class=&quot;hljs-variable constant_&quot;&gt;FETCH_OBJ&lt;/span&gt;);
&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;var_dump&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$row&lt;/span&gt;);
