    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(src);
         }, &lt;span class=&quot;hljs-number&quot;&gt;3000&lt;/span&gt;);


    &lt;span class=&quot;hljs-built_in&quot;&gt;setTimeout&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){


        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; src = $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;.iView2&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;contents&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;.shrinkToFit&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;src&apos;&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(src);
         }, &lt;span class=&quot;hljs-number&quot;&gt;3000&lt;/span&gt;);

         &lt;span class=&quot;hljs-built_in&quot;&gt;setTimeout&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){


        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; src = $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;.iView3&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;contents&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;img&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;src&apos;&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(src);
         }, &lt;span class=&quot;hljs-number&quot;&gt;3000&lt;/span&gt;);


    })


&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;
&amp;lt;/body&amp;gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;For even more robustness:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getIframeWindow&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;iframe_object&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; doc;

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;contentWindow&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;contentWindow&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;window&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;window&lt;/span&gt;;
  } 

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!doc &amp;amp;&amp;amp; iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;contentDocument&lt;/span&gt;) {
    doc = iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;contentDocument&lt;/span&gt;;
  } 

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!doc &amp;amp;&amp;amp; iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;document&lt;/span&gt;) {
    doc = iframe_object.&lt;span class=&quot;hljs-property&quot;&gt;document&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (doc &amp;amp;&amp;amp; doc.&lt;span class=&quot;hljs-property&quot;&gt;defaultView&lt;/span&gt;) {
   &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; doc.&lt;span class=&quot;hljs-property&quot;&gt;defaultView&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (doc &amp;amp;&amp;amp; doc.&lt;span class=&quot;hljs-property&quot;&gt;parentWindow&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; doc.&lt;span class=&quot;hljs-property&quot;&gt;parentWindow&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;and &lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;...
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; frame_win = &lt;span class=&quot;hljs-title function_&quot;&gt;getIframeWindow&lt;/span&gt;( frames[&lt;span class=&quot;hljs-string&quot;&gt;&apos;nameOfMyIframe&apos;&lt;/span&gt;] );

&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (frame_win) {
  $(frame_win.&lt;span class=&quot;hljs-property&quot;&gt;contentDocument&lt;/span&gt; || frame_win.&lt;span class=&quot;hljs-property&quot;&gt;document&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;some selector&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;doStuff&lt;/span&gt;();
  ...
}
...
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I ended up here looking for getting the content of an iframe without jquery, so for anyone else looking for that, it is just this:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;querySelector&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;iframe[name=iframename]&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;contentDocument&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This solution works same as iFrame. I have created a PHP script that can get all the contents from the other website, and most important part is you can easily apply your custom jQuery to that external content. Please refer to the following script that can get all the contents from the other website and then you can apply your cusom jQuery/JS as well. This content can be used anywhere, inside any element or any page.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;div id=&lt;span class=&quot;hljs-string&quot;&gt;&apos;myframe&apos;&lt;/span&gt;&amp;gt;

  &amp;lt;?php 
   &lt;span class=&quot;hljs-comment&quot;&gt;/* 
    Use below function to display final HTML inside this div
   */&lt;/span&gt;

   &lt;span class=&quot;hljs-comment&quot;&gt;//Display Frame&lt;/span&gt;
   echo &lt;span class=&quot;hljs-title function_&quot;&gt;displayFrame&lt;/span&gt;(); 
  ?&amp;gt;

&amp;lt;/div&amp;gt;

&amp;lt;?php

&lt;span class=&quot;hljs-comment&quot;&gt;/* 
  Function to display frame from another domain 
*/&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;displayFrame&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;)
{
  $webUrl = &lt;span class=&quot;hljs-string&quot;&gt;&apos;http://[external-web-domain.com]/&apos;&lt;/span&gt;;

  &lt;span class=&quot;hljs-comment&quot;&gt;//Get HTML from the URL&lt;/span&gt;
  $content = &lt;span class=&quot;hljs-title function_&quot;&gt;file_get_contents&lt;/span&gt;($webUrl);

  &lt;span class=&quot;hljs-comment&quot;&gt;//Add custom JS to returned HTML content&lt;/span&gt;
  $customJS = &lt;span class=&quot;hljs-string&quot;&gt;&quot;
  &amp;lt;script&amp;gt;

      /* Here I am writing a sample jQuery to hide the navigation menu
         You can write your own jQuery for this content
      */
    //Hide Navigation bar
    jQuery(\&quot;.navbar.navbar-default\&quot;).hide();

  &amp;lt;/script&amp;gt;&quot;&lt;/span&gt;;

  &lt;span class=&quot;hljs-comment&quot;&gt;//Append Custom JS with HTML&lt;/span&gt;
  $html = $content . $customJS;

  &lt;span class=&quot;hljs-comment&quot;&gt;//Return customized HTML&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; $html;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If the code below doesn&apos;t work&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#iFrame&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;contents&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#someDiv&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;removeClass&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;hidden&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Here is the reliable way to make it work:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$(&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){ 
  &lt;span class=&quot;hljs-built_in&quot;&gt;setTimeout&lt;/span&gt;(
    &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
      $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#iFrame&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;contents&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#someDiv&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;removeClass&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;hidden&quot;&lt;/span&gt;);
    },
    &lt;span class=&quot;hljs-number&quot;&gt;300&lt;/span&gt;
  );
});
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;This way the script will run after 300 miliseconds, so it&apos;ll get enough time for &lt;code&gt;iFrame&lt;/code&gt; to be loaded and then the code will come into action. At times the &lt;code&gt;iFrame&lt;/code&gt; doesn&apos;t load and script tries to execute before it. &lt;code&gt;300ms&lt;/code&gt; can be tweaked to anything else as per your needs.&lt;/p&gt;

