         &lt;span class=&quot;hljs-number&quot;&gt;1.552&lt;/span&gt;s [&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;1.552&lt;/span&gt;]  f1  [/test/t2.py:&lt;span class=&quot;hljs-number&quot;&gt;21&lt;/span&gt;]  [/test/t2.py:&lt;span class=&quot;hljs-number&quot;&gt;14&lt;/span&gt;]
