&lt;p&gt;ES5:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;arrayUnique&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr, uniqueKey&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; flagList = []
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; arr.&lt;span class=&quot;hljs-title function_&quot;&gt;filter&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;item&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (flagList.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(item[uniqueKey]) === -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) {
      flagList.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(item[uniqueKey])
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;
    }
  })
}
