  &lt;span class=&quot;hljs-selector-class&quot;&gt;.row&lt;/span&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.row-sm-eq&lt;/span&gt;&lt;span class=&quot;hljs-selector-pseudo&quot;&gt;::after&lt;/span&gt; {
