&lt;p&gt;&lt;strong&gt;3 main things&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-delphi&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; a.    p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; ptr &lt;span class=&quot;hljs-keyword&quot;&gt;to&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; int

&lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; b.    p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ptr &lt;span class=&quot;hljs-keyword&quot;&gt;to&lt;/span&gt; int 

&lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; c.    p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ptr &lt;span class=&quot;hljs-keyword&quot;&gt;to&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; int
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;[Error]&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; * comes before &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; 
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;two types&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;1.&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; *

&lt;span class=&quot;hljs-number&quot;&gt;2.&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; *
