&lt;p&gt;&lt;strong&gt;we look first&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Major type 1. const int&lt;/strong&gt;*&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;ways to arrange 3 things at 3 places 3!=6&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;i. * at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;*&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; p      [Error]
*&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p      [Error]
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;ii. const at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; *p      type a. p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; ptr to &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; 
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; p      [Error]
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;iii. int at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-go&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p      &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; a. 
&lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; b. p is &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ptr to &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;Major type 2. const const int&lt;/strong&gt;*&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;ways to arrange 4 things at 4 places in which 2 are alike 4!/2!=12&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;i. * at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;* &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     [Error]
* &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     [Error]
* &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; p     [Error]

&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;ii. int at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p      type a. p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; ptr to &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     type c. p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ptr to &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     type b. p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ptr to &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;iii. const at start&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-go&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; *p     &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; a.
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; * &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; p    [Error]

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p      &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; a.
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p     &lt;span class=&quot;hljs-keyword&quot;&gt;type&lt;/span&gt; c.

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; * &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p    [Error]
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; p    [Error]
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;squeezing all in one&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;type a. p is ptr to const int  (5)&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; *p
&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p

&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;  *p
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; *p
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;type b.  p is const ptr to int (2)&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p
&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;type c. p is const ptr to const int (2)&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; * &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; p
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;just little calculation&lt;/strong&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-scss&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;. const int * &lt;span class=&quot;hljs-selector-tag&quot;&gt;p&lt;/span&gt;        total arrangemets (&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;)   &lt;span class=&quot;hljs-selector-attr&quot;&gt;[Errors]&lt;/span&gt; (&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;)
&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;. const const int * &lt;span class=&quot;hljs-selector-tag&quot;&gt;p&lt;/span&gt;  total arrangemets (&lt;span class=&quot;hljs-number&quot;&gt;12&lt;/span&gt;)  &lt;span class=&quot;hljs-selector-attr&quot;&gt;[Errors]&lt;/span&gt; (&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;little Extra&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;int const * p,p2 ;&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-dart&quot;&gt;here p &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; ptr to &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt;  (type a.) 
but p2 &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; just &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;int&lt;/span&gt; please note that it &lt;span class=&quot;hljs-keyword&quot;&gt;is&lt;/span&gt; not ptr
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;em&gt;&lt;strong&gt;int * const p,p2 ;&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;
&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;similarly 
