.&lt;span class=&quot;hljs-property&quot;&gt;always&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;jqXHROrData, textStatus, jqXHROrErrorThrown&lt;/span&gt;) { 

});


            });
        });
