}

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; cars = [{ &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;r8&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;rs5&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2013&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;mustang&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;fusion&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2015&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;kia&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;optima&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt; }];

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; result = &lt;span class=&quot;hljs-title function_&quot;&gt;groupBy&lt;/span&gt;(cars, &lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;c&lt;/span&gt;) =&amp;gt;&lt;/span&gt; c.&lt;span class=&quot;hljs-property&quot;&gt;make&lt;/span&gt;);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(result);&lt;/code&gt;&lt;/pre&gt;
