&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; cars = [{
  &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;r8&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;rs5&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2013&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;mustang&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;fusion&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2015&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-attr&quot;&gt;make&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;kia&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;model&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;optima&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;year&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}].&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;r, car&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {

  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; {
    model,
    year,
    make
  } = car;

  r[make] = [...r[make] || [], {
    model,
    year
  }];

  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; r;
}, {});

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(cars);&lt;/code&gt;&lt;/pre&gt;
