    }, &lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;create&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Agree that unless you use these  often there is no need for an external library. Although similar solutions are available, I see that some of them are tricky to follow &lt;a href=&quot;https://gist.github.com/wattry/de36aa0fdecbab93adbaf4ff7818ef94&quot; rel=&quot;noreferrer&quot;&gt;here is a gist&lt;/a&gt; that has a solution with comments if you&apos;re trying to understand what is happening.&lt;/p&gt;
&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; cars = [{
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;r8&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;rs5&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2013&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;mustang&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;fusion&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2015&apos;&lt;/span&gt;
}, {
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;kia&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;optima&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;
}, ];

&lt;span class=&quot;hljs-comment&quot;&gt;/**
 * Groups an array of objects by a key an returns an object or array grouped by provided key.
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;array&lt;/span&gt; - array to group objects by key.
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;key&lt;/span&gt; - key to group array objects by.
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;removeKey&lt;/span&gt;  - remove the key and it&apos;s value from the resulting object.
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;outputType&lt;/span&gt; - type of structure the output should be contained in.
 */&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;groupBy&lt;/span&gt; = (&lt;span class=&quot;hljs-params&quot;&gt;
  inputArray,
  key,
  removeKey = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
  outputType = {},
&lt;/span&gt;) =&amp;gt; {
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; inputArray.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(
    &lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;previous, current&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
      &lt;span class=&quot;hljs-comment&quot;&gt;// Get the current value that matches the input key and remove the key value for it.&lt;/span&gt;
      &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; {
        [key]: keyValue
      } = current;
      &lt;span class=&quot;hljs-comment&quot;&gt;// remove the key if option is set&lt;/span&gt;
      removeKey &amp;amp;&amp;amp; keyValue &amp;amp;&amp;amp; &lt;span class=&quot;hljs-keyword&quot;&gt;delete&lt;/span&gt; current[key];
      &lt;span class=&quot;hljs-comment&quot;&gt;// If there is already an array for the user provided key use it else default to an empty array.&lt;/span&gt;
      &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; {
        [keyValue]: reducedValue = []
      } = previous;

      &lt;span class=&quot;hljs-comment&quot;&gt;// Create a new object and return that merges the previous with the current object&lt;/span&gt;
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;assign&lt;/span&gt;(previous, {
        [keyValue]: reducedValue.&lt;span class=&quot;hljs-title function_&quot;&gt;concat&lt;/span&gt;(current)
      });
    },
    &lt;span class=&quot;hljs-comment&quot;&gt;// Replace the object here to an array to change output object to an array&lt;/span&gt;
    outputType,
  );
};

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;groupBy&lt;/span&gt;(cars, &lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;))&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif10&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
&lt;/div&gt;
&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;groupBy&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;data, property&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; data.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;acc, obj&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; key = obj[property];
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!acc[key]) {
      acc[key] = [];
    }
    acc[key].&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(obj);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; acc;
  }, {});
}
&lt;span class=&quot;hljs-title function_&quot;&gt;groupBy&lt;/span&gt;(people, &lt;span class=&quot;hljs-string&quot;&gt;&apos;age&apos;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Just try this one it works fine for me.&lt;/p&gt;
