&lt;p&gt;Another one solution:&lt;/p&gt;
&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; cars = [
    {&lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;r8&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;}, {&lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;audi&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;rs5&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2013&apos;&lt;/span&gt;}, 
    {&lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;mustang&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;}, {&lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ford&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;fusion&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2015&apos;&lt;/span&gt;}, 
    {&lt;span class=&quot;hljs-string&quot;&gt;&apos;make&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;kia&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;model&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;optima&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;2012&apos;&lt;/span&gt;},
];


&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; reducedCars = cars.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;acc, { make, model, year }&lt;/span&gt;) =&amp;gt;&lt;/span&gt; (
    { 
      ...acc, 
      [make]: acc[make] ? [ ...acc[make], { model, year }] : [ { model, year } ],
    }
 ), {});

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(reducedCars);&lt;/code&gt;&lt;/pre&gt;
&lt;div class=&quot;snippet-result&quot;&gt;&lt;div class=&quot;snippet-ctas&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn s-btn__primary&quot;&gt;&lt;span class=&quot;icon-play-white _hover&quot;&gt;&lt;/span&gt;&lt;span&gt; Run code snippet&lt;/span&gt;&lt;/button&gt;&lt;input class=&quot;copySnippet s-btn s-btn__filled&quot; type=&quot;button&quot; value=&quot;Copy snippet to answer&quot; style=&quot;display: none;&quot;&gt;&lt;button type=&quot;button&quot; class=&quot;s-btn hideResults&quot; style=&quot;display: none;&quot;&gt;Hide results&lt;/button&gt;&lt;div class=&quot;popout-code&quot;&gt;&lt;a class=&quot;snippet-expand-link&quot;&gt;Expand snippet&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;snippet-result-code&quot; style=&quot;display: none;&quot;&gt;&lt;iframe name=&quot;sif11&quot; sandbox=&quot;allow-forms allow-modals allow-scripts&quot; class=&quot;snippet-box-edit snippet-box-result&quot; frameborder=&quot;0&quot;&gt;&lt;/iframe&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;
